import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function SCADPage() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div style={{ display: 'flex' }}>
      {/* Sidebar */}
      <div
        style={{
          width: menuOpen ? '250px' : '0',
          height: '100vh',
          backgroundColor: '#f8f9fa',
          overflowX: 'hidden',
          transition: '0.3s',
          padding: menuOpen ? '20px' : '0',
          boxShadow: menuOpen ? '2px 0 5px rgba(0,0,0,0.2)' : 'none',
          position: 'fixed',
          top: 0,
          left: 0,
          zIndex: 1000,
        }}
      >
        <button
          onClick={toggleMenu}
          style={{
            marginBottom: '20px',
            backgroundColor: 'transparent',
            border: 'none',
            fontSize: '20px',
            cursor: 'pointer',
          }}
        >
          ✖
        </button>
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          <li><Link to="/scad-dashboard">Dashboard</Link></li>
          <li><Link to="/view-students">View Students</Link></li>
          <li><Link to="/approve-internships">Approve Internships</Link></li>
          <li><Link to="/messages">Messages</Link></li>
          <li><Link to="/reports">Reports</Link></li>
          <li><Link to="/settings">Settings</Link></li>
          <li><Link to="/">Logout</Link></li>
        </ul>
      </div>

      {/* Main content */}
      <div style={{ marginLeft: menuOpen ? '250px' : '0', transition: 'margin-left 0.3s', padding: '20px', width: '100%' }}>
        <div>
          <button
            onClick={toggleMenu}
            style={{
              fontSize: '24px',
              background: 'transparent',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            ☰
          </button>
        </div>

        <h1>Welcome to the SCAD Page</h1>
        <p>This is your dashboard.</p>
      </div>
    </div>
  );
}

export default SCADPage;
