import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function CompanyPage() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [companyName, setCompanyName] = useState('');
  const [isJobModalOpen, setIsJobModalOpen] = useState(false);
  const [editingIndex, setEditingIndex] = useState(null);
  const [jobData, setJobData] = useState({
    title: '',
    duration: '',
    isPaid: false,
    salary: '',
    skills: '',
    description: '',
  });
  const [postedJobs, setPostedJobs] = useState([]);

  // notification states
  const [hasNotification, setHasNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('You have no notifications!');
  const [showPopup, setShowPopup] = useState(false);

  const navigate = useNavigate();

  // load company + jobs + notifications
  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('currentCompany'));
    if (stored) {
      setCompanyName(stored.companyName || stored.companyEmail);
      // load jobs
      const email = stored.companyEmail;
      const companyJobs = JSON.parse(localStorage.getItem('jobsByCompany')) || {};
      setPostedJobs(companyJobs[email] || []);
      // load notification flag
      if (stored.hasNotification) {
        setHasNotification(true);
        setNotificationMessage(stored.notificationMessage || 'You have a new notification!');
      }
    } else {
      navigate('/company-login');
    }
  }, [navigate]);

  // clear notification when popup opens
  const togglePopup = () => {
    setShowPopup(prev => !prev);
    if (hasNotification) {
      setHasNotification(false);
      // clear flag in localStorage
      const current = JSON.parse(localStorage.getItem('currentCompany'));
      const updated = { ...current, hasNotification: false };
      localStorage.setItem('currentCompany', JSON.stringify(updated));
    }
  };

  const toggleMenu = () => setMenuOpen(prev => !prev);
  const handleLogout = () => {
    localStorage.removeItem('currentCompany');
    navigate('/company-login');
  };

  // job modal & form handlers...
  const handleJobModalToggle = () => {
    setIsJobModalOpen(prev => !prev);
    setEditingIndex(null);
    setJobData({
      title: '',
      duration: '',
      isPaid: false,
      salary: '',
      skills: '',
      description: '',
    });
  };
  const handleJobInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setJobData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };
  const saveJobsToStorage = (jobs) => {
    const currentCompany = JSON.parse(localStorage.getItem('currentCompany')) || {};
    const email = currentCompany.companyEmail;
    const jobsByCompany = JSON.parse(localStorage.getItem('jobsByCompany')) || {};
    jobsByCompany[email] = jobs;
    localStorage.setItem('jobsByCompany', JSON.stringify(jobsByCompany));
  };
  const handleJobSubmit = (e) => {
    e.preventDefault();
    let updated = editingIndex !== null
      ? postedJobs.map((j,i) => i === editingIndex ? jobData : j)
      : [...postedJobs, jobData];
    setPostedJobs(updated);
    saveJobsToStorage(updated);
    setIsJobModalOpen(false);
    setEditingIndex(null);
    setJobData({ title:'', duration:'', isPaid:false, salary:'', skills:'', description:'' });
  };
  const handleEditJob = i => {
    setEditingIndex(i);
    setJobData(postedJobs[i]);
    setIsJobModalOpen(true);
  };
  const handleDeleteJob = i => {
    const updated = postedJobs.filter((_, idx) => idx !== i);
    setPostedJobs(updated);
    saveJobsToStorage(updated);
  };

  return (
    <div style={{ display: 'flex' }}>
      {/* Sidebar */}
      <div
        style={{
          width: menuOpen ? '250px' : '0',
          height: '100vh',
          backgroundColor: '#f8f9fa',
          overflowX: 'hidden',
          transition: '0.3s',
          padding: menuOpen ? '20px' : '0',
          boxShadow: menuOpen ? '2px 0 5px rgba(0,0,0,0.2)' : 'none',
          position: 'fixed',
          top: 0,
          left: 0,
          zIndex: 1000,
        }}
      >
        {menuOpen && (
          <ul style={{ listStyleType: 'none', padding: 0, marginTop: '50px' }}>
            <li style={{ margin: '15px 0' }}><Link to="/company/dashboard">Dashboard</Link></li>
            <li style={{ margin: '15px 0' }}><Link to="/company/profile">Profile</Link></li>
            <li style={{ margin: '15px 0' }}>
              <button onClick={handleJobModalToggle} style={{ background:'none', border:'none', color:'#007bff', textDecoration:'underline', cursor:'pointer' }}>
                Post a Job
              </button>
            </li>
            <li style={{ margin: '15px 0' }}><Link to="/company/applications">View Applications</Link></li>
            <li style={{ margin: '15px 0' }}><Link to="/company/interns">Your Interns</Link></li>
            <li style={{ margin: '15px 0' }}><Link to="/company/settings">Settings</Link></li>
            <li style={{ margin: '15px 0', cursor:'pointer' }} onClick={handleLogout}>Logout</li>
          </ul>
        )}
      </div>

      {/* Main Content */}
      <div style={{
        marginLeft: menuOpen ? '250px' : '0',
        transition: 'margin-left 0.3s',
        padding: '20px',
        width: '100%',
        position: 'relative'
      }}>
        <button onClick={toggleMenu}
          style={{
            fontSize: '28px',
            fontWeight: 'bold',
            background: 'transparent',
            border: 'none',
            cursor: 'pointer',
            position: 'absolute',
            top: '20px',
            left: '20px',
            zIndex: 1101
          }}
        >☰</button>

        {/* Bell Notification */}
        <div onClick={togglePopup} style={{ position:'absolute', top:'20px', right:'20px', cursor:'pointer', zIndex:1101 }}>
          {hasNotification && (
            <div style={{
              position:'absolute',
              top: 0, right: 0,
              width: '18px', height: '18px',
              backgroundColor: 'red',
              color: 'white',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '12px',
              fontWeight: 'bold'
            }}>!</div>
          )}
          <img src="/bell.jpg" alt="Notifications" style={{ width:'36px', height:'36px' }} />
        </div>

        {showPopup && (
          <div style={{
            position:'absolute', top:'60px', right:'20px',
            width:'220px', padding:'10px', backgroundColor:'#fff',
            border:'1px solid #ddd', borderRadius:'8px',
            boxShadow:'0 4px 8px rgba(0,0,0,0.1)', zIndex:1102
          }}>
            <h3 style={{ marginTop: 0 }}>Notifications</h3>
            <p>{notificationMessage}</p>
            <button onClick={togglePopup}
              style={{
                padding:'5px', backgroundColor:'#4CAF50',
                color:'white', border:'none', borderRadius:'4px',
                width:'100%'
              }}
            >Close</button>
          </div>
        )}

        <h1>Welcome, {companyName}</h1>
        <p>This is your company dashboard. Use the menu to navigate between sections.</p>

        {/* Posted Jobs */}
        <div>
          <h2>Posted Jobs</h2>
          <ul>
            {postedJobs.map((job, idx) => (
              <li key={idx} style={{ marginBottom:'20px' }}>
                <p><strong>Job Title:</strong> {job.title}</p>
                <p><strong>Duration:</strong> {job.duration}</p>
                <p><strong>Paid:</strong> {job.isPaid ? 'Yes' : 'No'}</p>
                {job.isPaid && <p><strong>Salary:</strong> {job.salary}</p>}
                <p><strong>Skills:</strong> {job.skills}</p>
                <p><strong>Description:</strong> {job.description}</p>
                <button onClick={() => handleEditJob(idx)} style={{ marginRight:'10px' }}>Edit</button>
                <button onClick={() => handleDeleteJob(idx)} style={{ color:'red' }}>Delete</button>
              </li>
            ))}
          </ul>
        </div>

        {/* Job Modal */}
        {isJobModalOpen && (
          <div style={{
            position:'fixed', top:0, left:0, right:0, bottom:0,
            backgroundColor:'rgba(0,0,0,0.5)', display:'flex',
            justifyContent:'center', alignItems:'center'
          }}>
            <div style={{ backgroundColor:'#fff', padding:'20px', borderRadius:'8px', width:'400px' }}>
              <h3>{editingIndex !== null ? 'Edit Job' : 'Post a Job'}</h3>
              <form onSubmit={handleJobSubmit}>
                {/* form fields… */}
                <div>
                  <label>Job Title:</label>
                  <input
                    type="text"
                    name="title"
                    value={jobData.title}
                    onChange={handleJobInputChange}
                    required
                    style={{ width:'100%', marginBottom:'10px' }}
                  />
                </div>
                {/* …other inputs… */}
                <button type="submit"
                  style={{ backgroundColor:'#4CAF50', color:'#fff', width:'100%', padding:'10px', border:'none' }}
                >
                  {editingIndex !== null ? 'Update Job' : 'Post Job'}
                </button>
              </form>
              <button
                onClick={handleJobModalToggle}
                style={{ backgroundColor:'#f44336', color:'#fff', width:'100%', marginTop:'10px', padding:'10px', border:'none' }}
              >Cancel</button>
            </div>
          </div>
        )}

      </div>
    </div>
  );
}

export default CompanyPage;
