import React, { useState, useEffect, useMemo } from 'react';

function ViewRegistration({ setNotificationMessage }) {
  const [companyDetails, setCompanyDetails] = useState(null);
  const [companies, setCompanies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [industryOptions, setIndustryOptions] = useState([]);

  const loadPendingCompanies = () => {
    try {
      const all = JSON.parse(localStorage.getItem('companies')) || [];
      const pending = all.filter(c => !c.isAccepted);
      setCompanies(pending);
      setIndustryOptions([...new Set(pending.map(c => c.industry))].filter(Boolean));
    } catch (e) {
      console.error("Error loading companies:", e);
    }
  };

  useEffect(() => {
    loadPendingCompanies();
    const onStorage = (e) => e.key === 'companies' && loadPendingCompanies();
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
  }, []);

  const handleDownloadDocument = ({ dataUrl, name }) => {
    if (!dataUrl) {
      console.error("No document data!");
      return;
    }
    const link = document.createElement('a');
    link.href = dataUrl;
    link.download = name;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const filtered = useMemo(() => companies.filter(c => {
    const byName = c.companyName.toLowerCase().includes(searchQuery.toLowerCase());
    const byInd  = selectedIndustry ? c.industry === selectedIndustry : true;
    return byName && byInd;
  }), [companies, searchQuery, selectedIndustry]);

  return (
    <div>
      <h1>Company Registration View (Pending)</h1>

      <div style={{ textAlign:'center' }}>
        <input
          type="text"
          placeholder="Search by company name"
          value={searchQuery}
          onChange={e=>setSearchQuery(e.target.value)}
        />
      </div>

      <div style={{ textAlign:'center',margin:'20px 0' }}>
        <button
          onClick={()=>setSelectedIndustry('')}
          style={{
            backgroundColor: selectedIndustry===''?'#007bff':'#f8f9fa',
            color: selectedIndustry===''?'white':'black'
          }}
        >All Industries</button>
        <select value={selectedIndustry} onChange={e=>setSelectedIndustry(e.target.value)}>
          <option value="">Select Industry</option>
          {industryOptions.map((i,idx)=><option key={idx} value={i}>{i}</option>)}
        </select>
      </div>

      <div style={{ textAlign:'center',margin:'20px' }}>
        <h2>Pending Registrations</h2>
        {filtered.length ? filtered.map(c=>(
          <p key={c.companyEmail} style={{ marginBottom:'10px' }}>
            <button
              onClick={()=>setCompanyDetails(c)}
              style={{ 
                fontSize:'18px',color:'blue',background:'none',border:'none',textDecoration:'underline',cursor:'pointer' 
              }}
            >
              {c.companyLogo && <img src={c.companyLogo} alt="" style={{ width:'30px',height:'30px',marginRight:'10px',borderRadius:'50%' }}/>}
              {c.companyName}
            </button>
          </p>
        )) : <p>No pending registrations.</p>}
      </div>

      {companyDetails && (
        <div style={{
          position:'fixed',top:0,right:0,width:'350px',height:'100vh',
          backgroundColor:'#f8f9fa',boxShadow:'-2px 0 5px rgba(0,0,0,0.2)',
          padding:'20px',overflowY:'auto',zIndex:1000
        }}>
          <button onClick={()=>setCompanyDetails(null)} style={{
            position:'absolute',top:'10px',right:'10px',padding:'5px 10px',
            backgroundColor:'grey',color:'white',border:'none',cursor:'pointer'
          }}>Close</button>
          <h2 style={{ textAlign:'center' }}>Company Details</h2>

          <div style={{ textAlign:'center',marginBottom:'15px' }}>
            {companyDetails.companyLogo
              ? <img src={companyDetails.companyLogo} alt="" style={{ width:'150px',height:'150px',borderRadius:'50%',marginBottom:'10px' }} />
              : <p>No logo uploaded</p>
            }
            <h3>{companyDetails.companyName}</h3>
          </div>

          <p><strong>Industry:</strong> {companyDetails.industry}</p>
          <p><strong>Company Size:</strong> {companyDetails.companySize}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail}</p>

          {companyDetails.document && (
            <div style={{ textAlign:'center',marginTop:'20px' }}>
              <button
                onClick={()=>handleDownloadDocument(companyDetails.document)}
                style={{
                  padding:'10px 20px',backgroundColor:'#007bff',color:'white',
                  borderRadius:'5px',border:'none',cursor:'pointer'
                }}
              >Download Legal Document</button>
            </div>
          )}

          <div style={{ display:'flex',justifyContent:'space-around',marginTop:'20px' }}>
            <button onClick={()=>{
              // accept logic...
            }} style={{ /* ... */ }}>Accept</button>
            <button onClick={()=>{
              // reject logic...
            }} style={{ /* ... */ }}>Reject</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
