import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function CompanyPage() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [companyName, setCompanyName] = useState('');
  const [isJobModalOpen, setIsJobModalOpen] = useState(false);
  const [editingIndex, setEditingIndex] = useState(null);
  const [jobData, setJobData] = useState({
    title: '',
    duration: '',
    isPaid: false,
    salary: '',
    skills: '',
    description: '',
  });
  const [postedJobs, setPostedJobs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('currentCompany'));
    if (stored) {
      setCompanyName(stored.companyName || stored.companyEmail);
      const email = stored.companyEmail;
      const companyJobs = JSON.parse(localStorage.getItem('jobsByCompany')) || {};
      setPostedJobs(companyJobs[email] || []);
    } else {
      navigate('/company-login');
    }
  }, [navigate]);

  const toggleMenu = () => setMenuOpen(prev => !prev);

  const handleLogout = () => {
    localStorage.removeItem('currentCompany');
    navigate('/company-login');
  };

  const handleJobModalToggle = () => {
    setIsJobModalOpen(prev => !prev);
    setEditingIndex(null);
    setJobData({
      title: '',
      duration: '',
      isPaid: false,
      salary: '',
      skills: '',
      description: '',
    });
  };

  const handleJobInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setJobData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const saveJobsToStorage = (jobs) => {
    const currentCompany = JSON.parse(localStorage.getItem('currentCompany'));
    const email = currentCompany?.companyEmail;
    if (!email) return;
    const jobsByCompany = JSON.parse(localStorage.getItem('jobsByCompany')) || {};
    jobsByCompany[email] = jobs;
    localStorage.setItem('jobsByCompany', JSON.stringify(jobsByCompany));
  };

  const handleJobSubmit = (e) => {
    e.preventDefault();
    let updatedJobs;

    if (editingIndex !== null) {
      updatedJobs = [...postedJobs];
      updatedJobs[editingIndex] = jobData;
    } else {
      updatedJobs = [...postedJobs, jobData];
    }

    setPostedJobs(updatedJobs);
    saveJobsToStorage(updatedJobs);
    setEditingIndex(null);
    setIsJobModalOpen(false);

    setJobData({
      title: '',
      duration: '',
      isPaid: false,
      salary: '',
      skills: '',
      description: '',
    });
  };

  const handleEditJob = (index) => {
    setEditingIndex(index);
    setJobData(postedJobs[index]);
    setIsJobModalOpen(true);
  };

  const handleDeleteJob = (index) => {
    const updatedJobs = postedJobs.filter((_, i) => i !== index);
    setPostedJobs(updatedJobs);
    saveJobsToStorage(updatedJobs);
  };

  return (
    <div style={{ display: 'flex' }}>
      {/* Sidebar */}
      <div
        style={{
          width: menuOpen ? '250px' : '0',
          height: '100vh',
          backgroundColor: '#f8f9fa',
          overflowX: 'hidden',
          transition: '0.3s',
          padding: menuOpen ? '20px' : '0',
          boxShadow: menuOpen ? '2px 0 5px rgba(0,0,0,0.2)' : 'none',
          position: 'fixed',
          top: 0,
          left: 0,
          zIndex: 1000,
        }}
      >
        {menuOpen && (
          <ul style={{ listStyleType: 'none', padding: 0, marginTop: '50px' }}>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/dashboard">Dashboard</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/profile">Profile</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <button
                onClick={handleJobModalToggle}
                style={{
                  background: 'none',
                  border: 'none',
                  padding: 0,
                  color: '#007bff',
                  textDecoration: 'underline',
                  cursor: 'pointer',
                  font: 'inherit',
                }}
              >
                Post a Job
              </button>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/applications">View Applications</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/interns">Your Interns</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/settings">Settings</Link>
            </li>
            <li style={{ margin: '15px 0', cursor: 'pointer' }} onClick={handleLogout}>
              Logout
            </li>
          </ul>
        )}
      </div>

      {/* Main Content */}
      <div
        style={{
          marginLeft: menuOpen ? '250px' : '0',
          transition: 'margin-left 0.3s',
          padding: '20px',
          width: '100%',
        }}
      >
        <button
          onClick={toggleMenu}
          style={{
            fontSize: '28px',
            fontWeight: 'bold',
            background: 'transparent',
            border: 'none',
            cursor: 'pointer',
            zIndex: 1101,
            position: 'absolute',
            top: '20px',
            left: '20px',
          }}
        >
          â˜°
        </button>

        <h1>Welcome, {companyName}</h1>
        <p>This is your company dashboard. Use the menu to navigate between sections.</p>

        {/* Posted Jobs */}
        <div>
          <h2>Posted Jobs</h2>
          <ul>
            {postedJobs.map((job, index) => (
              <li key={index} style={{ marginBottom: '20px' }}>
                <p><strong>Job Title:</strong> {job.title}</p>
                <p><strong>Duration:</strong> {job.duration}</p>
                <p><strong>Paid:</strong> {job.isPaid ? 'Yes' : 'No'}</p>
                {job.isPaid && <p><strong>Salary:</strong> {job.salary}</p>}
                <p><strong>Skills:</strong> {job.skills}</p>
                <p><strong>Description:</strong> {job.description}</p>
                <button onClick={() => handleEditJob(index)} style={{ marginRight: '10px' }}>
                  Edit
                </button>
                <button onClick={() => handleDeleteJob(index)} style={{ color: 'red' }}>
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Job Modal */}
        {isJobModalOpen && (
          <div
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              backgroundColor: 'rgba(0,0,0,0.5)',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <div
              style={{
                backgroundColor: '#fff',
                padding: '20px',
                borderRadius: '8px',
                width: '400px',
              }}
            >
              <h3>{editingIndex !== null ? 'Edit Job' : 'Post a Job'}</h3>
              <form onSubmit={handleJobSubmit}>
                <div>
                  <label>Job Title:</label>
                  <input
                    type="text"
                    name="title"
                    value={jobData.title}
                    onChange={handleJobInputChange}
                    required
                    style={{ width: '100%', marginBottom: '10px' }}
                  />
                </div>
                <div>
                  <label>Duration:</label>
                  <input
                    type="text"
                    name="duration"
                    value={jobData.duration}
                    onChange={handleJobInputChange}
                    required
                    style={{ width: '100%', marginBottom: '10px' }}
                  />
                </div>
                <div>
                  <label>
                    Paid:
                    <input
                      type="checkbox"
                      name="isPaid"
                      checked={jobData.isPaid}
                      onChange={handleJobInputChange}
                      style={{ marginLeft: '10px' }}
                    />
                  </label>
                </div>
                {jobData.isPaid && (
                  <div>
                    <label>Salary:</label>
                    <input
                      type="number"
                      name="salary"
                      value={jobData.salary}
                      onChange={handleJobInputChange}
                      required
                      style={{ width: '100%', marginBottom: '10px' }}
                    />
                  </div>
                )}
                <div>
                  <label>Skills Required:</label>
                  <input
                    type="text"
                    name="skills"
                    value={jobData.skills}
                    onChange={handleJobInputChange}
                    required
                    style={{ width: '100%', marginBottom: '10px' }}
                  />
                </div>
                <div>
                  <label>Job Description:</label>
                  <textarea
                    name="description"
                    value={jobData.description}
                    onChange={handleJobInputChange}
                    required
                    style={{ width: '100%', marginBottom: '10px' }}
                  />
                </div>
                <button
                  type="submit"
                  style={{ backgroundColor: '#4CAF50', color: '#fff', width: '100%', padding: '10px', border: 'none' }}
                >
                  {editingIndex !== null ? 'Update Job' : 'Post Job'}
                </button>
              </form>
              <button
                onClick={handleJobModalToggle}
                style={{ backgroundColor: '#f44336', color: '#fff', width: '100%', marginTop: '10px', padding: '10px', border: 'none' }}
              >
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CompanyPage;
