import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function CompanyPage() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [companyName, setCompanyName] = useState('');
  const [isJobModalOpen, setIsJobModalOpen] = useState(false);
  const [jobData, setJobData] = useState({
    title: '',
    duration: '',
    isPaid: false,
    salary: '',
    skills: '',
    description: '',
  });
  const [postedJobs, setPostedJobs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = JSON.parse(sessionStorage.getItem('currentCompany'));
    if (stored) {
      setCompanyName(stored.companyName || stored.companyEmail);
    } else {
      // Optional: redirect to login if no company is logged in
      navigate('/company-login');
    }
  }, [navigate]);

  const toggleMenu = () => {
    setMenuOpen(prev => !prev);
  };

  const handleLogout = () => {
    sessionStorage.removeItem('currentCompany');
    navigate('/company-login');
  };

  const handleJobModalToggle = () => {
    setIsJobModalOpen(prev => !prev);
  };

  const handleJobInputChange = (e) => {
    const { name, value } = e.target;
    setJobData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleJobSubmit = (e) => {
    e.preventDefault();
    // Add new job to the list
    setPostedJobs([...postedJobs, jobData]);
    setIsJobModalOpen(false); // Close the modal after posting
    setJobData({
      title: '',
      duration: '',
      isPaid: false,
      salary: '',
      skills: '',
      description: '',
    });
  };

  return (
    <div style={{ display: 'flex' }}>
      {/* Sidebar */}
      <div
        style={{
          width: menuOpen ? '250px' : '0',
          height: '100vh',
          backgroundColor: '#f8f9fa',
          overflowX: 'hidden',
          transition: '0.3s',
          padding: menuOpen ? '20px' : '0',
          boxShadow: menuOpen ? '2px 0 5px rgba(0,0,0,0.2)' : 'none',
          position: 'fixed',
          top: 0,
          left: 0,
          zIndex: 1000,
        }}
      >
        {menuOpen && (
          <ul style={{ listStyleType: 'none', padding: 0, marginTop: '50px' }}>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/dashboard">Dashboard</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/profile">Profile</Link>
            </li>
            <li style={{ margin: '15px 0' }} onClick={handleJobModalToggle}>
              Post a Job
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/applications">View Applications</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/interns">Your Interns</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/settings">Settings</Link>
            </li>
            <li style={{ margin: '15px 0', cursor: 'pointer' }} onClick={handleLogout}>
              Logout
            </li>
          </ul>
        )}
      </div>

      {/* Main content */}
      <div
        style={{
          marginLeft: menuOpen ? '250px' : '0',
          transition: 'margin-left 0.3s',
          padding: '20px',
          width: '100%',
        }}
      >
        <button
          onClick={toggleMenu}
          style={{
            fontSize: '28px',
            fontWeight: 'bold',
            background: 'transparent',
            border: 'none',
            cursor: 'pointer',
            zIndex: 1101,
            position: 'absolute',
            top: '20px',
            left: '20px',
          }}
        >
          â˜°
        </button>

        <h1>Welcome, {companyName}</h1>
        <p>This is your company dashboard. Use the menu to navigate between sections.</p>

        {/* Display Posted Jobs */}
        <div>
          <h2>Posted Jobs</h2>
          <ul>
            {postedJobs.map((job, index) => (
              <li key={index}>
                <strong>{job.title}</strong>
                <p>Duration: {job.duration}</p>
                <p>{job.isPaid ? `Salary: ${job.salary}` : 'Unpaid'}</p>
                <p>Skills Required: {job.skills}</p>
                <p>{job.description}</p>
              </li>
            ))}
          </ul>
        </div>

        {/* Job Posting Modal */}
        {isJobModalOpen && (
          <div
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              backgroundColor: 'rgba(0,0,0,0.5)',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <div
              style={{
                backgroundColor: '#fff',
                padding: '20px',
                borderRadius: '8px',
                width: '400px',
              }}
            >
              <h3>Post a Job</h3>
              <form onSubmit={handleJobSubmit}>
                <div>
                  <label>Job Title:</label>
                  <input
                    type="text"
                    name="title"
                    value={jobData.title}
                    onChange={handleJobInputChange}
                    style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
                    required
                  />
                </div>
                <div>
                  <label>Duration:</label>
                  <input
                    type="text"
                    name="duration"
                    value={jobData.duration}
                    onChange={handleJobInputChange}
                    style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
                    required
                  />
                </div>
                <div>
                  <label>
                    Paid:
                    <input
                      type="checkbox"
                      name="isPaid"
                      checked={jobData.isPaid}
                      onChange={() => setJobData((prev) => ({ ...prev, isPaid: !prev.isPaid }))}
                    />
                  </label>
                </div>
                {jobData.isPaid && (
                  <div>
                    <label>Salary:</label>
                    <input
                      type="number"
                      name="salary"
                      value={jobData.salary}
                      onChange={handleJobInputChange}
                      style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
                      required
                    />
                  </div>
                )}
                <div>
                  <label>Skills Required:</label>
                  <input
                    type="text"
                    name="skills"
                    value={jobData.skills}
                    onChange={handleJobInputChange}
                    style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
                    required
                  />
                </div>
                <div>
                  <label>Job Description:</label>
                  <textarea
                    name="description"
                    value={jobData.description}
                    onChange={handleJobInputChange}
                    style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
                    required
                  />
                </div>
                <button
                  type="submit"
                  style={{
                    backgroundColor: '#4CAF50',
                    color: '#fff',
                    padding: '10px',
                    border: 'none',
                    width: '100%',
                  }}
                >
                  Post Job
                </button>
              </form>
              <button
                onClick={handleJobModalToggle}
                style={{
                  backgroundColor: '#f44336',
                  color: '#fff',
                  padding: '10px',
                  border: 'none',
                  width: '100%',
                  marginTop: '10px',
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CompanyPage;
