import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';



function CompanyLogin() {;
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Check if the company has registered on page load
  useEffect(() => {
    // If logged in, navigate to company page directly
    if (localStorage.getItem('companyLoggedIn')) {
      navigate('/companypage');
    } else if (!localStorage.getItem('isRegistered')) {
      // Redirect to registration page if not registered
      navigate('/company-register');
    }
  }, [navigate]);

  // Handle form submission
  const handleLogin = (e) => {
    e.preventDefault();
    if (email === 'company@guc.edu.eg' && password === 'company123') {
      // Simulate successful login
      localStorage.setItem('companyLoggedIn', 'true'); // Save login status
      setError(''); // Reset error on successful login
      navigate('/companypage'); // Redirect to company dashboard
    } else {
      setError('Invalid credentials. Please try again.');
    }
  };

  return (
    <div style={{ height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      {/* Login Form */}
      <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px', width: '300px' }}>
        <h2>Company Login</h2>
        {error && <p style={{ color: 'red' }}>{error}</p>} {/* Show error message */}
        <form onSubmit={handleLogin}>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: '#ddd',
              }}
            />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: '#ddd',
              }}
            />
          </div>

          <button
            type="submit"
            style={{
              padding: '10px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              width: '100%',
            }}
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
}

export default CompanyLogin;
