import React, { useState, useEffect } from 'react';

function ViewRegistration({ setNotificationMessage }) {
  const [companyDetails, setCompanyDetails] = useState(null);
  const [companies, setCompanies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [industryOptions, setIndustryOptions] = useState([]);
  const [isLoading, setIsLoading] = useState(true); // Loading state for fetching companies

  useEffect(() => {
    const storedCompanies = JSON.parse(localStorage.getItem('companies')) || [];
    setCompanies(storedCompanies);

    // Extract unique industries from the companies list
    const industries = [...new Set(storedCompanies.map(company => company.industry))];
    setIndustryOptions(industries);

    setIsLoading(false); // Set loading to false once data is fetched
  }, []);

  const handleCompanyClick = (company) => {
    setCompanyDetails(company);
  };

  const handleCloseSidebar = () => {
    setCompanyDetails(null);
  };

  // Filter companies by search query and industry
  const filteredCompanies = companies.filter(company => {
    const matchesName = company.companyName.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesIndustry = selectedIndustry ? company.industry === selectedIndustry : true;
    return matchesName && matchesIndustry;
  });

  const handleAccept = () => {
    if (setNotificationMessage) {
      // Send notification to companyLogin page
      setNotificationMessage(`You have accepted ${companyDetails.companyName}`);

      // Remove the company from the list
      const updatedCompanies = companies.filter(company => company.companyEmail !== companyDetails.companyEmail);
      setCompanies(updatedCompanies);
      localStorage.setItem('companies', JSON.stringify(updatedCompanies));

      // Close the sidebar after accepting
      setCompanyDetails(null);
    } else {
      console.warn('setNotificationMessage is not passed as a prop.');
    }
  };

  const handleReject = () => {
    // Remove the company from the list without accepting
    const updatedCompanies = companies.filter(company => company.companyEmail !== companyDetails.companyEmail);
    setCompanies(updatedCompanies);
    localStorage.setItem('companies', JSON.stringify(updatedCompanies));

    // Close the sidebar after rejecting
    setCompanyDetails(null);
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>Company Registration View</h1>

      {/* Search Bar */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by company name"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{
            padding: '8px',
            fontSize: '16px',
            marginBottom: '10px',
            width: '300px',
          }}
        />
      </div>

      {/* Industry Filter with Dropdown */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <button
          onClick={() => setSelectedIndustry('')}
          style={{
            padding: '8px 16px',
            marginRight: '10px',
            backgroundColor: '#f8f9fa',
            border: '1px solid #ddd',
            cursor: 'pointer',
            fontSize: '16px',
          }}
        >
          All Industries
        </button>
        
        {/* Dropdown Menu for Industry Filter */}
        <select
          value={selectedIndustry}
          onChange={(e) => setSelectedIndustry(e.target.value)}
          style={{
            padding: '8px 16px',
            backgroundColor: '#f8f9fa',
            border: '1px solid #ddd',
            cursor: 'pointer',
            fontSize: '16px',
          }}
        >
          <option value="">Select Industry</option>
          {industryOptions.map((industry, index) => (
            <option key={index} value={industry}>
              {industry}
            </option>
          ))}
        </select>
      </div>

      {/* List of Companies */}
      <div style={{ margin: '20px', textAlign: 'center' }}>
        <h2>Companies</h2>
        {isLoading ? (
          <div style={{ textAlign: 'center' }}>Loading companies...</div>
        ) : filteredCompanies.length > 0 ? (
          filteredCompanies.map((company, index) => (
            <p key={index} style={{ marginBottom: '10px' }}>
              <button
                onClick={() => handleCompanyClick(company)}
                style={{
                  fontSize: '18px',
                  color: 'blue',
                  background: 'none',
                  border: 'none',
                  textDecoration: 'underline',
                  cursor: 'pointer',
                  padding: '0',
                }}
              >
                {company.companyLogo && (
                  <img
                    src={company.companyLogo}
                    alt={`${company.companyName} logo`}
                    style={{
                      width: '30px',
                      height: '30px',
                      marginRight: '10px',
                      verticalAlign: 'middle',
                      borderRadius: '50%', // Make the logo circular
                    }}
                  />
                )}
                {company.companyName}
              </button>
            </p>
          ))
        ) : (
          <p>No companies found</p>
        )}
      </div>

      {/* Sidebar showing company details */}
      {companyDetails && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            right: 0,
            width: '350px',
            height: '100vh',
            backgroundColor: '#f8f9fa',
            boxShadow: '-2px 0 5px rgba(0,0,0,0.2)',
            padding: '20px',
            zIndex: 1000,
            overflowY: 'auto',
          }}
        >
          <button
            onClick={handleCloseSidebar}
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              padding: '5px 10px',
              backgroundColor: 'red',
              color: 'white',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            Close
          </button>
          <h2 style={{ textAlign: 'center' }}>Company Details</h2>

          <div style={{ textAlign: 'center', marginBottom: '15px' }}>
            {companyDetails.companyLogo ? (
              <img
                src={companyDetails.companyLogo}
                alt={`${companyDetails.companyName} logo`}
                style={{
                  width: '150px',
                  height: '150px',
                  marginBottom: '10px',
                  borderRadius: '50%', // Circular shape for company logo in the sidebar
                }}
              />
            ) : (
              <p>No logo uploaded</p>
            )}
            <h3>{companyDetails.companyName}</h3>
          </div>

          <p><strong>Industry:</strong> {companyDetails.industry}</p>
          <p><strong>Company Size:</strong> {companyDetails.companySize}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail}</p>

          {/* Accept and Reject Buttons */}
          <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '20px' }}>
            <button
              onClick={handleAccept}
              style={{
                padding: '10px 20px',
                backgroundColor: '#4CAF50',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              Accept
            </button>
            <button
              onClick={handleReject}
              style={{
                padding: '10px 20px',
                backgroundColor: '#f44336',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              Reject
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
