import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('You have no notifications!');
  const [hasNotification, setHasNotification] = useState(false);

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  useEffect(() => {
    const handleStorageChange = () => {
      const companies = JSON.parse(localStorage.getItem('companies')) || [];
      const companyUser = companies.find((c) => c.companyEmail === email);

      if (companyUser?.hasNotification) {
        setNotificationMessage('Your registration has been accepted!');
        setHasNotification(true);
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, [email]);

  const handleLogin = (e) => {
    e.preventDefault();

    const newErrors = {};

    if (!email) {
      newErrors.email = 'Email is required';
    } else if (!emailRegex.test(email)) {
      newErrors.email = 'Invalid email format';
    }

    if (!password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    if (Object.keys(newErrors).length > 0) return;

    const companies = JSON.parse(localStorage.getItem('companies')) || [];
    const companyUser = companies.find((c) => c.companyEmail === email);

    if (!companyUser) {
      setErrors({ form: 'Invalid email' });
    } else if (companyUser.password !== password) {
      setErrors({ form: 'Wrong password' });
    } else if (!companyUser.isAccepted) {
      setErrors({ form: 'You are not accepted yet!' });
    } else {
      if (companyUser.hasNotification) {
        setNotificationMessage('Your registration has been accepted!');
        setHasNotification(true);

        const updatedCompanies = companies.map(c =>
          c.companyEmail === email ? { ...c, hasNotification: false } : c
        );
        localStorage.setItem('companies', JSON.stringify(updatedCompanies));
      }

      navigate('/company-dashboard');
    }
  };

  const inputStyle = (error) => ({
    width: '100%',
    padding: '8px',
    marginBottom: '5px',
    border: error ? '1px solid red' : '1px solid #ddd',
    boxSizing: 'border-box',
  });

  const errorText = (msg) => (
    <div style={{ color: 'red', fontSize: '12px', marginTop: '5px' }}>{msg}</div>
  );

  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        padding: '20px 0',
        position: 'relative',
      }}
    >
      {/* Bell icon */}
      <div
        onClick={togglePopup}
        style={{
          position: 'absolute',
          top: '20px',
          right: '20px',
          cursor: 'pointer',
          fontSize: '24px',
        }}
      >
        {hasNotification && (
          <div
            style={{
              position: 'absolute',
              top: '0',
              right: '0',
              width: '20px',
              height: '20px',
              backgroundColor: 'red',
              color: 'white',
              borderRadius: '50%',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              fontSize: '12px',
              fontWeight: 'bold',
            }}
          >
            1
          </div>
        )}
        <img src="/bell.jpg" alt="Bell Icon" style={{ width: '40px', height: '40px' }} />
      </div>

      {/* Pop-up window */}
      {showPopup && (
        <div
          style={{
            position: 'absolute',
            top: '50px',
            right: '20px',
            width: '200px',
            padding: '10px',
            backgroundColor: 'white',
            border: '1px solid #ddd',
            borderRadius: '8px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          }}
        >
          <h3>Notifications</h3>
          <p>{notificationMessage}</p>
          <button
            onClick={togglePopup}
            style={{
              padding: '5px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
            }}
          >
            Close
          </button>
        </div>
      )}

      {/* Login Form */}
      <div
        style={{
          background: '#fff',
          borderRadius: '8px',
          padding: '20px',
          boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
          width: '300px',
        }}
      >
        <h2 style={{ textAlign: 'center' }}>Company Login</h2>

        <form onSubmit={handleLogin}>
          <div style={{ marginBottom: '15px' }}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={inputStyle(errors.email)}
            />
            {errors.email && errorText(errors.email)}
          </div>

          <div style={{ marginBottom: '15px' }}>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle(errors.password)}
            />
            {errors.password && errorText(errors.password)}
          </div>

          {errors.form && errorText(errors.form)}

          <div style={{ textAlign: 'center' }}>
            <button
              type="submit"
              style={{
                padding: '10px 20px',
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                width: '100%',
              }}
            >
              Login
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default CompanyLogin;
