import React, { useState, useEffect, useMemo } from 'react';

function ViewRegistration({ setNotificationMessage }) {
  const [companyDetails, setCompanyDetails] = useState(null);
  const [companies, setCompanies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [industryOptions, setIndustryOptions] = useState([]);

  // Load pending companies from sessionStorage
  const loadPendingCompanies = () => {
    try {
      // Get the companies from sessionStorage
      const all = JSON.parse(sessionStorage.getItem('companies')) || [];
      
      if (all.length === 0) {
        console.log('No companies found in sessionStorage.');
      }

      // Filter the companies that are not accepted (pending)
      const pending = all.filter(company => !company.isAccepted);

      // Update state with the pending companies
      setCompanies(pending);

      // Extract unique industries from pending companies and set options for the industry filter
      const uniqueIndustries = [...new Set(pending.map(c => c.industry))].filter(Boolean);
      setIndustryOptions(uniqueIndustries);

    } catch (error) {
      console.error('Error loading companies from sessionStorage:', error);
    }
  };

  useEffect(() => {
    loadPendingCompanies(); // Load companies initially
    
    const onStorage = (e) => {
      if (e.key === 'companies') {
        loadPendingCompanies(); // Refresh companies on sessionStorage change
      }
    };
    
    window.addEventListener('storage', onStorage); // Listen for changes to sessionStorage
    
    return () => {
      window.removeEventListener('storage', onStorage); // Clean up listener
    };
  }, []);

  const handleAccept = () => {
    if (!companyDetails) return;
    const all = JSON.parse(sessionStorage.getItem('companies')) || [];
    const updated = all.map(c =>
      c.companyEmail === companyDetails.companyEmail
        ? { ...c, isAccepted: true, hasNotification: true }
        : c
    );
    sessionStorage.setItem('companies', JSON.stringify(updated));
    setNotificationMessage?.(`Registration for ${companyDetails.companyName} accepted.`);
    setCompanyDetails(null);
    loadPendingCompanies();
  };

  const handleReject = () => {
    if (!companyDetails) return;
    const all = JSON.parse(sessionStorage.getItem('companies')) || [];
    const updated = all.filter(c => c.companyEmail !== companyDetails.companyEmail);
    sessionStorage.setItem('companies', JSON.stringify(updated));
    setNotificationMessage?.(`Registration for ${companyDetails.companyName} rejected.`);
    setCompanyDetails(null);
    loadPendingCompanies();
  };

  const handleDownloadDocument = (document) => {
    if (!document || !document.dataUrl) return;
    
    // Create a download link element
    const link = window.document.createElement('a');
    link.href = document.dataUrl;
    link.download = document.name || 'document';
    
    // Programmatically trigger the click to download
    link.click();
  };

  const filtered = useMemo(() =>
    companies.filter(c =>
      c.companyName.toLowerCase().includes(searchQuery.toLowerCase()) &&
      (selectedIndustry ? c.industry === selectedIndustry : true)
    ),
    [companies, searchQuery, selectedIndustry]
  );

  return (
    <div>
      <h1>Pending Company Registrations</h1>

      <div style={{ textAlign: 'center' }}>
        <input
          type="text"
          placeholder="Search by name"
          value={searchQuery}
          onChange={e => setSearchQuery(e.target.value)}
          style={{ padding: '8px', width: '90%', maxWidth: '300px', margin: '10px auto', borderRadius:'4px', border:'1px solid #ccc' }}
        />
      </div>

      <div style={{ textAlign: 'center', margin: '20px 0' }}>
        <button
          onClick={() => setSelectedIndustry('')}
          style={{
            backgroundColor: selectedIndustry === '' ? '#007bff' : '#f8f9fa',
            color: selectedIndustry === '' ? 'white' : 'black',
            marginRight: '10px',
            border: '1px solid #007bff',
            borderRadius: '4px',
            padding: '8px 12px',
            cursor: 'pointer'
          }}
        >
          All Industries
        </button>
        <select
          value={selectedIndustry}
          onChange={e => setSelectedIndustry(e.target.value)}
          style={{ padding: '8px 12px', borderRadius: '4px', border: '1px solid #ccc' }}
        >
          <option value="">Select Industry</option>
          {industryOptions.map((i, idx) => (
            <option key={idx} value={i}>{i}</option>
          ))}
        </select>
      </div>

      <div style={{ textAlign: 'center', margin: '20px' }}>
        {filtered.length ? filtered.map(c => (
          <p key={c.companyEmail} style={{ marginBottom: '10px' }}>
            <button
              onClick={() => setCompanyDetails(c)}
              style={{
                display: 'inline-flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '18px',
                color: 'blue',
                background: 'none',
                border: 'none',
                textDecoration: 'underline',
                cursor: 'pointer',
                padding: 0,
                lineHeight: 1
              }}
            >
              {c.companyLogo && (
                <img
                  src={c.companyLogo}
                  alt={`${c.companyName} logo`}
                  style={{
                    width: '30px',
                    height: '30px',
                    marginRight: '10px',
                    borderRadius: '50%',
                    objectFit: 'cover'
                  }}
                />
              )}
              <span style={{ textDecoration: 'underline' }}>{c.companyName}</span>
            </button>
          </p>
        )) : (
          <p>No pending registrations.</p>
        )}
      </div>

      {/* Company Details */}
      {companyDetails && (
        <div style={{
          position: 'fixed', top: 0, right: 0, width: '350px', height: '100vh',
          backgroundColor: '#f8f9fa', boxShadow: '-2px 0 5px rgba(0,0,0,0.2)',
          padding: '20px', overflowY: 'auto', zIndex: 1000
        }}>
          <button
            onClick={() => setCompanyDetails(null)}
            style={{
              position: 'absolute', top: '10px', right: '10px',
              backgroundColor: 'grey', color: 'white', border: 'none', cursor: 'pointer', borderRadius:'4px',
              padding: '5px 10px'
            }}
          >
            Close
          </button>
          <h2 style={{ textAlign: 'center' }}>Company Details</h2>

          <div style={{ textAlign: 'center', marginBottom: '15px' }}>
            {companyDetails.companyLogo
              ? (
                <img
                  src={companyDetails.companyLogo}
                  alt={`${companyDetails.companyName} logo`}
                  style={{ width: '150px', height: '150px', borderRadius: '50%', objectFit:'cover' }}
                />
              ) : <p>No logo uploaded</p>}
            <h3>{companyDetails.companyName}</h3>
          </div>

          <p><strong>Industry:</strong> {companyDetails.industry}</p>
          <p><strong>Size:</strong> {companyDetails.companySize}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail}</p>

          {companyDetails.document && (
            <div style={{ textAlign: 'center', marginTop: '20px' }}>
              <button
                onClick={() => handleDownloadDocument(companyDetails.document)}
                style={{
                  padding: '10px 20px', backgroundColor: '#007bff',
                  color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer'
                }}
              >
                Download Document
              </button>
            </div>
          )}

          <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '20px' }}>
            <button
              onClick={handleAccept}
              style={{
                padding: '10px', backgroundColor: '#4CAF50',
                color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer'
              }}
            >
              Accept
            </button>
            <button
              onClick={handleReject}
              style={{
                padding: '10px', backgroundColor: '#f44336',
                color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer'
              }}
            >
              Reject
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
