// src/CompanyRegister.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyRegister() {
  // State hooks to manage input values
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [companyLogo, setCompanyLogo] = useState(null);
  const [companyEmail, setCompanyEmail] = useState('');
  const [document, setDocument] = useState('');

  // Error state for field validation
  const [errors, setErrors] = useState({
    companyName: false,
    industry: false,
    companySize: false,
    companyLogo: false,
    companyEmail: false,
    document: false,
  });

  const navigate = useNavigate();

  // Regex pattern for valid email
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  // Handle form submission
  const handleRegister = (e) => {
    e.preventDefault();

    // Initialize errors object
    const newErrors = {
      companyName: !companyName,
      industry: !industry,
      companySize: !companySize,
      companyLogo: !companyLogo,
      companyEmail: !companyEmail || !emailRegex.test(companyEmail), // Check if email is valid
      document: !document,
    };

    // Set errors state to show the red border
    setErrors(newErrors);

    // If there are errors, show alerts and stop the registration
    if (Object.values(newErrors).includes(true)) {
      // Show individual alerts for each missing field
      if (!companyName) alert('Please enter a company name.');
      if (!industry) alert('Please enter the industry.');
      if (!companySize) alert('Please select the company size.');
      if (!companyLogo) alert('Please upload the company logo.');
      if (!companyEmail) {
        alert('Please enter an official company email.');
      } else if (!emailRegex.test(companyEmail)) {
        alert('Please enter a valid email format (e.g., example@domain.com).');
      }
      if (!document) alert('Please upload the required documents.');

      return;
    }

    // If no errors, proceed with registration (e.g., send data to server)
    alert('Company Registered Successfully!');

    // Redirect to the login page after registration
    navigate('/');
  };

  // Handle file upload for company logo and documents
  const handleLogoChange = (e) => {
    setCompanyLogo(e.target.files[0]);
  };

  const handleDocumentChange = (e) => {
    setDocument(e.target.files[0]);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
        <h2>Company Register</h2>
        <form onSubmit={handleRegister}>
          <div>
            <label htmlFor="companyName">Company Name:</label>
            <input
              type="text"
              id="companyName"
              value={companyName}
              onChange={(e) => setCompanyName(e.target.value)}
              required
              placeholder="Enter company name"
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.companyName ? 'red' : '#ddd',
              }}
            />
          </div>
          <div>
            <label htmlFor="industry">Industry:</label>
            <input
              type="text"
              id="industry"
              value={industry}
              onChange={(e) => setIndustry(e.target.value)}
              required
              placeholder="Enter industry"
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.industry ? 'red' : '#ddd',
              }}
            />
          </div>
          <div>
            <label htmlFor="companySize">Company Size:</label>
            <select
              id="companySize"
              value={companySize}
              onChange={(e) => setCompanySize(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.companySize ? 'red' : '#ddd',
              }}
            >
              <option value="">Select company size</option>
              <option value="small">Small (50 employees or less)</option>
              <option value="medium">Medium (51-100 employees)</option>
              <option value="large">Large (101-500 employees)</option>
              <option value="corporate">Corporate (500+ employees)</option>
            </select>
          </div>
          <div>
            <label htmlFor="companyLogo">Company Logo:</label>
            <input
              type="file"
              id="companyLogo"
              onChange={handleLogoChange}
              required
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.companyLogo ? 'red' : '#ddd',
              }}
            />
          </div>
          <div>
            <label htmlFor="companyEmail">Official Company Email:</label>
            <input
              type="email"
              id="companyEmail"
              value={companyEmail}
              onChange={(e) => setCompanyEmail(e.target.value)}
              required
              placeholder="Enter official company email"
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.companyEmail ? 'red' : '#ddd',
              }}
            />
          </div>
          <div>
            <label htmlFor="document">Upload Documents (Tax or Other Legal Documents):</label>
            <input
              type="file"
              id="document"
              onChange={handleDocumentChange}
              required
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.document ? 'red' : '#ddd',
              }}
            />
          </div>
          <button type="submit" style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px' }}>
            Register
          </button>
        </form>
      </div>
      {/* Back button placed at the bottom left */}
      <button
        onClick={() => navigate('/')}
        style={{
          position: 'fixed',
          bottom: '20px',
          left: '20px',
          padding: '10px',
          backgroundColor: '#f44336',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
        }}
      >
        Back 
      </button>
    </div>
  );
}

export default CompanyRegister;
