import React, { useState, useEffect } from 'react';

function ViewRegistration() {
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);

  // Fetch companies from localStorage on component mount
  useEffect(() => {
    const storedCompanies = JSON.parse(localStorage.getItem('companies')) || [];
    setCompanies(storedCompanies);
  }, []);

  // Show the details of the selected company in the sidebar
  const handleCompanyClick = (company) => {
    setSelectedCompany(company);
    setSidebarOpen(true);
  };

  // Close the sidebar
  const closeSidebar = () => {
    setSidebarOpen(false);
    setSelectedCompany(null);
  };

  // Function to handle document download
  const downloadDocument = (documentName) => {
    const fileUrl = URL.createObjectURL(new Blob([documentName], { type: 'application/pdf' }));
    const link = document.createElement('a');
    link.href = fileUrl;
    link.download = documentName;
    link.click();
  };

  return (
    <div>
      {/* List of companies with clickable hyperlinks */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        {companies.map((company, index) => (
          <div key={index}>
            <a
              href="#"
              onClick={() => handleCompanyClick(company)}
              style={{
                marginBottom: '10px',
                textDecoration: 'none',
                color: 'blue',
                fontSize: '18px',
              }}
            >
              {company.companyName}
            </a>
          </div>
        ))}
      </div>

      {/* Sidebar with company details */}
      {sidebarOpen && selectedCompany && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            right: 0,
            width: '300px',
            height: '100vh',
            backgroundColor: 'white',
            boxShadow: '-2px 0 5px rgba(0, 0, 0, 0.3)',
            padding: '20px',
            zIndex: 1000,
            overflowY: 'auto', // Allows scrolling if the content exceeds the available height
          }}
        >
          <button
            onClick={closeSidebar}
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              backgroundColor: 'red',
              color: 'white',
              border: 'none',
              padding: '5px',
              cursor: 'pointer',
            }}
          >
            X
          </button>
          <h2>{selectedCompany.companyName}</h2>
          <p><strong>Industry:</strong> {selectedCompany.industry}</p>
          <p><strong>Size:</strong> {selectedCompany.companySize}</p>
          <p><strong>Email:</strong> {selectedCompany.companyEmail}</p>

          {/* Show document if it exists */}
          {selectedCompany.documentName && (
            <div>
              <h3>Legal Document</h3>
              {/* Document View */}
              <embed
                src={`data:application/pdf;base64,${selectedCompany.documentName}`}
                type="application/pdf"
                width="100%"
                height="400px"
              />
              {/* Download Link */}
              <button
                onClick={() => downloadDocument(selectedCompany.documentName)}
                style={{
                  padding: '10px',
                  backgroundColor: '#4CAF50',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  marginTop: '10px',
                  cursor: 'pointer',
                }}
              >
                Download Document
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
