import React, { useState, useEffect, useMemo } from 'react';

function ViewRegistration({ setNotificationMessage }) {
  const [companies, setCompanies] = useState([]);
  const [companyDetails, setCompanyDetails] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [industryOptions, setIndustryOptions] = useState([]);

  const loadPendingCompanies = () => {
    try {
      const all = JSON.parse(sessionStorage.getItem('companies')) || [];
      const pending = all.filter(c => !c.isAccepted);
      setCompanies(pending);
      const industries = [...new Set(pending.map(c => c.industry))].filter(Boolean);
      setIndustryOptions(industries);
    } catch (e) {
      console.error("Error loading companies:", e);
    }
  };

  useEffect(() => {
    loadPendingCompanies();
  }, []);

  const handleSelectCompany = (basicCompany) => {
    const dataKey = `companyData:${basicCompany.companyEmail}`;
    const fullData = JSON.parse(sessionStorage.getItem(dataKey)) || basicCompany;
    setCompanyDetails(fullData);
  };

  const handleAccept = () => {
    if (!companyDetails) return;
    const all = JSON.parse(sessionStorage.getItem('companies')) || [];
    const updated = all.map(c =>
      c.companyEmail === companyDetails.companyEmail
        ? { ...c, isAccepted: true, hasNotification: true }
        : c
    );
    sessionStorage.setItem('companies', JSON.stringify(updated));
    setNotificationMessage?.(`Registration for ${companyDetails.companyName} accepted.`);
    setCompanyDetails(null);
    loadPendingCompanies();
  };

  const handleReject = () => {
    if (!companyDetails) return;
    const all = JSON.parse(sessionStorage.getItem('companies')) || [];
    const updated = all.filter(c => c.companyEmail !== companyDetails.companyEmail);
    sessionStorage.setItem('companies', JSON.stringify(updated));
    sessionStorage.removeItem(`companyData:${companyDetails.companyEmail}`);
    setNotificationMessage?.(`Registration for ${companyDetails.companyName} rejected.`);
    setCompanyDetails(null);
    loadPendingCompanies();
  };

  const handleDownloadDocument = () => {
    const link = document.createElement('a');
    link.href = companyDetails.document?.dataUrl;
    link.download = companyDetails.document?.name;
    link.click();
  };

  const filteredCompanies = useMemo(() => {
    return companies.filter(c =>
      c.companyName.toLowerCase().includes(searchQuery.toLowerCase()) &&
      (selectedIndustry ? c.industry === selectedIndustry : true)
    );
  }, [companies, searchQuery, selectedIndustry]);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Pending Company Registrations</h2>

      <div style={{ marginBottom: '10px' }}>
        <input
          type="text"
          placeholder="Search by company name"
          value={searchQuery}
          onChange={e => setSearchQuery(e.target.value)}
          style={{ padding: '5px', width: '200px', marginRight: '10px' }}
        />
        <select
          value={selectedIndustry}
          onChange={e => setSelectedIndustry(e.target.value)}
        >
          <option value="">All Industries</option>
          {industryOptions.map((industry, idx) => (
            <option key={idx} value={industry}>{industry}</option>
          ))}
        </select>
      </div>

      <table border="1" cellPadding="10" cellSpacing="0" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>Logo</th>
            <th>Company Name</th>
            <th>Industry</th>
            <th>Email</th>
            <th>Size</th>
            <th>Details</th>
          </tr>
        </thead>
        <tbody>
          {filteredCompanies.length > 0 ? (
            filteredCompanies.map(company => (
              <tr key={company.companyEmail}>
                <td>
                  {(() => {
                    const full = JSON.parse(sessionStorage.getItem(`companyData:${company.companyEmail}`));
                    return full?.companyLogo ? (
                      <img
                        src={full.companyLogo}
                        alt="Logo"
                        style={{ width: '50px', height: '50px', objectFit: 'cover', borderRadius: '50%' }}
                      />
                    ) : 'No Logo';
                  })()}
                </td>
                <td>{company.companyName}</td>
                <td>{company.industry}</td>
                <td>{company.companyEmail}</td>
                <td>{company.companySize}</td>
                <td>
                  <button onClick={() => handleSelectCompany(company)}>
                    View
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr><td colSpan="6">No pending registrations found.</td></tr>
          )}
        </tbody>
      </table>

      {companyDetails && (
        <div style={{
          marginTop: '20px', padding: '20px', border: '1px solid #ccc', borderRadius: '8px'
        }}>
          <h3>{companyDetails.companyName}</h3>
          {companyDetails.companyLogo && (
            <img
              src={companyDetails.companyLogo}
              alt="Company Logo"
              style={{ width: '100px', height: '100px', borderRadius: '50%', objectFit: 'cover' }}
            />
          )}
          <p><strong>Industry:</strong> {companyDetails.industry}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail}</p>
          <p><strong>Size:</strong> {companyDetails.companySize}</p>

          {companyDetails.document ? (
            <button onClick={handleDownloadDocument}>
              Download Document
            </button>
          ) : <p>No document uploaded</p>}

          <div style={{ marginTop: '10px' }}>
            <button onClick={handleAccept} style={{ marginRight: '10px' }}>
              Accept
            </button>
            <button onClick={handleReject}>
              Reject
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
