import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function CompanyPage() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(prev => !prev);
  };

  return (
    <div style={{ display: 'flex' }}>
      {/* Sidebar */}
      <div
        style={{
          width: menuOpen ? '250px' : '0',
          height: '100vh',
          backgroundColor: '#f8f9fa',
          overflowX: 'hidden',
          transition: '0.3s',
          padding: menuOpen ? '20px' : '0',
          boxShadow: menuOpen ? '2px 0 5px rgba(0,0,0,0.2)' : 'none',
          position: 'fixed',
          top: 0,
          left: 0,
          zIndex: 1000,
        }}
      >
        {menuOpen && (
          <ul style={{ listStyleType: 'none', padding: 0, marginTop: '50px' }}>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/dashboard">Dashboard</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/profile">Profile</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/post-job">Post a Job</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/applications">View Applications</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/interns">Your Interns</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/company/settings">Settings</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to="/">Logout</Link>
            </li>
          </ul>
        )}
      </div>

      {/* Main content */}
      <div
        style={{
          marginLeft: menuOpen ? '250px' : '0',
          transition: 'margin-left 0.3s',
          padding: '20px',
          width: '100%',
        }}
      >
        <button
          onClick={toggleMenu}
          style={{
            fontSize: '28px',
            fontWeight: 'bold',
            background: 'transparent',
            border: 'none',
            cursor: 'pointer',
            zIndex: 1101,
            position: 'absolute',
            top: '20px',
            left: '20px',
          }}
        >
          â˜°
        </button>

        <h1>Company Dashboard</h1>
        <p>Welcome to your company portal. Use the menu to navigate between sections.</p>
      </div>
    </div>
  );
}

export default CompanyPage;
