import React, { useState, useEffect } from 'react';

function ViewRegistration({ setNotificationMessage }) { // setNotificationMessage is for admin UI feedback
  const [companyDetails, setCompanyDetails] = useState(null);
  const [companies, setCompanies] = useState([]); // This will store PENDING companies for display
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [industryOptions, setIndustryOptions] = useState([]);

  // Function to load/reload pending companies from localStorage
  const loadPendingCompanies = () => {
    const allCompanies = JSON.parse(localStorage.getItem('companies')) || [];
    // Filter for companies that are not yet accepted
    // A company is pending if 'isAccepted' is false or not present.
    const pendingCompanies = allCompanies.filter(company => !company.isAccepted);
    setCompanies(pendingCompanies);

    // Extract unique industries from the PENDING companies list
    const industries = [...new Set(pendingCompanies.map(company => company.industry))].filter(Boolean);
    setIndustryOptions(industries);
  };

  useEffect(() => {
    loadPendingCompanies(); // Load pending companies on initial mount

    // Add storage event listener to keep admin view in sync if localStorage changes
    const handleStorageChange = (event) => {
      if (event.key === 'companies') {
        loadPendingCompanies();
      }
    };
    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []); // Empty dependency array ensures this runs once on mount and cleans up on unmount

  const handleCompanyClick = (company) => {
    setCompanyDetails(company);
  };

  const handleCloseSidebar = () => {
    setCompanyDetails(null);
  };

  const handleAccept = () => {
    if (!companyDetails) return;

    // Get the full list of companies from localStorage
    const allCompanies = JSON.parse(localStorage.getItem('companies')) || [];

    // Find and update the accepted company
    const updatedCompaniesList = allCompanies.map(company =>
      company.companyEmail === companyDetails.companyEmail
        ? { ...company, isAccepted: true, hasNotification: true } // Mark as accepted and set notification flag
        : company
    );

    // Save the updated full list back to localStorage
    localStorage.setItem('companies', JSON.stringify(updatedCompaniesList));

    // Provide feedback to the admin (optional, via prop)
    if (setNotificationMessage) {
      setNotificationMessage(`Registration for ${companyDetails.companyName} has been accepted.`);
    }

    // Refresh the list of pending companies in this view
    loadPendingCompanies();
    setCompanyDetails(null); // Close the sidebar
  };

  const handleReject = () => {
    if (!companyDetails) return;

    // Get the full list of companies from localStorage
    const allCompanies = JSON.parse(localStorage.getItem('companies')) || [];

    // Remove the rejected company from the list
    const updatedCompaniesList = allCompanies.filter(
      company => company.companyEmail !== companyDetails.companyEmail
    );

    // Save the updated list (without the rejected company) back to localStorage
    localStorage.setItem('companies', JSON.stringify(updatedCompaniesList));

    // Provide feedback to the admin (optional, via prop)
    if (setNotificationMessage) {
      setNotificationMessage(`Registration for ${companyDetails.companyName} has been rejected and removed.`);
    }

    // Refresh the list of pending companies in this view
    loadPendingCompanies();
    setCompanyDetails(null); // Close the sidebar
  };

  // Filter companies (already pending) by search query and industry
  const filteredCompanies = companies.filter(company => {
    const matchesName = company.companyName.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesIndustry = selectedIndustry ? company.industry === selectedIndustry : true;
    return matchesName && matchesIndustry;
  });

  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>Company Registration View (Pending)</h1>

      {/* Search Bar */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by company name"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{
            padding: '8px',
            fontSize: '16px',
            marginBottom: '10px',
            width: '300px',
          }}
        />
      </div>

      {/* Industry Filter with Dropdown */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <button
          onClick={() => setSelectedIndustry('')}
          style={{
            padding: '8px 16px',
            marginRight: '10px',
            backgroundColor: selectedIndustry === '' ? '#007bff' : '#f8f9fa', // Highlight if active
            color: selectedIndustry === '' ? 'white' : 'black',
            border: '1px solid #ddd',
            cursor: 'pointer',
            fontSize: '16px',
          }}
        >
          All Industries
        </button>

        <select
          value={selectedIndustry}
          onChange={(e) => setSelectedIndustry(e.target.value)}
          style={{
            padding: '8px 16px',
            backgroundColor: '#f8f9fa',
            border: '1px solid #ddd',
            cursor: 'pointer',
            fontSize: '16px',
          }}
        >
          <option value="">Select Industry</option>
          {industryOptions.map((industry, index) => (
            <option key={index} value={industry}>
              {industry}
            </option>
          ))}
        </select>
      </div>

      {/* List of Companies */}
      <div style={{ margin: '20px', textAlign: 'center' }}>
        <h2>Pending Registrations</h2>
        <div>
          {filteredCompanies.length > 0 ? (
            filteredCompanies.map((company) => ( // Removed index as key if companyEmail is unique
              <p key={company.companyEmail} style={{ marginBottom: '10px' }}>
                <button
                  onClick={() => handleCompanyClick(company)}
                  style={{
                    fontSize: '18px',
                    color: 'blue',
                    background: 'none',
                    border: 'none',
                    textDecoration: 'underline',
                    cursor: 'pointer',
                    padding: '0',
                  }}
                >
                  {company.companyLogo && (
                    <img
                      src={company.companyLogo}
                      alt={`${company.companyName} logo`}
                      style={{
                        width: '30px',
                        height: '30px',
                        marginRight: '10px',
                        verticalAlign: 'middle',
                        borderRadius: '50%',
                      }}
                    />
                  )}
                  {company.companyName}
                </button>
              </p>
            ))
          ) : (
            <p>No pending company registrations found.</p>
          )}
        </div>
      </div>

      {/* Sidebar showing company details */}
      {companyDetails && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            right: 0,
            width: '350px',
            height: '100vh',
            backgroundColor: '#f8f9fa',
            boxShadow: '-2px 0 5px rgba(0,0,0,0.2)',
            padding: '20px',
            zIndex: 1000,
            overflowY: 'auto',
          }}
        >
          <button
            onClick={handleCloseSidebar}
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              padding: '5px 10px',
              backgroundColor: 'grey', // Changed color for better visibility
              color: 'white',
              border: 'none',
              cursor: 'pointer',
              borderRadius: '4px',
            }}
          >
            Close
          </button>
          <h2 style={{ textAlign: 'center' }}>Company Details</h2>

          <div style={{ textAlign: 'center', marginBottom: '15px' }}>
            {companyDetails.companyLogo ? (
              <img
                src={companyDetails.companyLogo}
                alt={`${companyDetails.companyName} logo`}
                style={{
                  width: '150px',
                  height: '150px',
                  marginBottom: '10px',
                  borderRadius: '50%',
                }}
              />
            ) : (
              <p>No logo uploaded</p>
            )}
            <h3>{companyDetails.companyName}</h3>
          </div>

          <p><strong>Industry:</strong> {companyDetails.industry}</p>
          <p><strong>Company Size:</strong> {companyDetails.companySize}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail}</p>

          <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '20px' }}>
            <button
              onClick={handleAccept}
              style={{
                padding: '10px 20px',
                backgroundColor: '#4CAF50', // Green for accept
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
                flex: 1,
                marginRight: '5px',
              }}
            >
              Accept
            </button>
            <button
              onClick={handleReject}
              style={{
                padding: '10px 20px',
                backgroundColor: '#f44336', // Red for reject
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
                flex: 1,
                marginLeft: '5px',
              }}
            >
              Reject
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
