import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyRegister(props) {
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [companyLogo, setCompanyLogo] = useState(null);
  const [companyEmail, setCompanyEmail] = useState('');
  const [document, setDocument] = useState(null);
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});

  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleRegister = (e) => {
    e.preventDefault();

    const newErrors = {};

    if (!companyName) newErrors.companyName = 'Company name is required';
    if (!industry) newErrors.industry = 'Industry is required';
    if (!companySize) newErrors.companySize = 'Company size is required';
    if (!companyLogo) newErrors.companyLogo = 'Company logo is required';
    if (!companyEmail) {
      newErrors.companyEmail = 'Email is required';
    } else if (!emailRegex.test(companyEmail)) {
      newErrors.companyEmail = 'Invalid email format';
    }
    if (!document) newErrors.document = 'Document is required';
    if (!password) newErrors.password = 'Password is required';

    setErrors(newErrors);

    if (Object.keys(newErrors).length > 0) return;

    // Store the company data in localStorage
    const companyData = {
      companyName,
      industry,
      companySize,
      companyLogoName: companyLogo ? companyLogo.name : '',
      companyEmail,
      password,
      documentName: document ? document.name : '',
    };

    const existingCompanies = JSON.parse(localStorage.getItem('companies')) || [];
    existingCompanies.push(companyData);
    localStorage.setItem('companies', JSON.stringify(existingCompanies));

    setIsModalOpen(true);
  };

  const handleLogoChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setCompanyLogo(e.target.files[0]);
    } else {
      setCompanyLogo(null);
    }
  };

  const handleDocumentChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setDocument(e.target.files[0]);
    } else {
      setDocument(null);
    }
  };

  const handleModalOkClick = () => {
    setIsModalOpen(false);
    navigate('/company-login');
  };

  const inputStyle = (error) => ({
    width: '100%',
    padding: '8px',
    marginBottom: '5px',
    border: error ? '1px solid red' : '1px solid #ddd',
    boxSizing: 'border-box',
  });

  const errorText = (msg) => (
    <div style={{ color: 'red', fontSize: '12px', marginBottom: '10px' }}>{msg}</div>
  );

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh', padding: '20px 0' }}>
      <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px', width: '400px', maxWidth: '100%' }}>
        <h2>Company Register</h2>
        <form onSubmit={handleRegister} noValidate>
          <div>
            <label htmlFor="companyName">Company Name:</label>
            <input
              id="companyName"
              type="text"
              value={companyName}
              onChange={(e) => setCompanyName(e.target.value)}
              style={inputStyle(errors.companyName)}
            />
            {errors.companyName && errorText(errors.companyName)}
          </div>
          <div>
            <label htmlFor="industry">Industry:</label>
            <input
              id="industry"
              type="text"
              value={industry}
              onChange={(e) => setIndustry(e.target.value)}
              style={inputStyle(errors.industry)}
            />
            {errors.industry && errorText(errors.industry)}
          </div>
          <div>
            <label htmlFor="companySize">Company Size:</label>
            <select
              id="companySize"
              value={companySize}
              onChange={(e) => setCompanySize(e.target.value)}
              style={inputStyle(errors.companySize)}
            >
              <option value="">Select company size</option>
              <option value="small">Small (â‰¤50 employees)</option>
              <option value="medium">Medium (51-100 employees)</option>
              <option value="large">Large (101-500 employees)</option>
              <option value="corporate">Corporate (500+ employees)</option>
            </select>
            {errors.companySize && errorText(errors.companySize)}
          </div>
          <div>
            <label htmlFor="companyLogo">Company Logo:</label>
            <input
              id="companyLogo"
              type="file"
              accept="image/*"
              onChange={handleLogoChange}
              style={inputStyle(errors.companyLogo)}
            />
            {errors.companyLogo && errorText(errors.companyLogo)}
          </div>
          <div>
            <label htmlFor="companyEmail">Official Company Email:</label>
            <input
              id="companyEmail"
              type="email"
              value={companyEmail}
              onChange={(e) => setCompanyEmail(e.target.value)}
              style={inputStyle(errors.companyEmail)}
            />
            {errors.companyEmail && errorText(errors.companyEmail)}
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle(errors.password)}
            />
            {errors.password && errorText(errors.password)}
          </div>
          <div>
            <label htmlFor="document">Upload Documents (e.g., tax/legal):</label>
            <input
              id="document"
              type="file"
              accept=".pdf,.doc,.docx,.txt"
              onChange={handleDocumentChange}
              style={inputStyle(errors.document)}
            />
            {errors.document && errorText(errors.document)}
          </div>
          <button
            type="submit"
            style={{
              padding: '10px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              marginTop: '10px',
              width: '100%',
              cursor: 'pointer',
            }}
          >
            Register
          </button>
        </form>
      </div>

      <button
        onClick={() => navigate('/')}
        style={{
          position: 'fixed',
          bottom: '20px',
          left: '20px',
          padding: '10px',
          backgroundColor: '#f44336',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
        }}
      >
        Back
      </button>

      {isModalOpen && (
        <div style={{
          position: 'fixed',
          zIndex: 1000,
          top: 0, left: 0,
          width: '100%', height: '100%',
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          display: 'flex', justifyContent: 'center', alignItems: 'center'
        }}>
          <div style={{
            backgroundColor: 'white', padding: '20px',
            borderRadius: '8px', textAlign: 'center'
          }}>
            <h2>Registration Successful!</h2>
            <p>Your company has been successfully registered. Please proceed to the login page.</p>
            <button
              onClick={handleModalOkClick}
              style={{
                padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none',
                borderRadius: '4px', cursor: 'pointer'
              }}
            >
              OK
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default CompanyRegister;
