import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function ViewRegistration() {
  const [companies, setCompanies] = useState([]);
  const [companyDetails, setCompanyDetails] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedCompanies = JSON.parse(localStorage.getItem('companies')) || [];
    setCompanies(storedCompanies);
  }, []);

  const handleCompanyClick = (company) => {
    setCompanyDetails(company);
  };

  const handleCloseSidebar = () => {
    setCompanyDetails(null);
  };

  const handleBackClick = () => {
    navigate('/');
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Company Registrations</h2>
      {companies.length === 0 ? (
        <p>No companies registered yet.</p>
      ) : (
        <div>
          <h3>Click on a company name to view details:</h3>
          <div>
            {companies.map((company, index) => (
              <p key={index}>
                <a
                  href="#"
                  onClick={() => handleCompanyClick(company)}
                  style={{
                    fontSize: '18px',
                    color: 'blue',
                    textDecoration: 'underline',
                    cursor: 'pointer',
                  }}
                >
                  {company.companyName}
                </a>
              </p>
            ))}
          </div>
        </div>
      )}

      {/* Sidebar showing company details */}
      {companyDetails && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            right: 0,
            width: '300px',
            height: '100vh',
            backgroundColor: '#f8f9fa',
            boxShadow: '-2px 0 5px rgba(0,0,0,0.2)',
            padding: '20px',
            zIndex: 1000,
          }}
        >
          <button
            onClick={handleCloseSidebar}
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              padding: '5px 10px',
              backgroundColor: 'red',
              color: 'white',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            Close
          </button>
          <h3>Company Details</h3>
          <img
            src={companyDetails.companyLogo ? companyDetails.companyLogo : '/placeholder-logo.png'}
            alt={`${companyDetails.companyName} logo`}
            style={{ width: '100%', height: 'auto', marginBottom: '15px' }}
          />
          <p><strong>Industry:</strong> {companyDetails.industry}</p>
          <p><strong>Company Size:</strong> {companyDetails.companySize}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail}</p>
          <a
            href={`/${companyDetails.documentName}`}
            download
            style={{
              display: 'block',
              marginTop: '15px',
              textDecoration: 'none',
              color: 'green',
            }}
          >
            <button
              style={{
                padding: '10px 20px',
                backgroundColor: '#4CAF50',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              Download Legal Document
            </button>
          </a>
        </div>
      )}

      <button
        onClick={handleBackClick}
        style={{
          marginTop: '20px',
          padding: '10px',
          backgroundColor: '#f44336',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
        }}
      >
        Back
      </button>
    </div>
  );
}

export default ViewRegistration;
