import React, { useState } from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import CompanyRegister from './CompanyRegister';
import CompanyLogin from './companylogin';
import StudentPage from './studentpage';
import SCADPage from './scadpage';
import CompanyPage from './companypage';
import FacultyPage from './facultypage';
import ViewRegistration from './viewregistration';

function App() {
  // Manage notification state
  const [notificationMessage, setNotificationMessage] = useState('');

  // Dummy users for login
  const dummyUsers = [
    {
      role: 'student',
      email: 'student@example.com',
      password: 'student123',
    },
    {
      role: 'scad',
      email: 'scad@example.com',
      password: 'scad123',
    },
    {
      role: 'faculty',
      email: 'faculty@example.com',
      password: 'faculty123',
    },
  ];

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const navigate = useNavigate();

  // Handle form submission for login
  const handleLogin = (e) => {
    e.preventDefault();
    let valid = true;

    // Reset previous errors
    setEmailError('');
    setPasswordError('');

    // Trim any spaces from inputs
    const trimmedEmail = email.trim();
    const trimmedPassword = password.trim();

    // First check if email exists in dummy users (for student, scad, faculty)
    const user = dummyUsers.find((u) => u.email === trimmedEmail);

    // Check if the user is a registered company (using localStorage)
    const companies = JSON.parse(localStorage.getItem('companies')) || [];
    const companyUser = companies.find((c) => c.companyEmail === trimmedEmail);

    if (!user && !companyUser) {
      valid = false;
      setEmailError('Invalid email');
    } else if (user && user.password !== trimmedPassword) {
      valid = false;
      setPasswordError('Wrong password');
    } else if (companyUser && companyUser.password !== trimmedPassword) {
      valid = false;
      setPasswordError('Wrong password');
    } else if (user) {
      // If a student, scad, or faculty user, navigate to the respective page based on the role
      if (user.role === 'student') {
        navigate('/studentpage');
      } else if (user.role === 'scad') {
        navigate('/scadpage');
      } else if (user.role === 'faculty') {
        navigate('/facultypage');
      }
    } else if (companyUser) {
      // If the user is a company, navigate to the company page
      navigate('/company-dashboard');
    }

    return valid;
  };

  const handleCompanyRegister = () => {
    navigate('/company-register');
  };

  return (
    <div>
      <Routes>
        {/* Login Page */}
        <Route
          path="/"
          element={
            <div
              style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '100vh',
                backgroundColor: '#d9e4ec', // Blue Gray background
              }}
            >
              <div
                style={{
                  padding: '20px',
                  border: '1px solid #b7cfdc',
                  borderRadius: '8px',
                  backgroundColor: '#b7cfdc', // Misty Blue background for form
                  width: '400px',
                }}
              >
                <h2 style={{ textAlign: 'center', color: '#385e72' }}>Login</h2>
                <form onSubmit={handleLogin}>
                  <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="email" style={{ fontWeight: 'bold', color: '#385e72' }}>Email:</label>
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                      placeholder="Enter your email"
                      style={{
                        width: '100%',
                        padding: '8px',
                        marginBottom: '10px',
                        borderColor: emailError ? '#ff4d4d' : '#b7cfdc',
                        borderRadius: '4px',
                      }}
                    />
                    {emailError && <div style={{ color: '#ff4d4d', fontSize: '12px' }}>{emailError}</div>}
                  </div>
                  <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="password" style={{ fontWeight: 'bold', color: '#385e72' }}>Password:</label>
                    <input
                      type="password"
                      id="password"
                      name="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                      placeholder="Enter your password"
                      style={{
                        width: '100%',
                        padding: '8px',
                        marginBottom: '10px',
                        borderColor: passwordError ? '#ff4d4d' : '#b7cfdc',
                        borderRadius: '4px',
                      }}
                    />
                    {passwordError && <div style={{ color: '#ff4d4d', fontSize: '12px' }}>{passwordError}</div>}
                  </div>
                  <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                    <button
                      type="submit"
                      style={{
                        padding: '10px',
                        backgroundColor: '#6aabd2', // Blue Grotto
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                      }}
                    >
                      Login
                    </button>
                    <button
                      type="button"
                      onClick={handleCompanyRegister}
                      style={{
                        padding: '10px',
                        backgroundColor: '#385e72', // Dark Misty Blue
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                      }}
                    >
                      Register as a Company
                    </button>
                  </div>
                </form>
              </div>
            </div>
          }
        />

        {/* Other Pages */}
        <Route path="/company-register" element={<CompanyRegister />} />
        <Route path="/view-registration" element={<ViewRegistration setNotificationMessage={setNotificationMessage} />} />
        <Route path="/company-login" element={<CompanyLogin />} />
        <Route path="/studentpage" element={<StudentPage />} />
        <Route path="/scadpage" element={<SCADPage />} />
        <Route path="/company-dashboard" element={<CompanyPage />} />
        <Route path="/facultypage" element={<FacultyPage />} />
      </Routes>

      {/* Display Notification Message */}
      {notificationMessage && (
        <div
          style={{
            position: 'fixed',
            top: '10px',
            left: '50%',
            transform: 'translateX(-50%)',
            backgroundColor: '#6aabd2', // Blue Grotto
            color: 'white',
            padding: '10px',
            borderRadius: '4px',
          }}
        >
          {notificationMessage}
        </div>
      )}
    </div>
  );
}

export default App;
