import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';

function CompanyPage() {
  const { companyId } = useParams();
  const [menuOpen, setMenuOpen] = useState(false);
  const [companyName, setCompanyName] = useState('');
  const [isJobModalOpen, setIsJobModalOpen] = useState(false);
  const [editingIndex, setEditingIndex] = useState(null);
  const [jobData, setJobData] = useState({
    title: '',
    duration: '',
    isPaid: false,
    salary: '',
    skills: '',
    description: '',
  });
  const [postedJobs, setPostedJobs] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterPaid, setFilterPaid] = useState({ paid: false, unpaid: false });
  const [filterDuration, setFilterDuration] = useState({
    '1 month': false,
    '2 months': false,
    '3 months': false,
  });

  const navigate = useNavigate();

  useEffect(() => {
    if (!companyId) {
      navigate('/');
      return;
    }

    const allCompanies = JSON.parse(localStorage.getItem('registeredCompanies')) || [];
    const current = allCompanies.find(c => c.companyEmail === companyId);

    if (current) {
      setCompanyName(current.companyName || current.companyEmail);
      const allJobs = JSON.parse(localStorage.getItem('jobsByCompany')) || {};
      setPostedJobs(allJobs[companyId] || []);
    } else {
      navigate('/company-login');
    }
  }, [companyId, navigate]);

  const toggleMenu = () => setMenuOpen(prev => !prev);

  const handleLogout = () => {
    localStorage.removeItem('currentCompany');
    navigate('/');
  };

  const handleJobModalToggle = () => {
    setIsJobModalOpen(prev => !prev);
    setEditingIndex(null);
    setJobData({
      title: '',
      duration: '',
      isPaid: false,
      salary: '',
      skills: '',
      description: '',
    });
  };

  const handleJobInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setJobData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const saveJobsToStorage = (jobs) => {
    if (!companyId) return;
    const jobsByCompany = JSON.parse(localStorage.getItem('jobsByCompany')) || {};
    jobsByCompany[companyId] = jobs;
    localStorage.setItem('jobsByCompany', JSON.stringify(jobsByCompany));
  };

  const handleJobSubmit = (e) => {
    e.preventDefault();
    let updatedJobs;

    if (editingIndex !== null) {
      updatedJobs = [...postedJobs];
      updatedJobs[editingIndex] = jobData;
    } else {
      updatedJobs = [...postedJobs, jobData];
    }

    setPostedJobs(updatedJobs);
    saveJobsToStorage(updatedJobs);
    setEditingIndex(null);
    setIsJobModalOpen(false);

    setJobData({
      title: '',
      duration: '',
      isPaid: false,
      salary: '',
      skills: '',
      description: '',
    });
  };

  const handleEditJob = (index) => {
    setEditingIndex(index);
    setJobData(postedJobs[index]);
    setIsJobModalOpen(true);
  };

  const handleDeleteJob = (index) => {
    const updatedJobs = postedJobs.filter((_, i) => i !== index);
    setPostedJobs(updatedJobs);
    saveJobsToStorage(updatedJobs);
  };

  const filteredJobs = postedJobs.filter(job => {
    const matchesSearchQuery =
      job.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      job.skills.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesPaid =
      (!filterPaid.paid && !filterPaid.unpaid) ||
      (filterPaid.paid && job.isPaid) ||
      (filterPaid.unpaid && !job.isPaid);

    const selectedDurations = Object.entries(filterDuration)
      .filter(([_, checked]) => checked)
      .map(([duration]) => duration);

    const matchesDuration =
      selectedDurations.length === 0 || selectedDurations.includes(job.duration);

    return matchesSearchQuery && matchesPaid && matchesDuration;
  });

  return (
    <div style={{ display: 'flex' }}>
      {/* Sidebar */}
      <div
        style={{
          width: menuOpen ? '250px' : '0',
          height: '100vh',
          backgroundColor: '#f8f9fa',
          overflowX: 'hidden',
          transition: '0.3s',
          padding: menuOpen ? '20px' : '0',
          boxShadow: menuOpen ? '2px 0 5px rgba(0,0,0,0.2)' : 'none',
          position: 'fixed',
          top: 0,
          left: 0,
          zIndex: 1000,
        }}
      >
        {menuOpen && (
          <ul style={{ listStyleType: 'none', padding: 0, marginTop: '50px' }}>
            <li style={{ margin: '15px 0' }}>
              <Link to={`/company/${companyId}`}>Dashboard</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to={`/company/${companyId}/profile`}>Profile</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <button
                onClick={handleJobModalToggle}
                style={{
                  background: 'none',
                  border: 'none',
                  padding: 0,
                  color: '#007bff',
                  textDecoration: 'underline',
                  cursor: 'pointer',
                  font: 'inherit',
                }}
              >
                Post a Job
              </button>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to={`/company/${companyId}/applications`}>View Applications</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to={`/company/${companyId}/interns`}>Your Interns</Link>
            </li>
            <li style={{ margin: '15px 0' }}>
              <Link to={`/company/${companyId}/settings`}>Settings</Link>
            </li>
            <li style={{ margin: '15px 0', cursor: 'pointer' }} onClick={handleLogout}>
              Logout
            </li>
          </ul>
        )}
      </div>

      {/* Main Content */}
      <div
        style={{
          marginLeft: menuOpen ? '250px' : '0',
          transition: 'margin-left 0.3s',
          padding: '20px',
          width: '100%',
        }}
      >
        <button
          onClick={toggleMenu}
          style={{
            fontSize: '28px',
            fontWeight: 'bold',
            background: 'transparent',
            border: 'none',
            cursor: 'pointer',
            zIndex: 1101,
            position: 'absolute',
            top: '20px',
            left: '20px',
          }}
        >
          ☰
        </button>

        <h1>Welcome, {companyName}</h1>
        <p>This is your company dashboard. Use the menu to navigate between sections.</p>

        {/* Filter Section */}
        <div style={{ marginBottom: '20px' }}>
          <input
            type="text"
            placeholder="Search By Job Title..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            style={{
              padding: '10px',
              width: '100%',
              marginBottom: '10px',
              border: '1px solid #ddd',
              borderRadius: '4px',
            }}
          />

          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '15px', marginBottom: '10px' }}>
            <label>
              <input
                type="checkbox"
                checked={filterPaid.paid}
                onChange={(e) =>
                  setFilterPaid(prev => ({ ...prev, paid: e.target.checked }))
                }
              />{' '}
              Paid
            </label>

            <label>
              <input
                type="checkbox"
                checked={filterPaid.unpaid}
                onChange={(e) =>
                  setFilterPaid(prev => ({ ...prev, unpaid: e.target.checked }))
                }
              />{' '}
              Unpaid
            </label>

            {['1 month', '2 months', '3 months'].map(duration => (
              <label key={duration}>
                <input
                  type="checkbox"
                  checked={filterDuration[duration]}
                  onChange={(e) =>
                    setFilterDuration(prev => ({
                      ...prev,
                      [duration]: e.target.checked,
                    }))
                  }
                />{' '}
                {duration}
              </label>
            ))}
          </div>
        </div>

        {/* Posted Jobs */}
        <div>
          <h2>Posted Jobs</h2>
          <ul>
            {filteredJobs.map((job, index) => (
              <li key={index} style={{ marginBottom: '20px' }}>
                <p><strong>Job Title:</strong> {job.title}</p>
                <p><strong>Duration:</strong> {job.duration}</p>
                <p><strong>Paid:</strong> {job.isPaid ? 'Yes' : 'No'}</p>
                {job.isPaid && <p><strong>Salary:</strong> {job.salary}</p>}
                <p><strong>Skills:</strong> {job.skills}</p>
                <p><strong>Description:</strong> {job.description}</p>
                <button onClick={() => handleEditJob(index)} style={{ marginRight: '10px' }}>
                  Edit
                </button>
                <button onClick={() => handleDeleteJob(index)} style={{ color: 'red' }}>
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Job Modal */}
        {isJobModalOpen && (
          <div
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              backgroundColor: 'rgba(0,0,0,0.5)',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <div
              style={{
                backgroundColor: '#fff',
                padding: '20px',
                borderRadius: '8px',
                width: '400px',
              }}
            >
              <h3>{editingIndex !== null ? 'Edit Job' : 'Post a Job'}</h3>
              <form onSubmit={handleJobSubmit}>
                <div>
                  <label>Job Title:</label>
                  <input
                    type="text"
                    name="title"
                    value={jobData.title}
                    onChange={handleJobInputChange}
                    required
                    style={{ width: '100%', marginBottom: '10px' }}
                  />
                </div>
                <div>
                  <label>Duration:</label>
                  <input
                    type="text"
                    name="duration"
                    value={jobData.duration}
                    onChange={handleJobInputChange}
                    required
                    style={{ width: '100%', marginBottom: '10px' }}
                  />
                </div>
                <div>
                  <label>
                    Paid:
                    <input
                      type="checkbox"
                      name="isPaid"
                      checked={jobData.isPaid}
                      onChange={handleJobInputChange}
                      style={{ marginLeft: '10px' }}
                    />
                  </label>
                </div>
                {jobData.isPaid && (
                  <div>
                    <label>Salary:</label>
                    <input
                      type="number"
                      name="salary"
                      value={jobData.salary}
                      onChange={handleJobInputChange}
                      required
                      style={{ width: '100%', marginBottom: '10px' }}
                    />
                  </div>
                )}
                <div>
                  <label>Skills Required:</label>
                  <input
                    type="text"
                    name="skills"
                    value={jobData.skills}
                    onChange={handleJobInputChange}
                    required
                    style={{ width: '100%', marginBottom: '10px' }}
                  />
                </div>
                <div>
                  <label>Job Description:</label>
                  <textarea
                    name="description"
                    value={jobData.description}
                    onChange={handleJobInputChange}
                    required
                    style={{ width: '100%', marginBottom: '10px' }}
                  />
                </div>
                <button
                  type="submit"
                  style={{ backgroundColor: '#4CAF50', color: '#fff', width: '100%', padding: '10px', border: 'none' }}
                >
                  {editingIndex !== null ? 'Update Job' : 'Post Job'}
                </button>
              </form>
              <button
                onClick={handleJobModalToggle}
                style={{ backgroundColor: '#f44336', color: '#fff', width: '100%', marginTop: '10px', padding: '10px', border: 'none' }}
              >
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CompanyPage;
