import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [notificationMessage, setNotificationMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [hasNotification, setHasNotification] = useState(false);

  const navigate = useNavigate();

  const validateForm = () => {
    const newErrors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!email) newErrors.email = 'Email is required';
    else if (!emailRegex.test(email)) newErrors.email = 'Invalid email format';

    if (!password) newErrors.password = 'Password is required';

    return newErrors;
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    setErrors(validationErrors);
    if (Object.keys(validationErrors).length > 0) return;

    const companies = JSON.parse(localStorage.getItem('companies')) || [];
    const company = companies.find(c => c.companyEmail === email);

    if (!company) {
      setErrors({ form: 'Invalid email' });
    } else if (company.password !== password) {
      setErrors({ form: 'Wrong password' });
    } else if (!company.isAccepted) {
      setErrors({ form: 'You are not accepted yet!' });
    } else {
      if (company.hasNotification) {
        setHasNotification(true);
        setNotificationMessage('Your registration has been accepted!');

        const updatedCompanies = companies.map(c =>
          c.companyEmail === email ? { ...c, hasNotification: false } : c
        );
        localStorage.setItem('companies', JSON.stringify(updatedCompanies));
      }

      // If no notification, go straight to dashboard
      navigate('/company-dashboard');
    }
  };

  const inputStyle = (hasError) => ({
    border: hasError ? '1px solid red' : '1px solid #ccc',
    padding: '8px',
    width: '100%',
    boxSizing: 'border-box',
    marginBottom: '4px',
  });

  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  return (
    <div style={{ position: 'relative', minHeight: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      {/* Notification Bell */}
      <div onClick={togglePopup} style={{ position: 'absolute', top: 20, right: 20, cursor: 'pointer' }}>
        <img src="/bell.jpg" alt="Bell" style={{ width: 60, height: 60 }} />
        {hasNotification && (
          <div style={{
            position: 'absolute',
            top: 0,
            right: 0,
            width: 20,
            height: 20,
            backgroundColor: 'red',
            color: 'white',
            fontSize: 12,
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>1</div>
        )}
      </div>

      {showPopup && (
        <div style={{
          position: 'absolute',
          top: '70px',
          right: '20px',
          backgroundColor: 'white',
          border: '1px solid #ddd',
          padding: '10px',
          borderRadius: '8px',
          width: '220px'
        }}>
          <h4>Notifications</h4>
          <p>{notificationMessage || 'You have no notifications!'}</p>
          <button onClick={togglePopup}>Close</button>
        </div>
      )}

      <form onSubmit={handleLogin} style={{ border: '1px solid #ccc', padding: '20px', borderRadius: '8px', width: '300px' }}>
        <h2>Company Login</h2>

        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={inputStyle(errors.email || errors.form === 'Invalid email')}
        />
        {errors.email && <div style={{ color: 'red', fontSize: 12 }}>{errors.email}</div>}
        {errors.form === 'Invalid email' && <div style={{ color: 'red', fontSize: 12 }}>{errors.form}</div>}

        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={inputStyle(errors.password || errors.form === 'Wrong password')}
        />
        {errors.password && <div style={{ color: 'red', fontSize: 12 }}>{errors.password}</div>}
        {errors.form === 'Wrong password' && <div style={{ color: 'red', fontSize: 12 }}>{errors.form}</div>}

        {errors.form === 'You are not accepted yet!' && <div style={{ color: 'red', fontSize: 12 }}>{errors.form}</div>}

        <button type="submit" style={{ marginTop: '10px', width: '100%', padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none' }}>
          Login
        </button>
      </form>
    </div>
  );
}

export default CompanyLogin;
