import React, { useState, useEffect } from 'react';

function ViewRegistration() {
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);

  // Fetch companies from localStorage on component mount
  useEffect(() => {
    const storedCompanies = JSON.parse(localStorage.getItem('companies')) || [];
    setCompanies(storedCompanies);
  }, []);

  // Show the details of the selected company in the sidebar
  const handleCompanyClick = (company) => {
    setSelectedCompany(company);
    setSidebarOpen(true);
  };

  // Close the sidebar
  const closeSidebar = () => {
    setSidebarOpen(false);
    setSelectedCompany(null);
  };

  // Create a URL for the logo if it exists
  const getLogoURL = (logoName) => {
    return logoName ? URL.createObjectURL(new Blob([logoName])) : '/default-logo.png';
  };

  // Handle document preview and provide a download link
  const getDocumentPreview = (documentName) => {
    if (!documentName) return null;

    const fileExtension = documentName.split('.').pop().toLowerCase();

    if (fileExtension === 'pdf') {
      return (
        <div>
          <iframe
            src={URL.createObjectURL(new Blob([documentName]))}
            width="100%"
            height="400px"
            title="Document Preview"
          />
          <a
            href={URL.createObjectURL(new Blob([documentName]))}
            download
            style={{ display: 'block', marginTop: '10px', color: '#007bff', textDecoration: 'underline' }}
          >
            Download PDF
          </a>
        </div>
      );
    } else if (['doc', 'docx', 'txt'].includes(fileExtension)) {
      // For Word or text documents, provide a download link
      return (
        <a
          href={URL.createObjectURL(new Blob([documentName]))}
          download
          style={{ display: 'block', marginTop: '10px', color: '#007bff', textDecoration: 'underline' }}
        >
          Download Document
        </a>
      );
    }

    return <span>Unsupported document type</span>;
  };

  return (
    <div style={{ display: 'flex' }}>
      {/* Left side: List of companies, centered initially */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          flexDirection: 'column',
          padding: '20px',
          width: '100%',
          textAlign: 'center',
        }}
      >
        <h2>Companies</h2>
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {companies.map((company, index) => (
            <li key={index} style={{ marginBottom: '10px' }}>
              <button
                onClick={() => handleCompanyClick(company)}
                style={{
                  background: 'none',
                  border: 'none',
                  color: '#007bff',
                  textDecoration: 'underline',
                  cursor: 'pointer',
                }}
              >
                <img
                  src={getLogoURL(company.companyLogoName)}
                  alt={company.companyName}
                  style={{ width: '30px', height: '30px', marginRight: '10px' }}
                />
                {company.companyName}
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Sidebar: Display company details */}
      {sidebarOpen && selectedCompany && (
        <div
          style={{
            width: '400px',
            padding: '20px',
            position: 'fixed',
            top: 0,
            right: 0,
            bottom: 0,
            backgroundColor: '#f8f9fa',
            borderLeft: '1px solid #ddd',
            boxShadow: '-2px 0 5px rgba(0, 0, 0, 0.1)',
            zIndex: 1000,
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          <button
            onClick={closeSidebar}
            style={{
              backgroundColor: '#f44336',
              color: 'white',
              border: 'none',
              padding: '10px',
              borderRadius: '5px',
              alignSelf: 'flex-end',
              cursor: 'pointer',
              marginBottom: '20px',
            }}
          >
            Close
          </button>
          <h3>{selectedCompany.companyName}</h3>
          <p><strong>Industry:</strong> {selectedCompany.industry}</p>
          <p><strong>Size:</strong> {selectedCompany.companySize}</p>
          <p><strong>Email:</strong> {selectedCompany.companyEmail}</p>
          <p><strong>Logo:</strong></p>
          <img
            src={getLogoURL(selectedCompany.companyLogoName)}
            alt={selectedCompany.companyName}
            style={{ width: '100px', height: '100px' }}
          />
          <p><strong>Document:</strong></p>
          {getDocumentPreview(selectedCompany.documentName)}
        </div>
      )}

      {/* If no company is selected, show a message */}
      {!sidebarOpen && (
        <div style={{ padding: '20px', flex: 1 }}>
          <h3>Select a company to view details</h3>
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
