import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function ViewRegistration() {
  const [companies, setCompanies] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedCompanies = JSON.parse(localStorage.getItem('companies')) || [];
    setCompanies(storedCompanies);
  }, []);

  const handleBackClick = () => {
    navigate('/');
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h2>Company Registrations</h2>
      {companies.length === 0 ? (
        <p>No companies registered yet.</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
          <thead>
            <tr>
              <th style={{ padding: '8px', border: '1px solid #ddd' }}>Company Name</th>
              <th style={{ padding: '8px', border: '1px solid #ddd' }}>Industry</th>
              <th style={{ padding: '8px', border: '1px solid #ddd' }}>Company Size</th>
              <th style={{ padding: '8px', border: '1px solid #ddd' }}>Email</th>
              <th style={{ padding: '8px', border: '1px solid #ddd' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {companies.map((company, index) => (
              <tr key={index}>
                <td style={{ padding: '8px', border: '1px solid #ddd' }}>{company.companyName}</td>
                <td style={{ padding: '8px', border: '1px solid #ddd' }}>{company.industry}</td>
                <td style={{ padding: '8px', border: '1px solid #ddd' }}>{company.companySize}</td>
                <td style={{ padding: '8px', border: '1px solid #ddd' }}>{company.companyEmail}</td>
                <td style={{ padding: '8px', border: '1px solid #ddd' }}>
                  <button
                    onClick={() => alert(`Viewing details for: ${company.companyName}`)}
                    style={{
                      padding: '5px 10px',
                      backgroundColor: '#4CAF50',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer',
                    }}
                  >
                    View
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <button
        onClick={handleBackClick}
        style={{
          marginTop: '20px',
          padding: '10px',
          backgroundColor: '#f44336',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
        }}
      >
        Back
      </button>
    </div>
  );
}

export default ViewRegistration;
