import React, { useState, useEffect } from 'react';
import { FaFilter } from 'react-icons/fa';

function ViewRegistration() {
  const [companies, setCompanies] = useState([]);
  const [filteredCompanies, setFilteredCompanies] = useState([]);
  const [companyDetails, setCompanyDetails] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedIndustry, setSelectedIndustry] = useState('');

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('companies')) || [];
    setCompanies(stored);
    setFilteredCompanies(stored);
  }, []);

  useEffect(() => {
    let filtered = companies;

    if (searchTerm.trim()) {
      filtered = filtered.filter(company =>
        company.companyName.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (selectedIndustry) {
      filtered = filtered.filter(company => company.industry === selectedIndustry);
    }

    setFilteredCompanies(filtered);
  }, [searchTerm, selectedIndustry, companies]);

  const handleCompanyClick = (company) => {
    setCompanyDetails(company);
  };

  const handleCloseSidebar = () => {
    setCompanyDetails(null);
  };

  const industries = [...new Set(companies.map(c => c.industry).filter(Boolean))];

  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>Company Registration View</h1>

      {/* Search and Filter Bar */}
      <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', margin: '20px' }}>
        <input
          type="text"
          placeholder="Search by company name"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '8px', width: '200px' }}
        />

        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <FaFilter />
          <select
            value={selectedIndustry}
            onChange={(e) => setSelectedIndustry(e.target.value)}
            style={{ padding: '8px' }}
          >
            <option value="">All Industries</option>
            {industries.map((industry, index) => (
              <option key={index} value={industry}>{industry}</option>
            ))}
          </select>
        </div>
      </div>

      {/* List of Companies */}
      <div style={{ textAlign: 'center' }}>
        <h2>Companies</h2>
        {filteredCompanies.length > 0 ? (
          filteredCompanies.map((company, index) => (
            <div key={index} style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px', margin: '10px 0' }}>
              {company.companyLogo ? (
                <img src={company.companyLogo} alt="logo" style={{ width: '40px', height: '40px', objectFit: 'contain' }} />
              ) : (
                <div style={{ width: '40px', height: '40px', backgroundColor: '#eee', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '14px' }}>N/A</div>
              )}
              <button
                onClick={() => handleCompanyClick(company)}
                style={{
                  fontSize: '18px',
                  color: 'blue',
                  background: 'none',
                  border: 'none',
                  textDecoration: 'underline',
                  cursor: 'pointer',
                  padding: '0',
                }}
              >
                {company.companyName}
              </button>
            </div>
          ))
        ) : (
          <p>No companies found</p>
        )}
      </div>

      {/* Sidebar showing company details */}
      {companyDetails && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            right: 0,
            width: '350px',
            height: '100vh',
            backgroundColor: '#f8f9fa',
            boxShadow: '-2px 0 5px rgba(0,0,0,0.2)',
            padding: '20px',
            zIndex: 1000,
            overflowY: 'auto',
          }}
        >
          <button
            onClick={handleCloseSidebar}
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              padding: '5px 10px',
              backgroundColor: 'red',
              color: 'white',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            Close
          </button>
          <h2 style={{ textAlign: 'center' }}>Company Details</h2>

          <div style={{ textAlign: 'center', marginBottom: '15px' }}>
            {companyDetails.companyLogo ? (
              <img
                src={companyDetails.companyLogo}
                alt={`${companyDetails.companyName} logo`}
                style={{ width: '150px', height: 'auto', marginBottom: '10px' }}
              />
            ) : (
              <p>No logo uploaded</p>
            )}
            <h3>{companyDetails.companyName}</h3>
          </div>

          <p><strong>Industry:</strong> {companyDetails.industry}</p>
          <p><strong>Company Size:</strong> {companyDetails.companySize}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail}</p>

          {/* Download Document */}
          {companyDetails.document ? (
            <a
              href={companyDetails.document}
              download={`${companyDetails.companyName}-document`}
              target="_blank"
              rel="noopener noreferrer"
              style={{
                display: 'block',
                marginTop: '15px',
                textDecoration: 'none',
                textAlign: 'center',
              }}
            >
              <button
                style={{
                  padding: '10px 20px',
                  backgroundColor: '#4CAF50',
                  color: 'white',
                  border: 'none',
                  borderRadius: '5px',
                  cursor: 'pointer',
                }}
              >
                Download Legal Document
              </button>
            </a>
          ) : (
            <p style={{ color: 'gray', marginTop: '15px' }}>No document uploaded</p>
          )}
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
