import React, { useState, useEffect, useMemo } from 'react';

function ViewRegistration({ setNotificationMessage }) {
  const [companyDetails, setCompanyDetails] = useState(null);
  const [companies, setCompanies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [industryOptions, setIndustryOptions] = useState([]);

  const loadPendingCompanies = () => {
    console.log("ViewReg: [loadPendingCompanies] CALLED");
    try {
      const allLightweightCompaniesString = sessionStorage.getItem('companies');
      console.log("ViewReg: [loadPendingCompanies] 'companies' string from session:", allLightweightCompaniesString ? allLightweightCompaniesString.substring(0, 200) + "..." : "NULL");

      if (!allLightweightCompaniesString) {
        console.warn("ViewReg: [loadPendingCompanies] No 'companies' key found in sessionStorage. Setting empty lists.");
        setCompanies([]);
        setIndustryOptions([]);
        return;
      }

      let allLightweightCompanies;
      try {
        allLightweightCompanies = JSON.parse(allLightweightCompaniesString);
      } catch (jsonParseError) {
        console.error("ViewReg: [loadPendingCompanies] Error parsing 'companies' from sessionStorage:", jsonParseError, "Data:", allLightweightCompaniesString);
        setCompanies([]);
        setIndustryOptions([]);
        return;
      }
      
      console.log("ViewReg: [loadPendingCompanies] Parsed lightweight companies:", allLightweightCompanies);

      if (!Array.isArray(allLightweightCompanies)) {
        console.error("ViewReg: [loadPendingCompanies] 'companies' from sessionStorage is not an array. Type:", typeof allLightweightCompanies);
        setCompanies([]);
        setIndustryOptions([]);
        return;
      }

      const pendingCompaniesWithDetails = [];
      for (const lwCompany of allLightweightCompanies) {
        console.log("ViewReg: [loadPendingCompanies] Processing lwCompany:", lwCompany);

        if (!lwCompany || typeof lwCompany !== 'object') {
            console.warn("ViewReg: [loadPendingCompanies] Encountered invalid lwCompany in list. Skipping:", lwCompany);
            continue;
        }

        if (lwCompany.isAccepted) {
            console.log(`ViewReg: [loadPendingCompanies] Company ${lwCompany.companyName || lwCompany.companyEmail} is already accepted. Skipping.`);
            continue;
        }

        if (!lwCompany.companyEmail) {
            console.warn("ViewReg: [loadPendingCompanies] lwCompany is missing companyEmail. Cannot fetch full details. Skipping:", lwCompany);
            continue;
        }

        const fullDataKey = `companyData:${lwCompany.companyEmail}`;
        console.log(`ViewReg: [loadPendingCompanies] Attempting to get full data with key: "${fullDataKey}"`);
        const fullDataString = sessionStorage.getItem(fullDataKey);

        if (fullDataString) {
          console.log(`ViewReg: [loadPendingCompanies] Found fullDataString for ${lwCompany.companyEmail}:`, fullDataString.substring(0, 100) + "...");
          try {
            const fullData = JSON.parse(fullDataString);
            console.log(`ViewReg: [loadPendingCompanies] Parsed fullData for ${lwCompany.companyEmail}:`, fullData);
            
            // Construct the company object for the state
            const companyEntry = {
              companyName: fullData.companyName || lwCompany.companyName,
              industry: fullData.industry || lwCompany.industry,
              companySize: fullData.companySize || lwCompany.companySize,
              companyEmail: lwCompany.companyEmail, // Email is the key, must be from lwCompany
              companyLogo: fullData.companyLogo,   // Crucial: logo from 'companyData'
              document: fullData.document,         // Crucial: document from 'companyData'
              isAccepted: lwCompany.isAccepted,    // Status from the main 'companies' list
              hasNotification: lwCompany.hasNotification, // Status from the main 'companies' list
              // password field is not needed for display from lwCompany or fullData
            };
            pendingCompaniesWithDetails.push(companyEntry);
            console.log(`ViewReg: [loadPendingCompanies] Pushed to pendingCompaniesWithDetails:`, companyEntry);

          } catch (parseError) {
            console.error(`ViewReg: [loadPendingCompanies] Error parsing fullData for ${lwCompany.companyEmail} from key ${fullDataKey}. Data: ${fullDataString.substring(0,100)}... Error:`, parseError);
          }
        } else {
          console.warn(`ViewReg: [loadPendingCompanies] Full data (key: ${fullDataKey}) not found for ${lwCompany.companyEmail}. Pushing lwCompany as is (may lack logo/doc).`);
          pendingCompaniesWithDetails.push(lwCompany); // Push with limited info
        }
      }
      console.log("ViewReg: [loadPendingCompanies] Final pendingCompaniesWithDetails before setCompanies:", pendingCompaniesWithDetails);
      setCompanies(pendingCompaniesWithDetails);

      const industries = [...new Set(pendingCompaniesWithDetails.map(c => c.industry).filter(Boolean))];
      console.log("ViewReg: [loadPendingCompanies] Setting industry options:", industries);
      setIndustryOptions(industries);

    } catch (e) {
      console.error("ViewReg: [loadPendingCompanies] Overall try-catch error:", e);
      setCompanies([]);
      setIndustryOptions([]);
    }
  };

  useEffect(() => {
    console.log("ViewReg: [useEffect] Initial load & storage listener setup.");
    loadPendingCompanies();

    const onStorageChange = (e) => {
      console.log("ViewReg: [onStorageChange] Event triggered:", e);
      if (e.key === 'companies' || (e.key && e.key.startsWith('companyData:')) || e.key === null) {
        console.log("ViewReg: [onStorageChange] Relevant storage key changed. Reloading companies.");
        loadPendingCompanies();
      } else {
        console.log("ViewReg: [onStorageChange] Storage key changed, but not relevant. No reload. Key:", e.key);
      }
    };
    window.addEventListener('storage', onStorageChange);
    return () => {
      console.log("ViewReg: [useEffect Cleanup] Removing storage event listener.");
      window.removeEventListener('storage', onStorageChange);
    };
  }, []);

  // MODIFIED: handleAccept to also update the full companyData entry
  const handleAccept = () => {
    if (!companyDetails) return;
    console.log("ViewReg: [handleAccept] Accepting:", companyDetails);

    const allCompaniesList = JSON.parse(sessionStorage.getItem('companies')) || [];
    const updatedCompaniesList = allCompaniesList.map(c =>
      c.companyEmail === companyDetails.companyEmail
        ? { ...c, isAccepted: true, hasNotification: true }
        : c
    );
    sessionStorage.setItem('companies', JSON.stringify(updatedCompaniesList));
    console.log("ViewReg: [handleAccept] Updated 'companies' list in sessionStorage.");

    const companyDataKey = `companyData:${companyDetails.companyEmail}`;
    const companyDataString = sessionStorage.getItem(companyDataKey);
    if (companyDataString) {
      const companyData = JSON.parse(companyDataString);
      companyData.isAccepted = true;
      companyData.hasNotification = true;
      sessionStorage.setItem(companyDataKey, JSON.stringify(companyData));
      console.log(`ViewReg: [handleAccept] Updated '${companyDataKey}' in sessionStorage.`);
    }

    setNotificationMessage?.(`Registration for ${companyDetails.companyName} accepted.`);
    setCompanyDetails(null);
    loadPendingCompanies();
  };

  // MODIFIED: handleReject to also remove the full companyData entry
  const handleReject = () => {
    if (!companyDetails) return;
    console.log("ViewReg: [handleReject] Rejecting:", companyDetails);

    const allCompaniesList = JSON.parse(sessionStorage.getItem('companies')) || [];
    const updatedCompaniesList = allCompaniesList.filter(c => c.companyEmail !== companyDetails.companyEmail);
    sessionStorage.setItem('companies', JSON.stringify(updatedCompaniesList));
    console.log("ViewReg: [handleReject] Updated 'companies' list in sessionStorage.");

    sessionStorage.removeItem(`companyData:${companyDetails.companyEmail}`);
    console.log(`ViewReg: [handleReject] Removed 'companyData:${companyDetails.companyEmail}' from sessionStorage.`);

    setNotificationMessage?.(`Registration for ${companyDetails.companyName} rejected.`);
    setCompanyDetails(null);
    loadPendingCompanies();
  };

  const handleDownloadDocument = (documentToDownload) => {
    console.log("ViewReg: [handleDownloadDocument] Attempting to download:", documentToDownload);
    if (!documentToDownload || !documentToDownload.dataUrl || !documentToDownload.name) {
        console.error("ViewReg: [handleDownloadDocument] Document data is invalid for download.", documentToDownload);
        alert("Could not download document: data is missing or invalid.");
        return;
    }
    try {
        const link = document.createElement('a');
        link.href = documentToDownload.dataUrl;
        link.download = documentToDownload.name;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        console.log("ViewReg: [handleDownloadDocument] Download initiated for:", documentToDownload.name);
    } catch (error) {
        console.error("ViewReg: [handleDownloadDocument] Error during download:", error);
        alert("An error occurred while trying to download the document.");
    }
  };

  const filteredCompanies = useMemo(() => {
    console.log("ViewReg: [useMemo filteredCompanies] Recalculating. Current 'companies' state:", companies);
    console.log("ViewReg: [useMemo filteredCompanies] SearchQuery:", searchQuery, "SelectedIndustry:", selectedIndustry);

    if (!Array.isArray(companies)) {
        console.warn("ViewReg: [useMemo filteredCompanies] 'companies' state is not an array. Returning empty list for filtering.");
        return [];
    }

    const result = companies.filter(c => {
      if (!c || typeof c !== 'object') {
        console.warn("ViewReg: [useMemo filteredCompanies] Invalid item in 'companies' state during filter:", c);
        return false;
      }
      const companyNameForFilter = c.companyName || ""; // Default to empty string if undefined/null
      const nameMatch = typeof companyNameForFilter === 'string' ?
        companyNameForFilter.toLowerCase().includes(searchQuery.toLowerCase()) :
        searchQuery === ''; // If companyName is not a string, match if searchQuery is empty (or handle as error)

      const industryMatch = selectedIndustry ? (c.industry === selectedIndustry) : true;
      // console.log(`ViewReg: [useMemo filteredCompanies] Filtering ${c.companyName}: nameMatch=${nameMatch}, industryMatch=${industryMatch}`);
      return nameMatch && industryMatch;
    });
    console.log("ViewReg: [useMemo filteredCompanies] Result of filtering:", result);
    return result;
  }, [companies, searchQuery, selectedIndustry]);

  console.log("ViewReg: Rendering component. 'companies' state:", companies, "filteredCompanies:", filteredCompanies);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Pending Company Registrations</h1>

      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by company name..."
          value={searchQuery}
          onChange={e => setSearchQuery(e.target.value)}
          style={{ padding: '8px', width: '300px', border: '1px solid #ccc', borderRadius: '4px' }}
        />
        <div>
          <button
            onClick={() => setSelectedIndustry('')}
            style={{
              padding: '8px 12px',
              marginRight: '10px',
              cursor: 'pointer',
              backgroundColor: selectedIndustry === '' ? '#007bff' : '#f0f0f0',
              color: selectedIndustry === '' ? 'white' : 'black',
              border: '1px solid #ccc',
              borderRadius: '4px'
            }}
          >
            All Industries
          </button>
          <select
            value={selectedIndustry}
            onChange={e => setSelectedIndustry(e.target.value)}
            style={{ padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}
          >
            <option value="">Filter by Industry</option>
            {industryOptions.map((industry, idx) => (
              <option key={idx} value={industry}>{industry}</option>
            ))}
          </select>
        </div>
      </div>

      <div style={{ marginTop: '20px' }}>
        {filteredCompanies.length > 0 ? filteredCompanies.map(company => (
          <div key={company.companyEmail || `comp-${Math.random()}`} style={{ // Added fallback key
            padding: '10px', border: '1px solid #eee', borderRadius: '4px',
            marginBottom: '10px', display: 'flex', alignItems: 'center',
            backgroundColor: '#fff'
          }}>
            <button
              onClick={() => {
                console.log("ViewReg: Opening details for:", company);
                setCompanyDetails(company);
              }}
              style={{
                display: 'inline-flex',
                alignItems: 'center',
                background: 'none',
                border: 'none',
                padding: 0,
                cursor: 'pointer',
                textAlign: 'left',
                width: '100%' // Make the button take full width of its container
              }}
            >
              {company.companyLogo && typeof company.companyLogo === 'string' ? (
                <img
                  src={company.companyLogo}
                  alt={`${company.companyName || 'N/A'} logo`}
                  style={{
                    width: '40px',
                    height: '40px',
                    marginRight: '15px',
                    borderRadius: '50%',
                    objectFit: 'cover',
                    border: '1px solid #ddd'
                  }}
                />
              ) : (
                <div style={{
                    width: '40px', height: '40px', marginRight: '15px', borderRadius: '50%',
                    backgroundColor: '#e9ecef', display: 'flex', alignItems: 'center',
                    justifyContent: 'center', color: '#6c757d', fontSize: '10px', textAlign: 'center',
                    border: '1px solid #ddd', lineHeight: '1.2'
                }}>No Logo</div>
              )}
              <span style={{ fontSize: '18px', color: '#007bff', textDecoration: 'underline' }}>
                {company.companyName || `Unnamed Company (${company.companyEmail})`}
              </span>
            </button>
          </div>
        )) : (
          <p style={{ textAlign: 'center', color: '#777', marginTop: '20px', fontSize: '16px' }}>
            {companies.length > 0 && filteredCompanies.length === 0 ?
             "No pending registrations match your current filter criteria." :
             "No pending registrations found."
            }
          </p>
        )}
      </div>

      {companyDetails && (
        <div style={{
          position: 'fixed', top: 0, right: 0, width: '350px', height: '100vh',
          backgroundColor: '#ffffff', boxShadow: '-3px 0 8px rgba(0,0,0,0.15)',
          padding: '20px', overflowY: 'auto', zIndex: 1000, boxSizing: 'border-box',
          borderLeft: '1px solid #dee2e6'
        }}>
          <button
            onClick={() => {
                console.log("ViewReg: Closing company details sidebar.");
                setCompanyDetails(null);
            }}
            style={{
              position: 'absolute', top: '15px', right: '15px',
              background: '#6c757d', color: 'white', border: 'none',
              borderRadius: '50%', width: '30px', height: '30px',
              fontSize: '16px', cursor: 'pointer', lineHeight: '30px', textAlign: 'center',
              display: 'flex', alignItems: 'center', justifyContent: 'center'
            }}
            aria-label="Close"
          >
            Ã—
          </button>
          <h2 style={{ textAlign: 'center', marginTop: '30px', marginBottom: '20px', color: '#343a40' }}>Company Details</h2>

          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            {companyDetails.companyLogo && typeof companyDetails.companyLogo === 'string' ? (
              <img
                src={companyDetails.companyLogo}
                alt={`${companyDetails.companyName || 'N/A'} logo`}
                style={{ width: '120px', height: '120px', borderRadius: '50%', objectFit: 'cover', border: '2px solid #dee2e6' }}
              />
            ) : <div style={{width: '120px', height: '120px', borderRadius: '50%', backgroundColor: '#e9ecef', display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '10px auto', color: '#6c757d', border: '2px solid #dee2e6' }}>No Logo</div>}
            <h3 style={{marginTop: '10px', color: '#495057'}}>{companyDetails.companyName || 'N/A'}</h3>
          </div>

          <div style={{padding: '0 10px'}}>
            <p style={{marginBottom: '8px'}}><strong>Industry:</strong> {companyDetails.industry || 'N/A'}</p>
            <p style={{marginBottom: '8px'}}><strong>Size:</strong> {companyDetails.companySize || 'N/A'}</p>
            <p style={{marginBottom: '15px'}}><strong>Email:</strong> {companyDetails.companyEmail || 'N/A'}</p>
          </div>


          {companyDetails.document && companyDetails.document.dataUrl && companyDetails.document.name ? (
            <div style={{ textAlign: 'center', margin: '25px 0' }}>
              <button
                onClick={() => handleDownloadDocument(companyDetails.document)}
                style={{
                  padding: '10px 20px', backgroundColor: '#007bff',
                  color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer',
                  fontSize: '16px'
                }}
              >
                Download: {companyDetails.document.name}
              </button>
            </div>
          ) : (
            <p style={{textAlign: 'center', color: '#6c757d', margin: '25px 0'}}>No document uploaded.</p>
          )}

          <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '30px', padding: '10px 0', borderTop: '1px solid #e9ecef' }}>
            <button
              onClick={handleAccept}
              style={{
                padding: '12px 25px', backgroundColor: '#28a745',
                color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '16px'
              }}
            >
              Accept
            </button>
            <button
              onClick={handleReject}
              style={{
                padding: '12px 25px', backgroundColor: '#dc3545',
                color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '16px'
              }}
            >
              Reject
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
