import React, { useState } from 'react';

function CompanyViewRegistration() {
  const [companyDetails, setCompanyDetails] = useState(null);
  const [companies, setCompanies] = useState([
    {
      companyName: 'Tech Corp',
      industry: 'Technology',
      companySize: 'Medium',
      companyEmail: 'contact@techcorp.com',
      companyLogo: 'tech-corp-logo.png',
      document: 'legal-doc.pdf',
    },
    {
      companyName: 'Foodies Ltd.',
      industry: 'Food & Beverage',
      companySize: 'Small',
      companyEmail: 'info@foodies.com',
      companyLogo: 'foodies-logo.png',
      document: 'foodies-legal-doc.pdf',
    },
  ]);

  const handleCompanyClick = (company) => {
    setCompanyDetails(company);
  };

  const handleCloseSidebar = () => {
    setCompanyDetails(null);
  };

  return (
    <div>
      <h1>Company Registration View</h1>

      {/* List of Companies */}
      <div style={{ margin: '20px' }}>
        <h2>Companies</h2>
        <div>
          {companies.map((company, index) => (
            <button
              key={index}
              onClick={() => handleCompanyClick(company)}
              style={{
                marginBottom: '10px',
                textDecoration: 'none',
                color: 'blue',
                fontSize: '18px',
                background: 'none',
                border: 'none',
                cursor: 'pointer',
                padding: '0',
              }}
            >
              {company.companyName}
            </button>
          ))}
        </div>
      </div>

      {/* Sidebar showing company details */}
      {companyDetails && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            right: 0,
            width: '300px',
            height: '100vh',
            backgroundColor: '#f8f9fa',
            boxShadow: '-2px 0 5px rgba(0,0,0,0.2)',
            padding: '20px',
            zIndex: 1000,
          }}
        >
          <button
            onClick={handleCloseSidebar}
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              padding: '5px 10px',
              backgroundColor: 'red',
              color: 'white',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            Close
          </button>
          <h2>Company Details</h2>
          <img
            src={companyDetails.companyLogo}
            alt={`${companyDetails.companyName} logo`}
            style={{ width: '100%', height: 'auto', marginBottom: '15px' }}
          />
          <p><strong>Industry:</strong> {companyDetails.industry}</p>
          <p><strong>Company Size:</strong> {companyDetails.companySize}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail}</p>

          {/* Download Document */}
          <a
            href={`/${companyDetails.document}`}
            download
            style={{
              display: 'block',
              marginTop: '15px',
              textDecoration: 'none',
              color: 'green',
            }}
          >
            <button
              style={{
                padding: '10px 20px',
                backgroundColor: '#4CAF50',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              Download Legal Document
            </button>
          </a>
        </div>
      )}
    </div>
  );
}

export default CompanyViewRegistration;
