import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyRegister() {
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [companyLogo, setCompanyLogo] = useState(null);
  const [companyEmail, setCompanyEmail] = useState('');
  const [document, setDocument] = useState(null);
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const maxCompanies = 50; // Limit the number of companies

  const handleDocumentChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) return setDocument(null);

    // Store only file name and size in sessionStorage, not the entire file
    setDocument({
      name: file.name,
      size: file.size,
    });

    const reader = new FileReader();
    reader.onload = () => {
      setDocumentPreview(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleLogoChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) return setCompanyLogo(null);

    // Store only file name and size in sessionStorage, not the entire file
    setCompanyLogo({
      name: file.name,
      size: file.size,
    });

    const reader = new FileReader();
    reader.onloadend = () => {
      setCompanyLogoPreview(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const newErrors = {};
    if (!companyName) newErrors.companyName = 'Company name is required';
    if (!industry) newErrors.industry = 'Industry is required';
    if (!companySize) newErrors.companySize = 'Company size is required';
    if (!companyLogo) newErrors.companyLogo = 'Company logo is required';
    if (!companyEmail) newErrors.companyEmail = 'Email is required';
    else if (!emailRegex.test(companyEmail)) newErrors.companyEmail = 'Invalid email format';
    if (!document) newErrors.document = 'Document is required';
    if (!password) newErrors.password = 'Password is required';
    setErrors(newErrors);
    if (Object.keys(newErrors).length) return;

    const fullCompany = {
      companyName,
      industry,
      companySize,
      companyLogo,
      companyEmail,
      document,
      password,
      isAccepted: false,
      hasNotification: false
    };

    // Get existing companies from sessionStorage
    const existing = JSON.parse(sessionStorage.getItem('companies')) || [];

    // Remove the oldest company if the limit is reached
    if (existing.length >= maxCompanies) {
      existing.shift(); // Removes the first (oldest) company
    }

    // Add the new company to the list
    existing.push(fullCompany);
    sessionStorage.setItem('companies', JSON.stringify(existing));
    sessionStorage.setItem('currentCompany', JSON.stringify(fullCompany));
    setIsModalOpen(true);
  };

  const handleModalOkClick = () => {
    setIsModalOpen(false);
    navigate('/company-login');
  };

  const inputStyle = (err) => ({
    width: '100%',
    padding: '8px',
    marginBottom: '5px',
    border: err ? '1px solid red' : '1px solid #ddd'
  });

  const errorText = (msg) => (
    <div style={{ color: 'red', fontSize: '12px', marginBottom: '10px' }}>{msg}</div>
  );

  return (
    <div style={{ display: 'flex', justifyContent: 'center', minHeight: '100vh' }}>
      <div style={{ width: '400px', padding: '20px' }}>
        <h2>Company Register</h2>
        <form onSubmit={handleRegister}>
          <div>
            <label>Company Name:</label>
            <input type="text" value={companyName} onChange={e => setCompanyName(e.target.value)} style={inputStyle(errors.companyName)} />
            {errors.companyName && errorText(errors.companyName)}
          </div>
          <div>
            <label>Industry:</label>
            <input type="text" value={industry} onChange={e => setIndustry(e.target.value)} style={inputStyle(errors.industry)} />
            {errors.industry && errorText(errors.industry)}
          </div>
          <div>
            <label>Company Size:</label>
            <select value={companySize} onChange={e => setCompanySize(e.target.value)} style={inputStyle(errors.companySize)}>
              <option value="">Select company size</option>
              <option value="small">Small (≤50)</option>
              <option value="medium">Medium (51–100)</option>
              <option value="large">Large (101–500)</option>
              <option value="corporate">Corporate (500+)</option>
            </select>
            {errors.companySize && errorText(errors.companySize)}
          </div>
          <div>
            <label>Company Logo:</label>
            <input type="file" accept="image/*" onChange={handleLogoChange} style={inputStyle(errors.companyLogo)} />
            {errors.companyLogo && errorText(errors.companyLogo)}
            {companyLogo && <img src={URL.createObjectURL(companyLogo)} alt="Preview" style={{ width: 80, height: 80, borderRadius: '50%' }} />}
          </div>
          <div>
            <label>Email:</label>
            <input type="email" value={companyEmail} onChange={e => setCompanyEmail(e.target.value)} style={inputStyle(errors.companyEmail)} />
            {errors.companyEmail && errorText(errors.companyEmail)}
          </div>
          <div>
            <label>Password:</label>
            <input type="password" value={password} onChange={e => setPassword(e.target.value)} style={inputStyle(errors.password)} />
            {errors.password && errorText(errors.password)}
          </div>
          <div>
            <label>Upload Document:</label>
            <input type="file" accept=".pdf,.docx,.txt" onChange={handleDocumentChange} style={inputStyle(errors.document)} />
            {errors.document && errorText(errors.document)}
            {document && <p style={{ fontSize: '12px' }}>Selected: {document.name}</p>}
          </div>
          <button type="submit" style={{ marginTop: 10, width: '100%', padding: '10px', backgroundColor: '#4CAF50', color: '#fff', border: 'none' }}>Register</button>
        </form>

        {isModalOpen && (
          <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <div style={{ backgroundColor: '#fff', padding: 20, borderRadius: 8 }}>
              <h3>Registration Successful!</h3>
              <p>Await SCAD approval.</p>
              <button onClick={handleModalOkClick} style={{ marginTop: 10, backgroundColor: '#007bff', color: '#fff', border: 'none', padding: '10px' }}>OK</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CompanyRegister;
