import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyRegister() {
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [companyLogo, setCompanyLogo] = useState(null);
  const [companyEmail, setCompanyEmail] = useState('');
  const [document, setDocument] = useState(null);
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const maxCompanies = 50;

  // Handle document change
  const handleDocumentChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) return setDocument(null);
    setDocument({
      name: file.name,
      size: file.size,
    });
  };

  // Handle logo change and store the file
  const handleLogoChange = (e) => {
    try {
      const file = e.target.files?.[0];
      if (!file) return setCompanyLogo(null);

      // Store the actual file in state
      setCompanyLogo(file);

      // Store the file data in sessionStorage for syncing across tabs
      const logoData = {
        name: file.name,
        size: file.size,
      };
      sessionStorage.setItem('companyLogo', JSON.stringify(logoData));
    } catch (error) {
      console.error('Error handling logo file:', error);
    }
  };

  // Sync logo data across tabs
  useEffect(() => {
    const syncLogoWithSessionStorage = () => {
      try {
        const savedLogo = JSON.parse(sessionStorage.getItem('companyLogo'));
        if (savedLogo) {
          setCompanyLogo(savedLogo);
        }
      } catch (error) {
        console.error('Error syncing logo from sessionStorage:', error);
      }
    };

    syncLogoWithSessionStorage();

    window.addEventListener('storage', syncLogoWithSessionStorage);

    return () => {
      window.removeEventListener('storage', syncLogoWithSessionStorage);
    };
  }, []);

  // Handle form submission
  const handleRegister = (e) => {
    try {
      e.preventDefault();
      const newErrors = {};
      if (!companyName) newErrors.companyName = 'Company name is required';
      if (!industry) newErrors.industry = 'Industry is required';
      if (!companySize) newErrors.companySize = 'Company size is required';
      if (!companyEmail || !emailRegex.test(companyEmail)) newErrors.companyEmail = 'Valid company email is required';
      if (!password) newErrors.password = 'Password is required';
      if (Object.keys(newErrors).length > 0) return setErrors(newErrors);

      const fullCompany = {
        companyName,
        industry,
        companySize,
        companyLogo,
        companyEmail,
        document,
        password,
        isAccepted: false,
        hasNotification: false,
      };

      const existing = JSON.parse(sessionStorage.getItem('companies')) || [];
      if (existing.length >= maxCompanies) {
        alert('Maximum number of registrations reached.');
        return;
      }
      existing.push(fullCompany);
      sessionStorage.setItem('companies', JSON.stringify(existing));

      setCompanyName('');
      setIndustry('');
      setCompanySize('');
      setCompanyLogo(null);
      setCompanyEmail('');
      setDocument(null);
      setPassword('');
      alert('Company successfully registered!');
      navigate('/'); // Navigate to a different page or show a success message
    } catch (error) {
      console.error('Error registering company:', error);
    }
  };

  return (
    <div>
      <h1>Register Company</h1>
      <form onSubmit={handleRegister}>
        <div>
          <label>Company Name:</label>
          <input
            type="text"
            value={companyName}
            onChange={(e) => setCompanyName(e.target.value)}
          />
          {errors.companyName && <p>{errors.companyName}</p>}
        </div>
        <div>
          <label>Industry:</label>
          <input
            type="text"
            value={industry}
            onChange={(e) => setIndustry(e.target.value)}
          />
          {errors.industry && <p>{errors.industry}</p>}
        </div>
        <div>
          <label>Company Size:</label>
          <input
            type="text"
            value={companySize}
            onChange={(e) => setCompanySize(e.target.value)}
          />
          {errors.companySize && <p>{errors.companySize}</p>}
        </div>
        <div>
          <label>Company Email:</label>
          <input
            type="email"
            value={companyEmail}
            onChange={(e) => setCompanyEmail(e.target.value)}
          />
          {errors.companyEmail && <p>{errors.companyEmail}</p>}
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {errors.password && <p>{errors.password}</p>}
        </div>
        <div>
          <label>Upload Logo:</label>
          <input type="file" onChange={handleLogoChange} />
        </div>
        <div>
          <label>Upload Document:</label>
          <input type="file" onChange={handleDocumentChange} />
        </div>
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default CompanyRegister;
