// CompanyRegister.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyRegister() {
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [companyLogo, setCompanyLogo] = useState(null);
  const [companyEmail, setCompanyEmail] = useState('');
  const [document, setDocument] = useState(null);
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleDocumentChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) {
      setDocument(null);
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setDocument({ name: file.name, dataUrl: reader.result });
    };
    reader.readAsDataURL(file);
  };

  const handleLogoChange = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      setCompanyLogo(URL.createObjectURL(file));
    }
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const newErrors = {};
    if (!companyName) newErrors.companyName = 'Company name is required';
    if (!industry) newErrors.industry = 'Industry is required';
    if (!companySize) newErrors.companySize = 'Company size is required';
    if (!companyLogo) newErrors.companyLogo = 'Company logo is required';
    if (!companyEmail) newErrors.companyEmail = 'Email is required';
    else if (!emailRegex.test(companyEmail)) newErrors.companyEmail = 'Invalid email format';
    if (!document) newErrors.document = 'Document is required';
    if (!password) newErrors.password = 'Password is required';
    setErrors(newErrors);
    if (Object.keys(newErrors).length) return;

    const lightweightCompany = {
      companyName,
      industry,
      companySize,
      companyEmail,
      password,
      isAccepted: false,
      hasNotification: false
    };

    const existing = JSON.parse(localStorage.getItem('companies')) || [];
    existing.push(lightweightCompany);
    localStorage.setItem('companies', JSON.stringify(existing));

    sessionStorage.setItem(
      `companyData:${companyEmail}`,
      JSON.stringify({
        ...lightweightCompany,
        companyLogo,
        document
      })
    );

    // Auto-login company after registration
    localStorage.setItem('currentCompany', JSON.stringify(lightweightCompany));

    setIsModalOpen(true);
  };

  const handleModalOkClick = () => {
    setIsModalOpen(false);
    navigate('/company-login');
  };

  const inputStyle = (err) => ({
    width: '100%', padding: '8px', marginBottom: '5px',
    border: err ? '1px solid red' : '1px solid #ddd',
    boxSizing: 'border-box'
  });
  const errorText = (msg) => <div style={{ color: 'red', fontSize: '12px', marginBottom: '10px' }}>{msg}</div>;

  return (
    <div style={{ display:'flex',justifyContent:'center',alignItems:'center',minHeight:'100vh',padding:'20px 0' }}>
      <div style={{ padding:'20px',border:'1px solid #ddd',borderRadius:'8px',width:'400px',maxWidth:'100%' }}>
        <h2>Company Register</h2>
        <form onSubmit={handleRegister} noValidate>
          <div>
            <label>Company Name:</label>
            <input type="text" value={companyName} onChange={e=>setCompanyName(e.target.value)} style={inputStyle(errors.companyName)} />
            {errors.companyName && errorText(errors.companyName)}
          </div>
          <div>
            <label>Industry:</label>
            <input type="text" value={industry} onChange={e=>setIndustry(e.target.value)} style={inputStyle(errors.industry)} />
            {errors.industry && errorText(errors.industry)}
          </div>
          <div>
            <label>Company Size:</label>
            <select value={companySize} onChange={e=>setCompanySize(e.target.value)} style={inputStyle(errors.companySize)}>
              <option value="">Select company size</option>
              <option value="small">Small (≤50 employees)</option>
              <option value="medium">Medium (51–100 employees)</option>
              <option value="large">Large (101–500 employees)</option>
              <option value="corporate">Corporate (500+ employees)</option>
            </select>
            {errors.companySize && errorText(errors.companySize)}
          </div>
          <div>
            <label>Company Logo:</label>
            <input type="file" accept="image/*" onChange={handleLogoChange} style={inputStyle(errors.companyLogo)} />
            {errors.companyLogo && errorText(errors.companyLogo)}
            {companyLogo && <img src={companyLogo} alt="Logo" style={{ width:'100px',height:'100px',objectFit:'cover',borderRadius:'50%' }}/>}            
          </div>
          <div>
            <label>Official Company Email:</label>
            <input type="email" value={companyEmail} onChange={e=>setCompanyEmail(e.target.value)} style={inputStyle(errors.companyEmail)} />
            {errors.companyEmail && errorText(errors.companyEmail)}
          </div>
          <div>
            <label>Password:</label>
            <input type="password" value={password} onChange={e=>setPassword(e.target.value)} style={inputStyle(errors.password)} />
            {errors.password && errorText(errors.password)}
          </div>
          <div>
            <label>Upload Documents (PDF, Word, etc.):</label>
            <input type="file" accept=".pdf,.docx,.txt" onChange={handleDocumentChange} style={inputStyle(errors.document)} />
            {errors.document && errorText(errors.document)}
          </div>
          <button type="submit" style={{ padding:'10px',backgroundColor:'#4CAF50',color:'white',border:'none',borderRadius:'4px',marginTop:'10px',width:'100%',cursor:'pointer' }}>
            Register
          </button>
        </form>

        {isModalOpen && (
          <div style={{
            position:'fixed',top:0,left:0,right:0,bottom:0,
            backgroundColor:'rgba(0,0,0,0.5)',display:'flex',justifyContent:'center',alignItems:'center',zIndex:1000
          }}>
            <div style={{ backgroundColor:'#fff',padding:'20px',borderRadius:'8px',textAlign:'center',width:'300px',maxWidth:'100%' }}>
              <h3>Registration Successful!</h3>
              <p>Please wait for SCAD approval.</p>
              <button onClick={handleModalOkClick} style={{ padding:'10px',backgroundColor:'#007bff',color:'white',border:'none',borderRadius:'4px',cursor:'pointer',marginTop:'10px' }}>
                OK
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CompanyRegister;
