import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CompanyLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [notificationMessage, setNotificationMessage] = useState('');
  const [hasNotification, setHasNotification] = useState(false);
  const navigate = useNavigate();

  // Handle login
  const handleLogin = (e) => {
    e.preventDefault();

    const newErrors = {};

    // Form validation
    if (!email) {
      newErrors.email = 'Email is required';
    } else if (!emailRegex.test(email)) {
      newErrors.email = 'Invalid email format';
    }

    if (!password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    if (Object.keys(newErrors).length > 0) return;

    // Check company credentials in localStorage
    const companies = JSON.parse(localStorage.getItem('companies')) || [];
    const companyUser = companies.find((c) => c.companyEmail === email);

    if (!companyUser) {
      setErrors({ form: 'Invalid email' });
    } else if (companyUser.password !== password) {
      setErrors({ form: 'Wrong password' });
    } else if (!companyUser.isAccepted) {
      setErrors({ form: 'You are not accepted yet!' });
    } else {
      if (companyUser.hasNotification) {
        setNotificationMessage('Your registration has been accepted!');
        setHasNotification(true);

        // Reset notification state in localStorage
        const updatedCompanies = companies.map(c =>
          c.companyEmail === email ? { ...c, hasNotification: false } : c
        );
        localStorage.setItem('companies', JSON.stringify(updatedCompanies));
      }

      navigate('/company-dashboard');
    }
  };

  const togglePopup = () => {
    // Handle toggling the notification popup if necessary
    alert(notificationMessage);
    setNotificationMessage('');
  };

  return (
    <div style={{ position: 'relative', padding: '20px' }}>
      <form onSubmit={handleLogin}>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={errors.email ? { borderColor: 'red' } : {}}
          />
          {errors.email && <span>{errors.email}</span>}
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={errors.password ? { borderColor: 'red' } : {}}
          />
          {errors.password && <span>{errors.password}</span>}
        </div>
        <button type="submit">Login</button>
        {errors.form && <p>{errors.form}</p>}
      </form>

      <div
        onClick={togglePopup}
        style={{
          position: 'absolute',
          top: '20px',
          right: '20px',
          cursor: 'pointer',
          fontSize: '24px',
        }}
      >
        {hasNotification && (
          <div
            style={{
              position: 'absolute',
              top: '0',
              right: '0',
              width: '20px',
              height: '20px',
              backgroundColor: 'red',
              color: 'white',
              borderRadius: '50%',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              fontSize: '12px',
              fontWeight: 'bold',
            }}
          >
            1
          </div>
        )}
        <img src="/bell.jpg" alt="Bell Icon" style={{ width: '40px', height: '40px' }} />
      </div>
    </div>
  );
};

export default CompanyLogin;
