import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyRegister(props) {
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [companyLogo, setCompanyLogo] = useState(null);
  const [companyEmail, setCompanyEmail] = useState('');
  const [document, setDocument] = useState(null);
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleRegister = (e) => {
    e.preventDefault();

    const newErrors = {};

    if (!companyName) newErrors.companyName = 'Company name is required';
    if (!industry) newErrors.industry = 'Industry is required';
    if (!companySize) newErrors.companySize = 'Company size is required';
    if (!companyLogo) newErrors.companyLogo = 'Company logo is required';
    if (!companyEmail) {
      newErrors.companyEmail = 'Email is required';
    } else if (!emailRegex.test(companyEmail)) {
      newErrors.companyEmail = 'Invalid email format';
    }
    if (!document) newErrors.document = 'Document is required';
    if (!password) newErrors.password = 'Password is required';

    setErrors(newErrors);

    if (Object.keys(newErrors).length > 0) return;

    // Save the document in the public folder for download
    const documentPath = `/documents/${document.name}`; // Assuming you are putting the document in the 'public/documents' folder

    // Store the company data in localStorage
    const companyData = {
      companyName,
      industry,
      companySize,
      companyLogoName: companyLogo ? companyLogo.name : '',
      companyEmail,
      password,
      documentName: document ? document.name : '',
      documentPath, // Store the document path for later reference
    };

    const existingCompanies = JSON.parse(localStorage.getItem('companies')) || [];
    existingCompanies.push(companyData);
    localStorage.setItem('companies', JSON.stringify(existingCompanies));

    setIsModalOpen(true);
  };

  const handleLogoChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setCompanyLogo(e.target.files[0]);
    } else {
      setCompanyLogo(null);
    }
  };

  const handleDocumentChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setDocument(e.target.files[0]);
    } else {
      setDocument(null);
    }
  };

  const handleModalOkClick = () => {
    setIsModalOpen(false);
    navigate('/company-login');
  };

  const inputStyle = (error) => ({
    width: '100%',
    padding: '8px',
    marginBottom: '5px',
    border: error ? '1px solid red' : '1px solid #ddd',
    boxSizing: 'border-box',
  });

  const errorText = (msg) => (
    <div style={{ color: 'red', fontSize: '12px', marginBottom: '10px' }}>{msg}</div>
  );

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh', padding: '20px 0' }}>
      <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px', width: '400px', maxWidth: '100%' }}>
        <h2>Company Register</h2>
        <form onSubmit={handleRegister} noValidate>
          {/* Form fields here */}
          <div>
            <label htmlFor="companyName">Company Name:</label>
            <input
              id="companyName"
              type="text"
              value={companyName}
              onChange={(e) => setCompanyName(e.target.value)}
              style={inputStyle(errors.companyName)}
            />
            {errors.companyName && errorText(errors.companyName)}
          </div>
          {/* Other fields like industry, company size, logo, email, password */}
          
          <div>
            <label htmlFor="document">Upload Documents (e.g., tax/legal):</label>
            <input
              id="document"
              type="file"
              accept=".pdf,.doc,.docx,.txt"
              onChange={handleDocumentChange}
              style={inputStyle(errors.document)}
            />
            {errors.document && errorText(errors.document)}
          </div>
          
          <button
            type="submit"
            style={{
              padding: '10px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              marginTop: '10px',
              width: '100%',
              cursor: 'pointer',
            }}
          >
            Register
          </button>
        </form>
      </div>

      {/* Modal after successful registration */}
      {isModalOpen && (
        <div style={{
          position: 'fixed',
          zIndex: 1000,
          top: 0, left: 0,
          width: '100%', height: '100%',
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          display: 'flex', justifyContent: 'center', alignItems: 'center'
        }}>
          <div style={{
            backgroundColor: 'white', padding: '20px',
            borderRadius: '8px', textAlign: 'center'
          }}>
            <h2>Registration Successful!</h2>
            <p>Your company has been successfully registered. Please wait for the SCAD to review your registration.</p>
            <button
              onClick={handleModalOkClick}
              style={{
                padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none',
                borderRadius: '4px', cursor: 'pointer'
              }}
            >
              OK
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default CompanyRegister;
