// DownloadButton.js
import React from 'react';

const DownloadButton = ({ file }) => {
  const handleDownloadDocument = (file) => {
    // Log to ensure the file is available and valid
    console.log('Downloading file:', file);

    // Check if document.createElement exists and is a valid function
    if (typeof window !== 'undefined' && typeof document !== 'undefined' && document.createElement) {
      try {
        // Check if the file is valid
        if (!file) {
          console.error("File object is missing!");
          return;
        }

        // Create a link element
        const link = document.createElement('a');
        const fileUrl = URL.createObjectURL(file); // Create an object URL for the file
        link.href = fileUrl;
        link.download = file.name || 'download'; // Set the filename or default to 'download'

        // Append the link to the document and simulate a click to trigger the download
        document.body.appendChild(link);
        link.click();

        // Clean up by removing the link and revoking the object URL
        document.body.removeChild(link);
        URL.revokeObjectURL(fileUrl);
      } catch (error) {
        console.error('Download failed:', error);
      }
    } else {
      console.error("Document object is unavailable or incorrect.");
    }
  };

  return (
    <button onClick={() => handleDownloadDocument(file)}>
      Download Document
    </button>
  );
};

export default DownloadButton;
