import React, { useState } from 'react';
import { Link, Route, Routes, useNavigate } from 'react-router-dom';
import CompanyRegister from './CompanyRegister'; // Import the new page

function App() {
  // States to manage input values
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  // useNavigate hook to navigate programmatically
  const navigate = useNavigate();

  // Handle form submission for login
  const handleLogin = (e) => {
    e.preventDefault();
    alert(`Logging in with ${email} and ${password}`);
  };

  // Handle form submission for register
  const handleRegister = (e) => {
    e.preventDefault();
    alert(`Registering with ${email} and ${password}`);
  };

  // Navigate to the company registration page
  const handleCompanyRegister = () => {
    navigate('/company-register');
  };

  return (
    <div>
      <Routes>
        {/* Define the routes */}
        <Route path="/" element={
          <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
              <h2>Login</h2>
              <form>
                <div>
                  <label htmlFor="email">Email:</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    placeholder="Enter your email"
                    style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
                  />
                </div>
                <div>
                  <label htmlFor="password">Password:</label>
                  <input
                    type="password"
                    id="password"
                    name="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    placeholder="Enter your password"
                    style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
                  />
                </div>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                  <button type="submit" onClick={handleLogin} style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px' }}>
                    Login
                  </button>
                  <button type="button" onClick={handleRegister} style={{ padding: '10px', backgroundColor: '#008CBA', color: 'white', border: 'none', borderRadius: '4px' }}>
                    Register
                  </button>
                  <button type="button" onClick={handleCompanyRegister} style={{ padding: '10px', backgroundColor: '#FFA500', color: 'white', border: 'none', borderRadius: '4px' }}>
                    Register as a Company
                  </button>
                </div>
              </form>
            </div>
          </div>
        } />
        {/* Define the company register route */}
        <Route path="/company-register" element={<CompanyRegister />} />
      </Routes>
    </div>
  );
}

export default App;
