import React, { useState, useEffect, useMemo } from 'react';

function ViewRegistration({ setNotificationMessage }) {
  const [companyDetails, setCompanyDetails] = useState(null);
  const [companies, setCompanies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [industryOptions, setIndustryOptions] = useState([]);

  const loadPendingCompanies = () => {
    try {
      const allCompanies = JSON.parse(localStorage.getItem('companies')) || [];
      const pendingCompanies = allCompanies.filter(company => !company.isAccepted);
      setCompanies(pendingCompanies);

      const industries = [...new Set(pendingCompanies.map(company => company.industry))].filter(Boolean);
      setIndustryOptions(industries);
    } catch (error) {
      console.error("Error loading companies from localStorage:", error);
    }
  };

  useEffect(() => {
    loadPendingCompanies();

    const handleStorageChange = (event) => {
      if (event.key === 'companies') {
        loadPendingCompanies();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const handleCompanyClick = (company) => {
    setCompanyDetails(company);
  };

  const handleCloseSidebar = () => {
    setCompanyDetails(null);
  };

  const handleAccept = () => {
    if (!companyDetails) return;

    try {
      const allCompanies = JSON.parse(localStorage.getItem('companies')) || [];
      const updatedCompaniesList = allCompanies.map(company =>
        company.companyEmail === companyDetails.companyEmail
          ? { ...company, isAccepted: true, hasNotification: true }
          : company
      );

      localStorage.setItem('companies', JSON.stringify(updatedCompaniesList));

      if (setNotificationMessage) {
        setNotificationMessage(`Registration for ${companyDetails.companyName} has been accepted.`);
      }

      loadPendingCompanies();
      setCompanyDetails(null);
    } catch (error) {
      console.error("Error accepting company:", error);
    }
  };

  const handleReject = () => {
    if (!companyDetails) return;

    try {
      const allCompanies = JSON.parse(localStorage.getItem('companies')) || [];
      const updatedCompaniesList = allCompanies.filter(company => company.companyEmail !== companyDetails.companyEmail);

      localStorage.setItem('companies', JSON.stringify(updatedCompaniesList));

      if (setNotificationMessage) {
        setNotificationMessage(`Registration for ${companyDetails.companyName} has been rejected and removed.`);
      }

      loadPendingCompanies();
      setCompanyDetails(null);
    } catch (error) {
      console.error("Error rejecting company:", error);
    }
  };

  const filteredCompanies = useMemo(() => companies.filter(company => {
    const matchesName = company.companyName.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesIndustry = selectedIndustry ? company.industry === selectedIndustry : true;
    return matchesName && matchesIndustry;
  }), [companies, searchQuery, selectedIndustry]);

  const handleDownloadDocument = (documentUrl) => {
    const link = document.createElement('a');
    link.href = documentUrl;
    link.download = documentUrl.split('/').pop(); // Extract filename from the URL

    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div>
      <h1>Company Registration View (Pending)</h1>

      <div style={{ textAlign: 'center' }}>
        <input
          type="text"
          placeholder="Search by company name"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <button
          onClick={() => setSelectedIndustry('')}
          style={{
            backgroundColor: selectedIndustry === '' ? '#007bff' : '#f8f9fa',
            color: selectedIndustry === '' ? 'white' : 'black',
          }}
        >
          All Industries
        </button>

        <select
          value={selectedIndustry}
          onChange={(e) => setSelectedIndustry(e.target.value)}
        >
          <option value="">Select Industry</option>
          {industryOptions.map((industry, index) => (
            <option key={index} value={industry}>
              {industry}
            </option>
          ))}
        </select>
      </div>

      <div style={{ margin: '20px', textAlign: 'center' }}>
        <h2>Pending Registrations</h2>
        <div>
          {filteredCompanies.length > 0 ? (
            filteredCompanies.map((company) => (
              <p key={company.companyEmail} style={{ marginBottom: '10px' }}>
                <button
                  onClick={() => handleCompanyClick(company)}
                  style={{ fontSize: '18px', color: 'blue', background: 'none', border: 'none', textDecoration: 'underline', cursor: 'pointer' }}
                >
                  {company.companyLogo && (
                    <img
                      src={company.companyLogo}
                      alt={`${company.companyName} logo`}
                      style={{
                        width: '30px',
                        height: '30px',
                        marginRight: '10px',
                        verticalAlign: 'middle',
                        borderRadius: '50%',
                      }}
                    />
                  )}
                  {company.companyName}
                </button>
              </p>
            ))
          ) : (
            <p>No pending company registrations found.</p>
          )}
        </div>
      </div>

      {companyDetails && (
        <div style={{ position: 'fixed', top: 0, right: 0, width: '350px', height: '100vh', backgroundColor: '#f8f9fa', boxShadow: '-2px 0 5px rgba(0,0,0,0.2)', padding: '20px', zIndex: 1000, overflowY: 'auto' }}>
          <button onClick={handleCloseSidebar} style={{ position: 'absolute', top: '10px', right: '10px', padding: '5px 10px', backgroundColor: 'grey', color: 'white', border: 'none', cursor: 'pointer' }}>Close</button>
          <h2 style={{ textAlign: 'center' }}>Company Details</h2>

          <div style={{ textAlign: 'center', marginBottom: '15px' }}>
            {companyDetails.companyLogo ? (
              <img
                src={companyDetails.companyLogo}
                alt={`${companyDetails.companyName} logo`}
                style={{
                  width: '150px',
                  height: '150px',
                  marginBottom: '10px',
                  borderRadius: '50%',
                }}
              />
            ) : (
              <p>No logo uploaded</p>
            )}
            <h3>{companyDetails.companyName}</h3>
          </div>

          <p><strong>Industry:</strong> {companyDetails.industry}</p>
          <p><strong>Company Size:</strong> {companyDetails.companySize}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail}</p>

          {companyDetails.document && (
            <div style={{ textAlign: 'center', marginTop: '20px' }}>
              <button
                onClick={() => handleDownloadDocument(companyDetails.document)}
                style={{
                  padding: '10px 20px',
                  backgroundColor: '#007bff',
                  color: 'white',
                  textDecoration: 'none',
                  borderRadius: '5px',
                  cursor: 'pointer',
                }}
              >
                Download Legal Document
              </button>
            </div>
          )}

          <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '20px' }}>
            <button onClick={handleAccept} style={{ padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', flex: 1, marginRight: '5px' }}>Accept</button>
            <button onClick={handleReject} style={{ padding: '10px 20px', backgroundColor: '#f44336', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', flex: 1, marginLeft: '5px' }}>Reject</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
