import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyLogin() {
  const [email, setEmail]           = useState('');
  const [password, setPassword]     = useState('');
  const [errors, setErrors]         = useState({});
  const [showPopup, setShowPopup]   = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('You have no notifications!');
  const [hasNotification, setHasNotification] = useState(false);
  const [canLogin, setCanLogin]     = useState(false);

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  // 1️⃣ On mount, restore the last-logged-in company (if any)
  useEffect(() => {
    const stored = JSON.parse(sessionStorage.getItem('currentCompany'));
    if (stored) {
      setEmail(stored.companyEmail);
      setCanLogin(stored.isAccepted);
      if (stored.hasNotification) {
        setHasNotification(true);
        setNotificationMessage('Your registration has been accepted!');
      }
    }
  }, []);

  // 2️⃣ Any time `email` changes (including the prefill above), refresh accept/notification state
  useEffect(() => {
    const refreshState = () => {
      const companies = JSON.parse(sessionStorage.getItem('companies')) || [];
      const user = companies.find(c => c.companyEmail === email);

      if (user) {
        setCanLogin(user.isAccepted);
        if (user.hasNotification) {
          setHasNotification(true);
          setNotificationMessage('Your registration has been accepted!');
        }
      } else {
        setCanLogin(false);
        setHasNotification(false);
      }
    };

    refreshState();
    window.addEventListener('storage', refreshState);
    return () => window.removeEventListener('storage', refreshState);
  }, [email]);

  const handleLogin = (e) => {
    e.preventDefault();
    const newErrors = {};

    if (!email) newErrors.email = 'Email is required';
    else if (!emailRegex.test(email)) newErrors.email = 'Invalid email format';
    if (!password) newErrors.password = 'Password is required';

    setErrors(newErrors);
    if (Object.keys(newErrors).length) return;

    const companies = JSON.parse(sessionStorage.getItem('companies')) || [];
    const user = companies.find(c => c.companyEmail === email);

    if (!user) {
      setErrors({ form: 'Invalid email' });
    } else if (user.password !== password) {
      setErrors({ form: 'Wrong password' });
    } else if (!user.isAccepted) {
      setErrors({ form: 'You are not accepted yet!' });
    } else {
      // Persist current company context for future tab loads
      sessionStorage.setItem('currentCompany', JSON.stringify({
        ...user,
        hasNotification: user.hasNotification
      }));

      // Clear their notification flag in the master list once seen
      if (user.hasNotification) {
        const updated = companies.map(c =>
          c.companyEmail === email ? { ...c, hasNotification: false } : c
        );
        sessionStorage.setItem('companies', JSON.stringify(updated));
      }

      navigate('/company-dashboard');
    }
  };

  const togglePopup = () => setShowPopup(prev => !prev);

  const inputStyle = (err) => ({
    width: '100%', padding: '8px', marginBottom: '5px',
    border: err ? '1px solid red' : '1px solid #ddd', boxSizing: 'border-box'
  });
  const errorText = (msg) => (
    <div style={{ color:'red', fontSize:'12px', marginTop:'5px' }}>{msg}</div>
  );

  return (
    <div style={{
      display:'flex', justifyContent:'center', alignItems:'center',
      minHeight:'100vh', position:'relative'
    }}>
      {/* Bell icon */}
      <div onClick={togglePopup} style={{
        position:'absolute', top:'20px', right:'20px', cursor:'pointer'
      }}>
        {hasNotification && (
          <div style={{
            position:'absolute', top:0, right:0,
            width:'20px', height:'20px',
            backgroundColor:'red', color:'white',
            borderRadius:'50%',
            display:'flex', alignItems:'center', justifyContent:'center',
            fontSize:'12px'
          }}>!</div>
        )}
        <img src="/bell.jpg" alt="Notifications" style={{ width:'40px', height:'40px' }}/>
      </div>

      {showPopup && (
        <div style={{
          position:'absolute', top:'70px', right:'20px',
          width:'220px', padding:'10px',
          backgroundColor:'white',
          border:'1px solid #ddd', borderRadius:'8px',
          boxShadow:'0 4px 8px rgba(0,0,0,0.1)'
        }}>
          <h3>Notifications</h3>
          <p>{notificationMessage}</p>
          <button onClick={togglePopup} style={{
            padding:'5px', backgroundColor:'#4CAF50',
            color:'white', border:'none', borderRadius:'4px'
          }}>Close</button>
        </div>
      )}

      {/* Login Form */}
      <div style={{
        background:'#fff', borderRadius:'8px', padding:'20px',
        boxShadow:'0 4px 6px rgba(0,0,0,0.1)', width:'300px'
      }}>
        <h2 style={{ textAlign:'center' }}>Company Login</h2>

        <form onSubmit={handleLogin}>
          <div style={{ marginBottom:'15px' }}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              style={inputStyle(errors.email)}
            />
            {errors.email && errorText(errors.email)}
          </div>

          <div style={{ marginBottom:'15px' }}>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              style={inputStyle(errors.password)}
            />
            {errors.password && errorText(errors.password)}
          </div>

          {errors.form && errorText(errors.form)}

          <div style={{ textAlign:'center' }}>
            <button
              type="submit"
              disabled={!canLogin}
              style={{
                padding:'10px 20px', width:'100%',
                backgroundColor: canLogin ? '#007bff' : '#999',
                color:'white', border:'none', borderRadius:'5px',
                cursor: canLogin ? 'pointer' : 'not-allowed'
              }}
            >
              Login
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default CompanyLogin;
