import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleLogin = (e) => {
    e.preventDefault();

    const newErrors = {};

    // Validate email
    if (!email) {
      newErrors.email = 'Email is required';
    } else if (!emailRegex.test(email)) {
      newErrors.email = 'Invalid email format';
    }

    // Validate password
    if (!password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length > 0) return;

    // Retrieve registered companies from localStorage
    const companies = JSON.parse(localStorage.getItem('companies')) || [];
    const companyUser = companies.find(
      (c) => c.companyEmail === email
    );

    // Check if email is valid and password matches
    if (!companyUser) {
      setErrors({ form: 'Invalid email' });
    } else if (companyUser.password !== password) {
      setErrors({ form: 'Wrong password' });
    } else {
      navigate('/company-dashboard'); // Navigate to the company dashboard after successful login
    }
  };

  const inputStyle = (error) => ({
    width: '100%',
    padding: '8px',
    marginBottom: '5px',
    border: error ? '1px solid red' : '1px solid #ddd',
    boxSizing: 'border-box', // Added for consistent sizing
  });

  const errorText = (msg) => (
    <div style={{ color: 'red', fontSize: '12px', marginTop: '5px' }}>{msg}</div>
  );

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh', padding: '20px 0' }}>
      <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px', width: '400px', maxWidth: '100%' }}>
        <h2>Company Login</h2>
        <form onSubmit={handleLogin}>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={inputStyle(errors.email)}
            />
            {errors.email && errorText(errors.email)}
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle(errors.password)}
            />
            {errors.password && errorText(errors.password)}
          </div>
          {errors.form && errorText(errors.form)}
          <button
            type="submit"
            style={{
              padding: '10px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              marginTop: '10px',
              width: '100%',
              cursor: 'pointer',
            }}
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
}

export default CompanyLogin;
