import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyRegister(props) {
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [companyLogo, setCompanyLogo] = useState(null);
  const [companyEmail, setCompanyEmail] = useState('');
  const [document, setDocument] = useState('');
  const [password, setPassword] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  const [errors, setErrors] = useState({
    companyName: false,
    industry: false,
    companySize: false,
    companyLogo: false,
    companyEmail: false,
    document: false,
    password: false,
  });

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const validateFields = () => {
    const validation = {
      companyName: !companyName,
      industry: !industry,
      companySize: !companySize,
      companyLogo: !companyLogo,
      companyEmail: !companyEmail || !emailRegex.test(companyEmail),
      document: !document,
      password: !password,
    };
    setErrors(validation);
    return validation;
  };

  const showAlerts = (validation) => {
    if (validation.companyName) alert('Please enter a company name.');
    if (validation.industry) alert('Please enter the industry.');
    if (validation.companySize) alert('Please select the company size.');
    if (validation.companyLogo) alert('Please upload the company logo.');
    if (validation.companyEmail) {
      if (!companyEmail) {
        alert('Please enter an official company email.');
      } else {
        alert('Please enter a valid email format (e.g., example@domain.com).');
      }
    }
    if (validation.document) alert('Please upload the required documents.');
    if (validation.password) alert('Please enter a password.');
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const validation = validateFields();
    if (Object.values(validation).includes(true)) {
      showAlerts(validation);
      return;
    }

    // Show confirmation modal
    setIsModalOpen(true);

    // Optionally save credentials for login use
    // props.setRegisteredCompany({ companyEmail, password });
  };

  const closeModal = () => {
    setIsModalOpen(false);
    navigate('/');
  };

  return (
    <div style={styles.container}>
      <div style={styles.formWrapper}>
        <h2>Company Register</h2>
        <form onSubmit={handleRegister}>
          <FormInput
            label="Company Name"
            value={companyName}
            onChange={(e) => setCompanyName(e.target.value)}
            placeholder="Enter company name"
            error={errors.companyName}
          />
          <FormInput
            label="Industry"
            value={industry}
            onChange={(e) => setIndustry(e.target.value)}
            placeholder="Enter industry"
            error={errors.industry}
          />
          <FormSelect
            label="Company Size"
            value={companySize}
            onChange={(e) => setCompanySize(e.target.value)}
            options={[
              { value: '', label: 'Select company size' },
              { value: 'small', label: 'Small (≤50 employees)' },
              { value: 'medium', label: 'Medium (51–100 employees)' },
              { value: 'large', label: 'Large (101–500 employees)' },
              { value: 'corporate', label: 'Corporate (500+ employees)' },
            ]}
            error={errors.companySize}
          />
          <FormFile
            label="Company Logo"
            onChange={(e) => setCompanyLogo(e.target.files[0])}
            error={errors.companyLogo}
          />
          <FormInput
            label="Official Company Email"
            type="email"
            value={companyEmail}
            onChange={(e) => setCompanyEmail(e.target.value)}
            placeholder="Enter official company email"
            error={errors.companyEmail}
          />
          <FormInput
            label="Password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter a password"
            error={errors.password}
          />
          <FormFile
            label="Upload Documents (e.g., tax/legal)"
            onChange={(e) => setDocument(e.target.files[0])}
            error={errors.document}
          />
          <button type="submit" style={styles.submitButton}>Register</button>
        </form>
      </div>

      <button onClick={() => navigate('/')} style={styles.backButton}>Back</button>

      {isModalOpen && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h3>Information Submitted</h3>
            <p>Your company information has been submitted. Please wait for SCAD to review and accept your submission.</p>
            <button onClick={closeModal} style={styles.submitButton}>OK</button>
          </div>
        </div>
      )}
    </div>
  );
}

// Reusable Components
const FormInput = ({ label, type = 'text', value, onChange, placeholder, error }) => (
  <div>
    <label>{label}:</label>
    <input
      type={type}
      value={value}
      onChange={onChange}
      placeholder={placeholder}
      style={{ ...styles.input, borderColor: error ? 'red' : '#ddd' }}
    />
  </div>
);

const FormSelect = ({ label, value, onChange, options, error }) => (
  <div>
    <label>{label}:</label>
    <select
      value={value}
      onChange={onChange}
      style={{ ...styles.input, borderColor: error ? 'red' : '#ddd' }}
    >
      {options.map((opt) => (
        <option key={opt.value} value={opt.value}>{opt.label}</option>
      ))}
    </select>
  </div>
);

const FormFile = ({ label, onChange, error }) => (
  <div>
    <label>{label}:</label>
    <input
      type="file"
      onChange={onChange}
      style={{ ...styles.input, borderColor: error ? 'red' : '#ddd' }}
    />
  </div>
);

// Styles
const styles = {
  container: {
    display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh',
  },
  formWrapper: {
    padding: '20px', border: '1px solid #ddd', borderRadius: '8px', width: '400px',
  },
  input: {
    width: '100%', padding: '8px', marginBottom: '10px',
  },
  submitButton: {
    padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px', width: '100%',
  },
  backButton: {
    position: 'fixed', bottom: '20px', left: '20px',
    padding: '10px', backgroundColor: '#f44336', color: 'white', border: 'none', borderRadius: '4px',
  },
  modalOverlay: {
    position: 'fixed', top: '0', left: '0', width: '100%', height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', display: 'flex', justifyContent: 'center', alignItems: 'center',
  },
  modalContent: {
    backgroundColor: 'white', padding: '20px', borderRadius: '8px', textAlign: 'center', width: '400px',
  },
};

export default CompanyRegister;
