// CompanyRegister.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function CompanyRegister() {
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [companyLogo, setCompanyLogo] = useState(null);
  const [companyEmail, setCompanyEmail] = useState('');
  const [document, setDocument] = useState(null);
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleLogoChange = e => {
    const file = e.target.files?.[0];
    if (file) {
      setCompanyLogo(URL.createObjectURL(file));
    } else {
      setCompanyLogo(null);
    }
  };

  const handleDocumentChange = e => {
    const file = e.target.files?.[0];
    if (!file) {
      setDocument(null);
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setDocument({ name: file.name, dataUrl: reader.result });
    };
    reader.readAsDataURL(file);
  };

  const validate = () => {
    const newErrors = {};
    if (!companyName) newErrors.companyName = 'Required';
    if (!industry) newErrors.industry = 'Required';
    if (!companySize) newErrors.companySize = 'Required';
    if (!companyLogo) newErrors.companyLogo = 'Required';
    if (!companyEmail) newErrors.companyEmail = 'Required';
    else if (!emailRegex.test(companyEmail)) newErrors.companyEmail = 'Invalid email';
    if (!password) newErrors.password = 'Required';
    if (!document) newErrors.document = 'Required';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleRegister = e => {
    e.preventDefault();
    if (!validate()) return;

    // include logo & document in the main array
    const newCompany = {
      companyName,
      industry,
      companySize,
      companyEmail,
      password,
      isAccepted: false,
      hasNotification: false,
      companyLogo,
      document,
    };

    const all = JSON.parse(sessionStorage.getItem('companies')) || [];
    all.push(newCompany);
    sessionStorage.setItem('companies', JSON.stringify(all));

    // optional: auto-login
    sessionStorage.setItem('currentCompany', JSON.stringify(newCompany));

    setIsModalOpen(true);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', padding: 20 }}>
      <div style={{ width: 360, border: '1px solid #ddd', borderRadius: 8, padding: 20 }}>
        <h2>Company Register</h2>
        <form onSubmit={handleRegister} noValidate>
          <label>Company Name</label>
          <input
            type="text"
            value={companyName}
            onChange={e => setCompanyName(e.target.value)}
            style={{ width: '100%', marginBottom: 8, borderColor: errors.companyName ? 'red' : '#ddd' }}
          />
          {errors.companyName && <div style={{ color: 'red' }}>{errors.companyName}</div>}

          <label>Industry</label>
          <input
            type="text"
            value={industry}
            onChange={e => setIndustry(e.target.value)}
            style={{ width: '100%', marginBottom: 8, borderColor: errors.industry ? 'red' : '#ddd' }}
          />
          {errors.industry && <div style={{ color: 'red' }}>{errors.industry}</div>}

          <label>Company Size</label>
          <select
            value={companySize}
            onChange={e => setCompanySize(e.target.value)}
            style={{ width: '100%', marginBottom: 8, borderColor: errors.companySize ? 'red' : '#ddd' }}
          >
            <option value="">Select size</option>
            <option value="small">≤50</option>
            <option value="medium">51–100</option>
            <option value="large">101–500</option>
            <option value="corporate">500+</option>
          </select>
          {errors.companySize && <div style={{ color: 'red' }}>{errors.companySize}</div>}

          <label>Logo</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleLogoChange}
            style={{ width: '100%', marginBottom: 8, borderColor: errors.companyLogo ? 'red' : '#ddd' }}
          />
          {errors.companyLogo && <div style={{ color: 'red' }}>{errors.companyLogo}</div>}
          {companyLogo && (
            <img src={companyLogo} alt="" style={{ width: 80, height: 80, borderRadius: '50%' }} />
          )}

          <label>Email</label>
          <input
            type="email"
            value={companyEmail}
            onChange={e => setCompanyEmail(e.target.value)}
            style={{ width: '100%', marginBottom: 8, borderColor: errors.companyEmail ? 'red' : '#ddd' }}
          />
          {errors.companyEmail && <div style={{ color: 'red' }}>{errors.companyEmail}</div>}

          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            style={{ width: '100%', marginBottom: 8, borderColor: errors.password ? 'red' : '#ddd' }}
          />
          {errors.password && <div style={{ color: 'red' }}>{errors.password}</div>}

          <label>Document</label>
          <input
            type="file"
            accept=".pdf,.docx"
            onChange={handleDocumentChange}
            style={{ width: '100%', marginBottom: 8, borderColor: errors.document ? 'red' : '#ddd' }}
          />
          {errors.document && <div style={{ color: 'red' }}>{errors.document}</div>}

          <button
            type="submit"
            style={{
              width: '100%',
              padding: 10,
              backgroundColor: '#28a745',
              color: '#fff',
              border: 'none',
              borderRadius: 4,
              cursor: 'pointer',
            }}
          >
            Register
          </button>
        </form>

        {isModalOpen && (
          <div style={{
            position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.4)',
            display: 'flex', alignItems: 'center', justifyContent: 'center'
          }}>
            <div style={{ background: '#fff', padding: 20, borderRadius: 8, textAlign: 'center' }}>
              <h3>Registration Successful!</h3>
              <p>Awaiting approval.</p>
              <button
                onClick={() => { setIsModalOpen(false); navigate('/company-login'); }}
                style={{
                  marginTop: 10, padding: 8, backgroundColor: '#007bff',
                  color: '#fff', border: 'none', borderRadius: 4, cursor: 'pointer'
                }}
              >
                OK
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
