import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyRegister(props) {
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [companyLogo, setCompanyLogo] = useState(null);
  const [companyEmail, setCompanyEmail] = useState('');
  const [document, setDocument] = useState('');
  const [password, setPassword] = useState('');

  const [errors, setErrors] = useState({
    companyName: false,
    industry: false,
    companySize: false,
    companyLogo: false,
    companyEmail: false,
    document: false,
    password: false,
  });

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleRegister = (e) => {
    e.preventDefault();

    const newErrors = {
      companyName: !companyName,
      industry: !industry,
      companySize: !companySize,
      companyLogo: !companyLogo,
      companyEmail: !companyEmail || !emailRegex.test(companyEmail),
      document: !document,
      password: !password,
    };

    setErrors(newErrors);

    if (Object.values(newErrors).includes(true)) {
      if (!companyName) alert('Please enter a company name.');
      if (!industry) alert('Please enter the industry.');
      if (!companySize) alert('Please select the company size.');
      if (!companyLogo) alert('Please upload the company logo.');
      if (!companyEmail) {
        alert('Please enter an official company email.');
      } else if (!emailRegex.test(companyEmail)) {
        alert('Please enter a valid email format (e.g., example@domain.com).');
      }
      if (!document) alert('Please upload the required documents.');
      if (!password) alert('Please enter a password.');
      return;
    }

    alert('Company Registered Successfully!');

    // Pass login info back to App.js
    props.setRegisteredCompany({
      companyEmail,
      password,
    });

    navigate('/');
  };

  const handleLogoChange = (e) => {
    setCompanyLogo(e.target.files[0]);
  };

  const handleDocumentChange = (e) => {
    setDocument(e.target.files[0]);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
        <h2>Company Register</h2>
        <form onSubmit={handleRegister}>
          <div>
            <label>Company Name:</label>
            <input
              type="text"
              value={companyName}
              onChange={(e) => setCompanyName(e.target.value)}
              placeholder="Enter company name"
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.companyName ? 'red' : '#ddd',
              }}
            />
          </div>
          <div>
            <label>Industry:</label>
            <input
              type="text"
              value={industry}
              onChange={(e) => setIndustry(e.target.value)}
              placeholder="Enter industry"
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.industry ? 'red' : '#ddd',
              }}
            />
          </div>
          <div>
            <label>Company Size:</label>
            <select
              value={companySize}
              onChange={(e) => setCompanySize(e.target.value)}
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.companySize ? 'red' : '#ddd',
              }}
            >
              <option value="">Select company size</option>
              <option value="small">Small (â‰¤50 employees)</option>
              <option value="medium">Medium (51-100 employees)</option>
              <option value="large">Large (101-500 employees)</option>
              <option value="corporate">Corporate (500+ employees)</option>
            </select>
          </div>
          <div>
            <label>Company Logo:</label>
            <input
              type="file"
              onChange={handleLogoChange}
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.companyLogo ? 'red' : '#ddd',
              }}
            />
          </div>

          <div>
            <label>Official Company Email:</label>
            <input
              type="email"
              value={companyEmail}
              onChange={(e) => setCompanyEmail(e.target.value)}
              placeholder="Enter official company email"
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.companyEmail ? 'red' : '#ddd',
              }}
            />
          </div>

          {/* Password field now below the email */}
          <div>
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="set password"
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.password ? 'red' : '#ddd',
              }}
            />
          </div>

          <div>
            <label>Upload Documents (e.g., tax/legal):</label>
            <input
              type="file"
              onChange={handleDocumentChange}
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                borderColor: errors.document ? 'red' : '#ddd',
              }}
            />
          </div>

          <button
            type="submit"
            style={{
              padding: '10px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
            }}
          >
            Register
          </button>
        </form>
      </div>

      <button
        onClick={() => navigate('/')}
        style={{
          position: 'fixed',
          bottom: '20px',
          left: '20px',
          padding: '10px',
          backgroundColor: '#f44336',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
        }}
      >
        Back
      </button>
    </div>
  );
}

export default CompanyRegister;
