import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Helper function to convert file to base64 string
const fileToBase64 = (file) => {
  return new Promise((resolve, reject) => {
    if (!file) resolve(null);
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = error => reject(error);
  });
};

function CompanyRegister() {
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [companyLogoFile, setCompanyLogoFile] = useState(null);
  const [companyEmail, setCompanyEmail] = useState('');
  const [documentFile, setDocumentFile] = useState(null);
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const maxCompanies = 50;

  const handleDocumentChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) return setDocumentFile(null);
    setDocumentFile(file);
  };

  const handleLogoChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) return setCompanyLogoFile(null);
    setCompanyLogoFile(file);
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const newErrors = {};
    if (!companyName) newErrors.companyName = 'Company name is required';
    if (!industry) newErrors.industry = 'Industry is required';
    if (!companySize) newErrors.companySize = 'Company size is required';
    if (!companyLogoFile) newErrors.companyLogo = 'Company logo is required';
    if (!companyEmail) newErrors.companyEmail = 'Email is required';
    else if (!emailRegex.test(companyEmail)) newErrors.companyEmail = 'Invalid email format';
    if (!documentFile) newErrors.document = 'Document is required';
    if (!password) newErrors.password = 'Password is required';
    setErrors(newErrors);
    if (Object.keys(newErrors).length) return;

    try {
      const companyLogoBase64 = await fileToBase64(companyLogoFile);
      const documentBase64 = await fileToBase64(documentFile);

      const fullCompany = {
        companyName,
        industry,
        companySize,
        companyLogo: companyLogoBase64,
        companyEmail,
        document: {
          name: documentFile.name,
          dataUrl: documentBase64,
        },
        password,
        isAccepted: false,
        hasNotification: false,
      };

      const existing = JSON.parse(localStorage.getItem('companies')) || [];
      if (existing.length >= maxCompanies) {
        existing.shift(); // Remove the first item if maxCompanies is reached
      }

      existing.push(fullCompany);
      localStorage.setItem('companies', JSON.stringify(existing));
      localStorage.setItem('currentCompany', JSON.stringify(fullCompany));

      // Notify other tabs
      localStorage.setItem('companiesUpdated', Date.now());

      setIsModalOpen(true);
    } catch (error) {
      console.error('Error processing files:', error);
      setErrors({ general: 'Failed to process files. Please try again.' });
    }
  };

  const handleModalOkClick = () => {
    setIsModalOpen(false);
    navigate('/company-login');
  };

  const inputStyle = (err) => ({
    width: '100%',
    padding: '8px',
    marginBottom: '5px',
    border: err ? '1px solid red' : '1px solid #ddd',
  });

  const errorText = (msg) => (
    <div style={{ color: 'red', fontSize: '12px', marginBottom: '10px' }}>{msg}</div>
  );

  return (
    <div style={{ display: 'flex', justifyContent: 'center', minHeight: '100vh' }}>
      <div style={{ width: '400px', padding: '20px' }}>
        <h2>Company Register</h2>
        <form onSubmit={handleRegister}>
          <div>
            <label>Company Name:</label>
            <input
              type="text"
              value={companyName}
              onChange={(e) => setCompanyName(e.target.value)}
              style={inputStyle(errors.companyName)}
            />
            {errors.companyName && errorText(errors.companyName)}
          </div>
          <div>
            <label>Industry:</label>
            <input
              type="text"
              value={industry}
              onChange={(e) => setIndustry(e.target.value)}
              style={inputStyle(errors.industry)}
            />
            {errors.industry && errorText(errors.industry)}
          </div>
          <div>
            <label>Company Size:</label>
            <select
              value={companySize}
              onChange={(e) => setCompanySize(e.target.value)}
              style={inputStyle(errors.companySize)}
            >
              <option value="">Select company size</option>
              <option value="small">Small (≤50)</option>
              <option value="medium">Medium (51–100)</option>
              <option value="large">Large (101–500)</option>
              <option value="corporate">Corporate (500+)</option>
            </select>
            {errors.companySize && errorText(errors.companySize)}
          </div>
          <div>
            <label>Company Logo:</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleLogoChange}
              style={inputStyle(errors.companyLogo)}
            />
            {errors.companyLogo && errorText(errors.companyLogo)}
            {companyLogoFile && (
              <img
                src={URL.createObjectURL(companyLogoFile)}
                alt="Logo Preview"
                style={{
                  width: 80,
                  height: 80,
                  borderRadius: '50%',
                  objectFit: 'cover',
                }}
              />
            )}
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              value={companyEmail}
              onChange={(e) => setCompanyEmail(e.target.value)}
              style={inputStyle(errors.companyEmail)}
            />
            {errors.companyEmail && errorText(errors.companyEmail)}
          </div>
          <div>
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle(errors.password)}
            />
            {errors.password && errorText(errors.password)}
          </div>
          <div>
            <label>Upload Document:</label>
            <input
              type="file"
              accept=".pdf,.docx,.txt"
              onChange={handleDocumentChange}
              style={inputStyle(errors.document)}
            />
            {errors.document && errorText(errors.document)}
            {documentFile && <p style={{ fontSize: '12px' }}>Selected: {documentFile.name}</p>}
          </div>
          <button
            type="submit"
            style={{
              marginTop: 10,
              width: '100%',
              padding: '10px',
              backgroundColor: '#4CAF50',
              color: '#fff',
              border: 'none',
            }}
          >
            Register
          </button>
          {errors.general && errorText(errors.general)}
        </form>

        {isModalOpen && (
          <div
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              backgroundColor: 'rgba(0,0,0,0.5)',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <div
              style={{
                backgroundColor: '#fff',
                padding: 20,
                borderRadius: 8,
              }}
            >
              <h3>Registration Successful!</h3>
              <p>Await SCAD approval.</p>
              <button
                onClick={handleModalOkClick}
                style={{
                  marginTop: 10,
                  backgroundColor: '#007bff',
                  color: '#fff',
                  border: 'none',
                  padding: '10px',
                }}
              >
                OK
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CompanyRegister;
