import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('You have no notifications!');
  const [hasNotification, setHasNotification] = useState(false);
  const [canLogin, setCanLogin] = useState(false);

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  // Load last-logged-in company from localStorage
  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('currentCompany'));
    if (stored) {
      setEmail(stored.companyEmail);
      setCanLogin(stored.isAccepted);
      if (stored.hasNotification) {
        setHasNotification(true);
        setNotificationMessage('Your registration has been accepted!');
      }
    }
  }, []);

  // Refresh company state when email changes
  useEffect(() => {
    const refreshState = () => {
      const companies = JSON.parse(localStorage.getItem('companies')) || [];
      const user = companies.find(c => c.companyEmail === email);

      if (user) {
        setCanLogin(user.isAccepted);
        if (user.hasNotification) {
          setHasNotification(true);
          setNotificationMessage('Your registration has been accepted!');
        }
      } else {
        setCanLogin(false);
        setHasNotification(false);
      }
    };

    refreshState();
    window.addEventListener('storage', refreshState);
    return () => window.removeEventListener('storage', refreshState);
  }, [email]);

  const handleLogin = (e) => {
    e.preventDefault();
    const newErrors = {};

    if (!email) newErrors.email = 'Email is required';
    else if (!emailRegex.test(email)) newErrors.email = 'Invalid email format';
    if (!password) newErrors.password = 'Password is required';

    setErrors(newErrors);
    if (Object.keys(newErrors).length) return;

    const companies = JSON.parse(localStorage.getItem('companies')) || [];
    const user = companies.find(c => c.companyEmail === email);

    if (!user) {
      setErrors({ form: 'Invalid email' });
    } else if (user.password !== password) {
      setErrors({ form: 'Wrong password' });
    } else if (!user.isAccepted) {
      setErrors({ form: 'You are not accepted yet!' });
    } else {
      localStorage.setItem('currentCompany', JSON.stringify({
        ...user,
        hasNotification: user.hasNotification
      }));

      if (user.hasNotification) {
        const updated = companies.map(c =>
          c.companyEmail === email ? { ...c, hasNotification: false } : c
        );
        localStorage.setItem('companies', JSON.stringify(updated));
      }

      navigate('/company-dashboard');
    }
  };

  const togglePopup = () => setShowPopup(prev => !prev);

  const inputStyle = (err) => ({
    width: '100%', padding: '10px', marginBottom: '15px',
    border: err ? '1px solid red' : '1px solid #ddd', borderRadius: '4px',
    boxSizing: 'border-box'
  });

  const errorText = (msg) => (
    <div style={{ color:'red', fontSize:'12px', marginTop:'5px' }}>{msg}</div>
  );

  return (
    <div style={{ backgroundColor: '#d9e4ec', minHeight: '100vh', padding: '20px' }}>
      <h1 style={{ textAlign: 'center', color: '#385e72' }}>Company Login</h1>

      <div style={{ textAlign: 'center' }}>
        <input
          type="text"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          style={inputStyle(errors.email)}
        />
        {errors.email && errorText(errors.email)}

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          style={inputStyle(errors.password)}
        />
        {errors.password && errorText(errors.password)}

        {errors.form && errorText(errors.form)}

        <div style={{ textAlign: 'center', margin: '20px 0' }}>
          <button
            type="submit"
            onClick={handleLogin}
            disabled={!canLogin}
            style={{
              padding: '10px 20px', width: '100%',
              backgroundColor: canLogin ? '#4CAF50' : '#999',
              color: 'white', border: 'none', borderRadius: '5px',
              cursor: canLogin ? 'pointer' : 'not-allowed'
            }}
          >
            Login
          </button>
        </div>
      </div>

      <div style={{
        position: 'absolute', top: '20px', right: '20px', cursor: 'pointer',
        display: 'flex', alignItems: 'center'
      }}>
        {hasNotification && (
          <div style={{
            position: 'absolute', top: '-5px', right: '-5px',
            width: '25px', height: '25px',
            backgroundColor: 'red', color: 'white',
            borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center',
            fontSize: '14px', fontWeight: 'bold'
          }}>!</div>
        )}
        <img
          src="/bell.jpg"
          alt="Notifications"
          style={{
            width: '50px', height: '50px', objectFit: 'cover',
            cursor: 'pointer'
          }}
          onClick={togglePopup}
        />
      </div>

      {showPopup && (
        <div style={{
          position: 'absolute', top: '70px', right: '20px',
          width: '220px', padding: '15px',
          backgroundColor: 'white', border: '1px solid #ddd',
          borderRadius: '8px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
        }}>
          <h3 style={{ margin: 0 }}>Notifications</h3>
          <p>{notificationMessage}</p>
          <button
            onClick={() => {
              setShowPopup(false);
              setHasNotification(false);
              const companies = JSON.parse(localStorage.getItem('companies')) || [];
              const updated = companies.map(c =>
                c.companyEmail === email ? { ...c, hasNotification: false } : c
              );
              localStorage.setItem('companies', JSON.stringify(updated));
            }}
            style={{
              padding: '5px 10px', backgroundColor: '#4CAF50',
              color: 'white', border: 'none', borderRadius: '4px',
            }}
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
}

export default CompanyLogin;
