import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function SCADPage() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div style={{ position: 'relative', padding: '20px' }}>
      {/* Hamburger icon */}
      <div
        onClick={toggleMenu}
        style={{
          fontSize: '24px',
          cursor: 'pointer',
          position: 'absolute',
          top: '20px',
          left: '20px',
          zIndex: 10,
        }}
      >
        â˜°
      </div>

      {/* Slide-out menu */}
      {menuOpen && (
        <div
          style={{
            position: 'absolute',
            top: '60px',
            left: '20px',
            backgroundColor: 'white',
            border: '1px solid #ddd',
            borderRadius: '8px',
            boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
            padding: '10px',
            zIndex: 9,
          }}
        >
          <ul style={{ listStyleType: 'none', padding: 0, margin: 0 }}>
            <li><Link to="/scad-dashboard">Dashboard</Link></li>
            <li><Link to="/view-students">View Students</Link></li>
            <li><Link to="/approve-internships">Approve Internships</Link></li>
            <li><Link to="/messages">Messages</Link></li>
            <li><Link to="/reports">Reports</Link></li>
            <li><Link to="/settings">Settings</Link></li>
            <li><Link to="/">Logout</Link></li>
          </ul>
        </div>
      )}

      <h1 style={{ marginTop: '80px' }}>Welcome to the SCAD Page</h1>
      <p>This is your dashboard.</p>
    </div>
  );
}

export default SCADPage;
