import React, { useState, useEffect, useMemo } from 'react';

function ViewRegistration({ setNotificationMessage }) {
  const [companyDetails, setCompanyDetails] = useState(null); // Stores the company selected for the sidebar
  const [companies, setCompanies] = useState([]); // Stores pending companies with full details
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [industryOptions, setIndustryOptions] = useState([]);

  // MODIFIED: Load pending companies and enrich them with full details from sessionStorage
  const loadPendingCompanies = () => {
    try {
      const allLightweightCompanies = JSON.parse(sessionStorage.getItem('companies')) || [];
      // console.log("Loaded lightweight companies from sessionStorage:", allLightweightCompanies);

      const pendingCompaniesWithDetails = [];
      for (const lwCompany of allLightweightCompanies) {
        if (!lwCompany.isAccepted) { // Filter for pending registrations
          const fullDataString = sessionStorage.getItem(`companyData:${lwCompany.companyEmail}`);
          if (fullDataString) {
            const fullData = JSON.parse(fullDataString);
            pendingCompaniesWithDetails.push({
              ...lwCompany, // Base info from the 'companies' list
              companyLogo: fullData.companyLogo, // Add/overwrite with logo from 'companyData'
              document: fullData.document,       // Add/overwrite with document from 'companyData'
              // Ensure all other relevant fields from fullData are considered if necessary
              // companyName: fullData.companyName || lwCompany.companyName, // Example
            });
          } else {
            // Fallback if full data is missing (should ideally not happen with robust registration)
            console.warn(`Full data not found for ${lwCompany.companyEmail}. Displaying with limited info.`);
            pendingCompaniesWithDetails.push(lwCompany); // Add as is, might lack logo/doc
          }
        }
      }
      // console.log("Pending companies with full details:", pendingCompaniesWithDetails);
      setCompanies(pendingCompaniesWithDetails);

      // Set industry options from these fully detailed pending companies
      setIndustryOptions([...new Set(pendingCompaniesWithDetails.map(c => c.industry).filter(Boolean))]);
    } catch (e) {
      console.error("Error loading companies:", e);
      setCompanies([]); // On error, clear companies
    }
  };

  useEffect(() => {
    loadPendingCompanies(); // Load companies initially
    const onStorageChange = (e) => {
      // Listen for changes to 'companies' list or specific 'companyData' items
      // e.key is null if sessionStorage.clear() is called
      if (e.key === 'companies' || (e.key && e.key.startsWith('companyData:')) || e.key === null) {
        loadPendingCompanies(); // Refresh companies on relevant sessionStorage change
      }
    };
    window.addEventListener('storage', onStorageChange); // Listen for changes to sessionStorage
    return () => window.removeEventListener('storage', onStorageChange); // Clean up listener
  }, []); // Empty dependency array ensures this runs once on mount and cleans up on unmount

  // MODIFIED: handleAccept to also update the full companyData entry
  const handleAccept = () => {
    if (!companyDetails) return;

    // Update the main 'companies' list (marks as accepted)
    const allCompaniesList = JSON.parse(sessionStorage.getItem('companies')) || [];
    const updatedCompaniesList = allCompaniesList.map(c =>
      c.companyEmail === companyDetails.companyEmail
        ? { ...c, isAccepted: true, hasNotification: true }
        : c
    );
    sessionStorage.setItem('companies', JSON.stringify(updatedCompaniesList));

    // Update the corresponding 'companyData:<email>' item for consistency
    const companyDataKey = `companyData:${companyDetails.companyEmail}`;
    const companyDataString = sessionStorage.getItem(companyDataKey);
    if (companyDataString) {
      const companyData = JSON.parse(companyDataString);
      companyData.isAccepted = true;
      companyData.hasNotification = true;
      // You could add other admin-specific fields here if needed
      sessionStorage.setItem(companyDataKey, JSON.stringify(companyData));
    }

    setNotificationMessage?.(`Registration for ${companyDetails.companyName} accepted.`);
    setCompanyDetails(null); // Close sidebar
    loadPendingCompanies(); // Refresh the list of pending companies
  };

  // MODIFIED: handleReject to also remove the full companyData entry
  const handleReject = () => {
    if (!companyDetails) return;

    // Remove from the main 'companies' list
    const allCompaniesList = JSON.parse(sessionStorage.getItem('companies')) || [];
    const updatedCompaniesList = allCompaniesList.filter(c => c.companyEmail !== companyDetails.companyEmail);
    sessionStorage.setItem('companies', JSON.stringify(updatedCompaniesList));

    // Also remove the detailed data for the rejected company from sessionStorage
    sessionStorage.removeItem(`companyData:${companyDetails.companyEmail}`);

    setNotificationMessage?.(`Registration for ${companyDetails.companyName} rejected.`);
    setCompanyDetails(null); // Close sidebar
    loadPendingCompanies(); // Refresh the list of pending companies
  };

  const handleDownloadDocument = (documentToDownload) => {
    if (!documentToDownload || !documentToDownload.dataUrl || !documentToDownload.name) {
        console.error("Document data is invalid for download.", documentToDownload);
        alert("Could not download document: data is missing.");
        return;
    }
    const link = document.createElement('a');
    link.href = documentToDownload.dataUrl;
    link.download = documentToDownload.name;
    document.body.appendChild(link); // Required for Firefox
    link.click();
    document.body.removeChild(link);
  };

  const filteredCompanies = useMemo(() =>
    companies.filter(c =>
      c.companyName.toLowerCase().includes(searchQuery.toLowerCase()) &&
      (selectedIndustry ? c.industry === selectedIndustry : true)
    ),
    [companies, searchQuery, selectedIndustry]
  );

  return (
    <div style={{ padding: '20px' }}>
      <h1>Pending Company Registrations</h1>

      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by company name..."
          value={searchQuery}
          onChange={e => setSearchQuery(e.target.value)}
          style={{ padding: '8px', width: '300px' }}
        />
        <div>
          <button
            onClick={() => setSelectedIndustry('')}
            style={{
              padding: '8px 12px',
              marginRight: '10px',
              cursor: 'pointer',
              backgroundColor: selectedIndustry === '' ? '#007bff' : '#f0f0f0',
              color: selectedIndustry === '' ? 'white' : 'black',
              border: '1px solid #ccc',
              borderRadius: '4px'
            }}
          >
            All Industries
          </button>
          <select
            value={selectedIndustry}
            onChange={e => setSelectedIndustry(e.target.value)}
            style={{ padding: '8px' }}
          >
            <option value="">Filter by Industry</option>
            {industryOptions.map((industry, idx) => (
              <option key={idx} value={industry}>{industry}</option>
            ))}
          </select>
        </div>
      </div>

      <div style={{ marginTop: '20px' }}>
        {filteredCompanies.length > 0 ? filteredCompanies.map(company => (
          <div key={company.companyEmail} style={{
            padding: '10px', border: '1px solid #eee', borderRadius: '4px',
            marginBottom: '10px', display: 'flex', alignItems: 'center'
          }}>
            {/* Company logo and name as clickable item */}
            <button
              onClick={() => setCompanyDetails(company)} // 'company' now has logo and document
              style={{
                display: 'inline-flex',
                alignItems: 'center',
                background: 'none',
                border: 'none',
                padding: 0,
                cursor: 'pointer',
                textAlign: 'left'
              }}
            >
              {company.companyLogo && ( // This will display if companyLogo (dataURL) exists
                <img
                  src={company.companyLogo}
                  alt={`${company.companyName} logo`}
                  style={{
                    width: '40px',
                    height: '40px',
                    marginRight: '15px',
                    borderRadius: '50%',
                    objectFit: 'cover',
                    border: '1px solid #ddd'
                  }}
                />
              )}
              <span style={{ fontSize: '18px', color: '#007bff', textDecoration: 'underline' }}>
                {company.companyName}
              </span>
            </button>
          </div>
        )) : (
          <p style={{ textAlign: 'center', color: '#777' }}>No pending registrations match your criteria.</p>
        )}
      </div>

      {/* Company Details Sidebar */}
      {companyDetails && (
        <div style={{
          position: 'fixed', top: 0, right: 0, width: '350px', height: '100vh',
          backgroundColor: '#f8f9fa', boxShadow: '-2px 0 5px rgba(0,0,0,0.1)',
          padding: '20px', overflowY: 'auto', zIndex: 1000, boxSizing: 'border-box'
        }}>
          <button
            onClick={() => setCompanyDetails(null)}
            style={{
              position: 'absolute', top: '15px', right: '15px',
              background: '#6c757d', color: 'white', border: 'none',
              borderRadius: '50%', width: '30px', height: '30px',
              fontSize: '16px', cursor: 'pointer', lineHeight: '30px', textAlign: 'center'
            }}
          >
            ×
          </button>
          <h2 style={{ textAlign: 'center', marginTop: '30px', marginBottom: '20px' }}>Company Details</h2>

          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            {companyDetails.companyLogo ? (
              <img
                src={companyDetails.companyLogo}
                alt={`${companyDetails.companyName} logo`}
                style={{ width: '120px', height: '120px', borderRadius: '50%', objectFit: 'cover', border: '2px solid #ddd' }}
              />
            ) : <div style={{width: '120px', height: '120px', borderRadius: '50%', backgroundColor: '#e9ecef', display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '0 auto', color: '#6c757d' }}>No Logo</div>}
            <h3 style={{marginTop: '10px'}}>{companyDetails.companyName}</h3>
          </div>

          <p><strong>Industry:</strong> {companyDetails.industry || 'N/A'}</p>
          <p><strong>Size:</strong> {companyDetails.companySize || 'N/A'}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail || 'N/A'}</p>

          {companyDetails.document && companyDetails.document.dataUrl && (
            <div style={{ textAlign: 'center', margin: '25px 0' }}>
              <button
                onClick={() => handleDownloadDocument(companyDetails.document)}
                style={{
                  padding: '10px 20px', backgroundColor: '#007bff',
                  color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer',
                  fontSize: '16px'
                }}
              >
                Download Document ({companyDetails.document.name})
              </button>
            </div>
          )}

          <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '30px' }}>
            <button
              onClick={handleAccept}
              style={{
                padding: '12px 25px', backgroundColor: '#28a745', // Green
                color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '16px'
              }}
            >
              Accept
            </button>
            <button
              onClick={handleReject}
              style={{
                padding: '12px 25px', backgroundColor: '#dc3545', // Red
                color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '16px'
              }}
            >
              Reject
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
