import React, { useState } from 'react';

function App() {
  // States to manage input values
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  // Handle form submission for login
  const handleLogin = (e) => {
    e.preventDefault();
    alert(`Logging in with ${email} and ${password}`);
  };

  // Handle form submission for register
  const handleRegister = (e) => {
    e.preventDefault();
    alert(`Registering with ${email} and ${password}`);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
        <h2>Login</h2>
        <form>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
              style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
            />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
              style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
            />
          </div>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
            {/* Login button above Register button */}
            <button type="submit" onClick={handleLogin} style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px' }}>
              Login
            </button>
            <button type="button" onClick={handleRegister} style={{ padding: '10px', backgroundColor: '#008CBA', color: 'white', border: 'none', borderRadius: '4px' }}>
              Register as a company
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
