import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('You have no notifications!');
  const [hasNotification, setHasNotification] = useState(false);

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleLogin = (e) => {
    e.preventDefault();

    const newErrors = {};

    // Form validation
    if (!email) {
      newErrors.email = 'Email is required';
    } else if (!emailRegex.test(email)) {
      newErrors.email = 'Invalid email format';
    }

    if (!password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    if (Object.keys(newErrors).length > 0) return;

    // Check company credentials in localStorage
    const companies = JSON.parse(localStorage.getItem('companies')) || [];
    const companyUser = companies.find((c) => c.companyEmail === email);

    if (!companyUser) {
      setErrors({ form: 'Invalid email' });
    } else if (companyUser.password !== password) {
      setErrors({ form: 'Wrong password' });
    } else if (!companyUser.isAccepted) {
      setErrors({ form: 'You are not accepted yet!' });
    } else {
      if (companyUser.hasNotification) {
        setNotificationMessage('Your registration has been accepted!');
        setHasNotification(true);

        // Reset notification state in localStorage
        const updatedCompanies = companies.map(c =>
          c.companyEmail === email ? { ...c, hasNotification: false } : c
        );
        localStorage.setItem('companies', JSON.stringify(updatedCompanies));
      }

      navigate('/company-dashboard');
    }
  };

  const inputStyle = (error) => ({
    width: '100%',
    padding: '8px',
    marginBottom: '5px',
    border: error ? '1px solid red' : '1px solid #ddd',
    boxSizing: 'border-box',
  });

  const errorText = (msg) => (
    <div style={{ color: 'red', fontSize: '12px', marginTop: '5px' }}>{msg}</div>
  );

  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  // Listen for localStorage changes across tabs
  useEffect(() => {
    const handleStorageChange = () => {
      const companies = JSON.parse(localStorage.getItem('companies')) || [];
      const companyUser = companies.find((c) => c.companyEmail === email);

      if (companyUser?.hasNotification) {
        setNotificationMessage('Your registration has been accepted!');
        setHasNotification(true);
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, [email]);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh', padding: '20px 0', position: 'relative' }}>
      {/* Bell icon */}
      <div onClick={togglePopup} style={{ position: 'absolute', top: '20px', right: '20px', cursor: 'pointer', fontSize: '24px' }}>
        {hasNotification && (
          <div style={{
            position: 'absolute', top: '0', right: '0', width: '20px', height: '20px',
            backgroundColor: 'red', color: 'white', borderRadius: '50%', display: 'flex',
            justifyContent: 'center', alignItems: 'center', fontSize: '12px', fontWeight: 'bold'
          }}>
            1
          </div>
        )}
        <img src="/bell.jpg" alt="Bell Icon" style={{ width: '100px', height: '100px' }} />
      </div>

      {/* Notification Pop-up */}
      {showPopup && (
        <div style={{ position: 'absolute', top: '50px', right: '20px', width: '200px', padding: '10px', backgroundColor: 'white', border: '1px solid #ddd', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
          <h3>Notifications</h3>
          <p>{notificationMessage}</p>
          <button onClick={togglePopup} style={{ padding: '5px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px' }}>
            Close
          </button>
        </div>
      )}

      <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px', width: '400px', maxWidth: '100%' }}>
        <h2>Company Login</h2>
        <form onSubmit={handleLogin}>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={inputStyle(errors.email || errors.form === 'Invalid email')}
            />
            {errors.email && errorText(errors.email)}
            {errors.form === 'Invalid email' && errorText(errors.form)}
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle(errors.password || errors.form === 'Wrong password')}
            />
            {errors.password && errorText(errors.password)}
            {errors.form === 'Wrong password' && errorText(errors.form)}
          </div>
          {errors.form === 'You are not accepted yet!' && errorText(errors.form)}
          <button type="submit" style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px', marginTop: '10px', width: '100%' }}>
            Login
          </button>
        </form>
      </div>
    </div>
  );
}

export default CompanyLogin;
