import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function CompanyLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('You have no notifications!');
  // eslint-disable-next-line no-unused-vars
  const [hasNotification, setHasNotification] = useState(false);

  const [canLogin, setCanLogin] = useState(false);

  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  // Load last-logged-in company from localStorage
  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('currentCompany'));
    if (stored) {
      setEmail(stored.companyEmail);
      setCanLogin(stored.isAccepted);
      if (stored.hasNotification) {
        setHasNotification(true);
        setNotificationMessage('Your registration has been accepted!');
      }
    }
  }, []);

  // Refresh company state when email changes
  useEffect(() => {
    const refreshState = () => {
      const companies = JSON.parse(localStorage.getItem('companies')) || [];
      const user = companies.find(c => c.companyEmail === email);

      if (user) {
        setCanLogin(user.isAccepted);
        if (user.hasNotification) {
          setHasNotification(true);
          setNotificationMessage('Your registration has been accepted!');
        }
      } else {
        setCanLogin(false);
        setHasNotification(false);
      }
    };

    refreshState();
    window.addEventListener('storage', refreshState);
    return () => window.removeEventListener('storage', refreshState);
  }, [email]);

  const handleLogin = (e) => {
    e.preventDefault();
    const newErrors = {};

    if (!email) newErrors.email = 'Email is required';
    else if (!emailRegex.test(email)) newErrors.email = 'Invalid email format';
    if (!password) newErrors.password = 'Password is required';

    setErrors(newErrors);
    if (Object.keys(newErrors).length) return;

    const companies = JSON.parse(localStorage.getItem('companies')) || [];
    const user = companies.find(c => c.companyEmail === email);

    if (!user) {
      setErrors({ form: 'Invalid email' });
    } else if (user.password !== password) {
      setErrors({ form: 'Wrong password' });
    } else if (!user.isAccepted) {
      setErrors({ form: 'You are not accepted yet!' });
    } else {
      localStorage.setItem('currentCompany', JSON.stringify({
        ...user,
        hasNotification: user.hasNotification
      }));

      if (user.hasNotification) {
        const updated = companies.map(c =>
          c.companyEmail === email ? { ...c, hasNotification: false } : c
        );
        localStorage.setItem('companies', JSON.stringify(updated));
      }

      navigate('/company-dashboard');
    }
  };

  const togglePopup = () => setShowPopup(prev => !prev);

  const inputStyle = (err) => ({
    width: '100%', padding: '8px', marginBottom: '5px',
    border: err ? '1px solid red' : '1px solid #ddd', boxSizing: 'border-box'
  });

  const errorText = (msg) => (
    <div style={{ color:'red', fontSize:'12px', marginTop:'5px' }}>{msg}</div>
  );

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundColor: '#e6f0f5', // Light background to match app layout
      }}
    >
      <div
        style={{
          padding: '30px',
          borderRadius: '10px',
          backgroundColor: '#ffffff', // White background for the form
          boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
          width: '400px',
          textAlign: 'center',
        }}
      >
        {/* SCAD Logo (or custom logo) */}
        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '15px' }}>
          <img src="/scad.png" alt="SCAD Logo" style={{ width: '250px', height: 'auto' }} />
        </div>
        <h2 style={{ color: '#385e72' }}>Company Login</h2>

        <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <div style={{ width: '100%', marginBottom: '15px' }}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              style={inputStyle(errors.email)}
            />
            {errors.email && errorText(errors.email)}
          </div>

          <div style={{ width: '100%', marginBottom: '15px' }}>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              style={inputStyle(errors.password)}
            />
            {errors.password && errorText(errors.password)}
          </div>

          {errors.form && errorText(errors.form)}

          <button
            type="submit"
            disabled={!canLogin}
            style={{
              padding: '10px',
              backgroundColor: canLogin ? '#385e72' : '#999',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              width: '100%',
              cursor: canLogin ? 'pointer' : 'not-allowed',
            }}
          >
            Login
          </button>
        </form>

        
      </div>

      {/* Notification Popup */}
      {showPopup && (
        <div
          style={{
            position: 'absolute',
            top: '70px',
            right: '20px',
            width: '220px',
            padding: '10px',
            backgroundColor: 'white',
            border: '1px solid #ddd',
            borderRadius: '8px',
            boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
          }}
        >
          <h3>Notifications</h3>
          <p>{notificationMessage}</p>
          <button
            onClick={togglePopup}
            style={{
              padding: '5px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
            }}
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
}

export default CompanyLogin;
