import React, { useState, useEffect } from 'react';

function ViewRegistration() {
  const [companyDetails, setCompanyDetails] = useState(null);
  const [companies, setCompanies] = useState([]);

  useEffect(() => {
    const storedCompanies = JSON.parse(localStorage.getItem('companies')) || [];
    setCompanies(storedCompanies);
  }, []);

  const handleCompanyClick = (company) => {
    setCompanyDetails(company);
  };

  const handleCloseSidebar = () => {
    setCompanyDetails(null);
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>Company Registration View</h1>
      <div style={{ margin: '20px', textAlign: 'center' }}>
        <h2>Companies</h2>
        {companies.length > 0 ? (
          companies.map((company, index) => (
            <p key={index}>
              <button onClick={() => handleCompanyClick(company)} style={{ fontSize: '18px', color: 'blue', background: 'none', border: 'none', textDecoration: 'underline', cursor: 'pointer' }}>
                {company.companyName}
              </button>
            </p>
          ))
        ) : (
          <p>No companies found</p>
        )}
      </div>

      {companyDetails && (
        <div style={{ position: 'fixed', top: 0, right: 0, width: '300px', height: '100vh', backgroundColor: '#f8f9fa', boxShadow: '-2px 0 5px rgba(0,0,0,0.2)', padding: '20px', zIndex: 1000 }}>
          <button onClick={handleCloseSidebar} style={{ position: 'absolute', top: '10px', right: '10px', padding: '5px 10px', backgroundColor: 'red', color: 'white', border: 'none', cursor: 'pointer' }}>
            Close
          </button>
          <h2 style={{ textAlign: 'center' }}>Company Details</h2>
          <div style={{ textAlign: 'center', marginBottom: '15px' }}>
            <p><strong>Logo:</strong> {companyDetails.companyLogoName}</p>
            <h3>{companyDetails.companyName}</h3>
          </div>
          <p><strong>Industry:</strong> {companyDetails.industry}</p>
          <p><strong>Company Size:</strong> {companyDetails.companySize}</p>
          <p><strong>Email:</strong> {companyDetails.companyEmail}</p>
          <p><strong>Document:</strong> {companyDetails.documentName}</p>
        </div>
      )}
    </div>
  );
}

export default ViewRegistration;
